"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[483322],{483322:e=>{e.exports=JSON.parse('{"slug":"defects-damages","meta":{"title":"डिफेक्ट्स और डैमेज – मार्गदर्शिका","description":"कमरे की खामियों की रिपोर्ट कैसे करें, गंभीर समस्याओं पर हमारा प्रतिक्रिया‑प्रोटोकॉल, डैमेज डिपॉज़िट नीति, अतिथि दायित्व, और बेड‑बग रोकथाम संबंधी आवश्यक जानकारी।"},"headings":{"reportingDefects":"खामियों की रिपोर्ट","assessingDefects":"खामियों का आकलन","responseToSeriousDefects":"गंभीर खामियों पर प्रतिक्रिया","unreportedDefects":"अनरिपोर्टेड खामियाँ","damagesPolicy":"डैमेज नीति","bedBugsPolicy":"बेड‑बग नीति","bedBugsPrevention":"बेड‑बग रोकथाम"},"content":{"reportingDefects":"यदि कमरे में कोई समस्या/खामी दिखे तो तुरंत हमें बताएँ। समय पर सूचना से हम शीघ्र कार्रवाई कर पाते हैं।","assessingDefects":"रिपोर्ट के बाद हम मुद्दे का प्राथमिकता‑आधारित आकलन करेंगे, इस आधार पर कि वह आपकी सुविधा/ठहराव को कितना प्रभावित करता है।","responseToSeriousDefects":"यदि कोई गंभीर खामी तुरंत ठीक नहीं हो सकती तो हम वैकल्पिक ठहराव देंगे। यदि विकल्प आपकी बुक की गई श्रेणी से सस्ता है, तो अंतर राशि लौटाई जाएगी। विकल्प अस्वीकार करने पर शेष रातों का आनुपातिक रिफंड माँगा जा सकता है।","unreportedDefects":"यदि उचित समय में रिपोर्ट नहीं की गई खामी हमें मिले, तो हम संभावित नुकसान का मूल्यांकन करेंगे और डैमेज नीति लागू हो सकती है।","damagesPolicy":"सुरक्षा हेतु हम क्रेडिट‑कार्ड प्री‑ऑथराइज़ कर सकते हैं या चेक‑इन पर नकद जमा माँग सकते हैं। जमा/प्री‑ऑथराइज़ेशन न होने पर बुकिंग रद्द हो सकती है। चेक‑आउट पर जमा से क्षति/हानि/श्रम/प्रशासनिक व्यय काटकर शेष लौटाया जाएगा। यदि लागत जमा से अधिक हुई तो अंतर आपको देना होगा। श्रम दरें (प्रति घंटा): निदेशक (€50), प्रबंधक (€35), स्टाफ (€25)।","bedBugsPolicy":"हम बेड‑बग मुद्दों का सक्रिय प्रबंधन करते हैं। फिर भी, प्रवास के दौरान कमरे में बेड‑बग पाए जाने पर खोई बुकिंग‑आय, सफाई, पेस्ट‑कंट्रोल जैसी लागतों की जिम्मेदारी आप पर आ सकती है। किसी भी संकेत की तुरंत सूचना दें।","bedBugsPrevention":"रोकथाम हेतु:\\n• यदि हाल में ऐसे स्थान पर रहे हैं जहाँ बेड‑बग की समस्या है तो चेक‑इन पर बताएँ।\\n• सामान को भली‑भाँति डिसइंफेक्ट करें।\\n• कमरे में प्रवेश से पहले बैग/कपड़ों की जाँच करें।"}}')}}]);