"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[485959],{485959:e=>{e.exports=JSON.parse('{"meta":{"title":"من رافيلو إلى بوزيتانو بالحافلة | Hostel Brikette","description":"إرشادات خطوة بخطوة من رافيلو إلى بوزيتانو عبر أمالفي باستخدام حافلات SITA، مع نصائح عن الجداول، تفاصيل المواقف، والمشي الأخير إلى Hostel Brikette."},"header":{"eyebrow":"دليل السفر من Hostel Brikette","title":"من رافيلو إلى بوزيتانو – عبر أمالفي بالحافلة","description":"سافر من رافيلو إلى بوزيتانو بتغيير بسيط في أمالفي. يغطي هذا الدليل أماكن شراء وتفعيل تذاكر SITA، كيفية قراءة الجدول الزمني، وما الذي تتوقعه عند تغيير الحافلة في أمالفي — بالإضافة إلى المشي القصير من محطة كيييزا نوفا في بوزيتانو إلى Hostel Brikette."},"tip":{"eyebrow":"نصيحة","body":"تحتاج إلى كل خيارات النقل؟ افتح <GuideLink>{{guideLabel}}</GuideLink> للاطلاع على العبّارات، سيارات الأجرة، وخطط الرحلات المختلطة."},"journeyOverview":{"title":"نظرة عامة على الرحلة","description":"توقّع مدة تقارب 1.5–2.5 ساعة بحسب الاتصالات. ابدأ بحافلة SITA من رافيلو إلى أمالفي، ثم انتقل إلى حافلة SITA المتجهة إلى بوزيتانو/سورينتو. تعمل الحافلات بكثرة في موسم الذروة وقد تكون مزدحمة — احضر مبكرًا قليلًا للوقوف في الصف."},"sitaTickets":{"title":"حافلات وتذاكر SITA","points":["تشغّل شركة SITA (SITA Sud Srl) الحافلات بين رافيلو وأمالفي.","يجب أن تسافر أولًا إلى أمالفي ثم تغيّر إلى حافلة ثانية متجهة إلى بوزيتانو.","هناك خياران من رافيلو → أمالفي: الخط 5110 والخط 5120. يكون الخط 5110 أسرع بنحو 10 دقائق عادةً.","اشترِ التذاكر من الأكشاك الصحفية أو محلات التبغ (تاباتشي) وكن مستعدًا لتفعيلها عند الصعود."]},"timetable":{"title":"الجداول الزمنية","description":"تحقق من أحدث الجداول على <TimetableLink>{{timetableLabel}}</TimetableLink>.","notesTitle":"كيفية قراءة الجدول","points":["تسرد الصفوف في العمود الأول كل محطة على طول المسار.","الأعمدة تقابل الرحلات المجدولة المميّزة بالحروف.","هل تسافر من الاثنين إلى السبت؟ استخدم العمودين \\"F\\" و\\"G\\".","هل تسافر يوم الأحد أو في عطلة رسمية؟ استخدم العمودين \\"G\\" و\\"H\\"."],"linkLabel":"Positano.com (نظرة عامة على جدول SITA)"},"ravelloToAmalfi":{"title":"رافيلو → أمالفي","points":["اركب حافلة SITA 5110 أو 5120 من رافيلو إلى أمالفي (5110 عادةً أسرع).","فعّل تذكرتك عند الصعود واحتفظ بها حتى الوصول.","انزل في محطة حافلات أمالفي للتواصل مع حافلتك المتجهة إلى بوزيتانو."]},"waitingInAmalfi":{"title":"الانتظار في أمالفي","points":["عند الوصول إلى أمالفي، يمكنك زيارة مركز البلدة أثناء الانتظار.","عد إلى منطقة محطة الحافلات نفسها حيث نزلت لركوب الحافلة إلى بوزيتانو.","اركب أي خدمة SITA مخصّصة لبوزيتانو/سورينتو عبر ساحل أمالفي."]},"arrivingPositano":{"title":"الوصول إلى بوزيتانو (كيييزا نوفا)","points":["أقرب موقف لحافلات SITA هو بوزيتانو (كيييزا نوفا) في شارع Via G. Marconi.","يبعد حوالي 140 مترًا (490 قدمًا) عن مدخل النزل.","ستنتظر خارج المقهى/البار المسمّى \\"Bar Internazionale\\"."],"figure":{"alt":"موقف حافلات SITA في كيييزا نوفا ببوزيتانو خارج Bar Internazionale","caption":"احضر قبل دقائق قليلة لتضمن مقعدًا؛ فالحافلات قد تمتلئ سريعًا في الصيف."}},"walking":{"title":"المشي من موقف الحافلة إلى النزل","points":["من موقف كيييزا نوفا، تابع السير لمسافة ~100 متر (330 قدمًا).","انعطف يمينًا واتبع الدرج صعودًا إلى الباب الرئيسي للنزل.","ابحث عن شعارنا بجوار التراس؛ المدخل فوق المنعطف مباشرة."]},"links":{"guideOverview":"نظرة عامة على كيفية الوصول"}}')}}]);