"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[492976],{492976:n=>{n.exports=JSON.parse('{"seo":{"title":"Cẩm nang b\xe3i biển Regina Giovanna","description":"L\xean kế hoạch tắm biển vắng người tại hồ tự nhi\xean của Sorrento với hướng dẫn xe bu\xfdt, gợi \xfd chuẩn bị đồ v\xe0 lưu \xfd an to\xe0n."},"linkLabel":"Cẩm nang b\xe3i biển Regina Giovanna","intro":["Bagni della Regina Giovanna l\xe0 hang biển sập tr\xean b\xe1n đảo Sorrento, nơi nước trong chảy v\xe0o hồ tự nhi\xean k\xedn gi\xf3. Kh\xf4ng c\xf3 beach club hay quầy ăn vặt — chỉ c\xf3 v\xe1ch đ\xe1 v\xf4i, mặt nước xanh ngọc v\xe0 những m\xe1i v\xf2m chụp ảnh đẹp mắt.","H\xe3y d\xf9ng hướng dẫn n\xe0y để tới vịnh bằng xe bu\xfdt c\xf4ng cộng, chuẩn bị đồ như người địa phương v\xe0 tận hưởng c\xe1c mỏm đ\xe1 tắm nắng trước khi v\xf2ng về Sorrento ăn uống."],"toc":[{"href":"#why-go","label":"V\xec sao n\xean đi"},{"href":"#getting-there","label":"Di chuyển"},{"href":"#what-to-pack","label":"N\xean mang g\xec"},{"href":"#swimming","label":"Tắm & an to\xe0n"},{"href":"#pair-it","label":"Kết hợp với Sorrento"}],"sections":[{"id":"why-go","title":"V\xec sao đ\xe1ng đi v\xf2ng tới Regina Giovanna","body":["T\xe0n t\xedch biệt thự La M\xe3 ph\xeda tr\xean \xf4m trọn mặt nước xanh ngọc đang chảy từ biển qua m\xe1i v\xf2m tự nhi\xean. Tới sớm bạn c\xf3 thể nổi lềnh bềnh dưới v\xe1ch đ\xe1 chỉ c\xf9ng v\xe0i người d\xe2n địa phương.","Kh\xf4ng như c\xe1c lido ở Positano, địa điểm n\xe0y miễn ph\xed quanh năm. Mang theo mọi thứ bạn cần v\xe0 sẵn s\xe0ng leo qua đ\xe1 — phần thưởng l\xe0 hồ bơi thi\xean nhi\xean cực ấn tượng v\xe0o giờ ho\xe0ng h\xf4n."]},{"id":"getting-there","title":"C\xe1ch đi xe bu\xfdt tới lối m\xf2n","body":["Từ Hostel Brikette, đi xe bu\xfdt SITA tới Sorrento rồi chuyển sang tuyến địa phương A hoặc C (do EAV vận h\xe0nh). N\xf3i \\"Capo di Sorrento\\" hoặc \\"Regina Giovanna\\" để t\xe0i xế biết bạn muốn xuống gần lối m\xf2n.","Điểm dừng c\xe1ch ga Sorrento khoảng 15 ph\xfat. Đi dọc Via Capo tới khi thấy biển \\"Bagni della Regina Giovanna\\" rồi men theo lối dốc l\xe1t đ\xe1 xuống. T\xednh 10 ph\xfat tới t\xe0n t\xedch v\xe0 th\xeam 5 ph\xfat xuống bậc đ\xe1 v\xe0o vịnh.","Chuyến về chạy khoảng mỗi 30 ph\xfat tới đầu buổi tối; quan s\xe1t lịch d\xe1n tại điểm dừng v\xe0 l\xean lại đường trước khi mặt trời lặn."]},{"id":"what-to-pack","title":"Mang g\xec khi kh\xf4ng c\xf3 dịch vụ","body":["Kh\xf4ng c\xf3 cứu hộ, nh\xe0 vệ sinh hay ki-ốt tại chỗ. Mang nước, đồ ăn nhẹ v\xe0 t\xfai r\xe1c để gom đồ mang ra ngo\xe0i.","Đi gi\xe0y chắc ch\xe2n cho đoạn đường đ\xe1 v\xe0 mang gi\xe0y đi nước cho c\xe1c mỏm trơn. Khăn nhẹ v\xe0 t\xfai chống nước gi\xfap đồ đạc tr\xe1nh bị bắn ướt.","V\xe0o cao điểm h\xe3y th\xeam kem chống nắng, mũ v\xe0 \xedt nhất 1,5 l\xedt nước mỗi người — b\xe3i gần như kh\xf4ng c\xf3 b\xf3ng r\xe2m khi nắng l\xean khỏi v\xe1ch đ\xe1."]},{"id":"swimming","title":"Tắm, nhảy v\xe0 lưu \xfd an to\xe0n","body":["Hồ đủ s\xe2u cho người bơi tự tin nhưng kh\xf4ng c\xf3 thang leo cố định. Xuống nước từ từ qua c\xe1c mỏm đ\xe1 tự nhi\xean v\xe0 tr\xe1nh nhảy nếu bạn chưa tự kiểm tra độ s\xe2u.","Người địa phương đ\xf4i khi nhảy từ m\xe1i v\xf2m khi thủy triều cao; chỉ n\xean l\xe0m theo khi biển \xeam v\xe0 bạn thấy thoải m\xe1i với độ cao. Nếu kh\xf4ng, h\xe3y thả m\xecnh trong hồ rồi leo sang ph\xeda hướng ra biển để c\xf3 tầm nh\xecn rộng hơn.","Sau mưa, bậc đ\xe1 c\xf3 thể trơn r\xeau. Di chuyển cẩn thận v\xe0 c\xe2n nhắc đeo găng nếu bạn cần b\xe1m v\xe0o tay vịn để giữ thăng bằng."]},{"id":"pair-it","title":"Kết hợp chuyến tắm với m\xf3n ngon Sorrento","body":["Chuẩn bị picnic hoặc l\xean kế hoạch ăn trưa muộn tại khu phố cổ Sorrento sau khi tắm. Bar Ruccio ở Marina Piccola v\xe0 Da Emilia dọc bờ biển đều phục vụ hải sản b\xecnh d\xe2n c\xf3 view đẹp.","Nếu ở lại tới ho\xe0ng h\xf4n, d\xe0nh 20 ph\xfat leo ngược l\xean điểm dừng xe bu\xfdt rồi bắt xe về trung t\xe2m. T\xe0u v\xe0 ph\xe0 về hướng Napoli v\xe0 Amalfi Coast thưa dần sau 20:30, n\xean h\xe3y xem trước lịch tiếp nối trước khi n\xe1n lại."]}],"essentialsTitle":"Mong đợi điều g\xec","essentials":["Hồ tự nhi\xean miễn ph\xed khắc v\xe0o v\xe1ch đ\xe1 ph\xeda t\xe2y Sorrento — kh\xf4ng c\xf3 ghế tắm nắng hay nh\xe0 h\xe0ng.","Xe bu\xfdt + đi bộ ngắn: đi tuyến EAV A hoặc C tới Capo di Sorrento rồi men theo lối được gắn biển xuống dốc.","Lối đi đ\xe1 gồ ghề với bậc kh\xf4ng đều; mang gi\xe0y chắc ch\xe2n v\xe0 mang r\xe1c ra ngo\xe0i."],"typicalCostsTitle":"Chi ph\xed tham khảo","typicalCosts":["Xe bu\xfdt SITA Positano ⇄ Sorrento: €2–€3 mỗi chiều t\xf9y loại v\xe9.","Xe bu\xfdt địa phương EAV tới Capo di Sorrento: €1,50 nếu mua trước.","Taxi về lại Sorrento (nếu lỡ xe bu\xfdt): €18–€22 cho tối đa bốn người."],"tipsTitle":"Mẹo nhanh","tips":["Đ\xf3ng dấu mọi v\xe9 xe bu\xfdt — thanh tra hoạt động thường xuy\xean tr\xean tuyến Sorrento, đặc biệt v\xe0o m\xf9a h\xe8.","Tới trước 10:00 để nước \xeam v\xe0 \xedt người hơn tr\xean c\xe1c mỏm đ\xe1 hẹp.","Mang t\xfai chống nước v\xe0 m\xf3c v\xe0o đ\xe1; kh\xf4ng c\xf3 tủ kh\xf3a hay nh\xe2n vi\xean tr\xf4ng coi."],"warningsTitle":"Lưu \xfd","warnings":["Kh\xf4ng c\xf3 cứu hộ hay thiết bị an to\xe0n — bơi trong khả năng của bạn v\xe0 ch\xfa \xfd thủy triều.","\xcdt b\xf3ng r\xe2m sau buổi trưa; đ\xe1 n\xf3ng rất nhanh trong th\xe1ng 7 v\xe0 8.","Đường m\xf2n gồ ghề v\xe0 trơn khi ẩm; tr\xe1nh d\xe9p xỏ ng\xf3n v\xe0 đi cẩn thận sau mưa."],"faqsTitle":"C\xe2u hỏi thường gặp về Regina Giovanna","faqs":[{"q":"T\xf4i c\xf3 thể mang đồ ăn thức uống v\xe0o hồ kh\xf4ng?","a":["Được, nhưng kh\xf4ng c\xf3 th\xf9ng r\xe1c — h\xe3y mang picnic của ri\xeang bạn v\xe0 đem tất cả r\xe1c ra ngo\xe0i. Chai nước đ\xf4ng lạnh vừa l\xe0m đ\xe1 vừa giữ lạnh cho chuyến xe bu\xfdt về."]},{"q":"C\xf3 chỗ thay đồ hoặc nh\xe0 vệ sinh kh\xf4ng?","a":["Cơ sở vật chất chỉ c\xf3 tại t\xe0n t\xedch tr\xean đỉnh lối m\xf2n v\xe0 thường đ\xf3ng cửa. H\xe3y thay đồ tại nơi lưu tr\xfa hoặc d\xf9ng nh\xe0 vệ sinh ở Sorrento trước khi l\xean xe bu\xfdt."]},{"q":"T\xf4i n\xean d\xe0nh bao l\xe2u cho chuyến đi từ Positano?","a":["Dự tr\xf9 khoảng ba giờ cả đi lẫn về: một giờ cho h\xe0nh tr\xecnh xe bu\xfdt mỗi chiều v\xe0 \xedt nhất một giờ tại địa điểm. Nếu muốn dạo Sorrento sau đ\xf3, h\xe3y chừa th\xeam thời gian."]}]}')}}]);