"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[510982],{510982:e=>{e.exports=JSON.parse('{"meta":{"title":"من بوسيتانو إلى مطار نابولي بالحافلة | هوستل بريكيت","description":"خطوات من الباب إلى مطار نابولي عبر حافلة SITA إلى سورينتو ثم حافلة كوريري، مع نصائح لشراء التذاكر وروابط الجداول الزمنية."},"header":{"eyebrow":"دليل السفر من هوستل بريكيت","title":"من بوسيتانو إلى مطار نابولي — بالحافلة عبر سورينتو","description":"استقل حافلة SITA Sud من بوسيتانو إلى سورينتو، ثم حافلة كوريري إلى مطار نابولي كابوديكيينو. هذا هو خيار النقل العام الأكثر مباشرة، مع رحلات متكررة واتصال بسيط في محطة حافلات سورينتو."},"aside":{"eyebrow":"بحاجة إلى طرق أخرى؟","body":"افتح <Link>نظرة عامة على كيفية الوصول</Link> للحصول على مسارات سيارات الأجرة، والعبّارات، وخطط الرحلات البديلة."},"sections":{"overview":{"title":"نظرة عامة على الرحلة","points":["سر إلى موقف بوسيتانو (كييزا نوفا) أمام بار إنترناسيونالي.","استقل حافلة SITA إلى محطة الحافلات/القطارات في سورينتو.","غيّر إلى حافلة كوريري من سورينتو إلى مطار نابولي (كابوديكيينو)."]},"walk":{"title":"المشي إلى موقف بوسيتانو","points":["اخرج من النزل وانزل الدرج إلى الطريق الرئيسي.","انعطف يسارًا وواصل السير لنحو 100 متر (330 قدمًا).","سيكون بار إنترناسيونالي على يسارك؛ موقف حافلات SITA أمامه مباشرة."],"image":{"alt":"موقف حافلات سيتا في بوسيتانو (كييزا نوفا) أمام بار إنترناسيونالي","caption":"حضر قبل بضع دقائق في موسم الذروة؛ تمتلئ الحافلات بسرعة."}},"tickets":{"title":"التذاكر والانتظار","points":["اشترِ تذكرة SITA إلى سورينتو من بار إنترناسيونالي أو كشك الصحف القريب.","احتفظ بتذكرتك بعد الصعود؛ قد يقوم السائق بختمها أو تمزيقها.","بالنسبة لحافلة كوريري، يمكنك الشراء عبر الإنترنت أو في المحطة (إذا توفرت)."]},"sorrento":{"title":"في محطة الحافلات في سورينتو","body":"انزل في محطة الحافلات والقطارات الرئيسية في سورينتو. من الأرصفة، استقل حافلة <Link>كوريري فياجي</Link> المتجهة إلى مطار نابولي (كابوديكيينو). راجع أحدث جدول زمني وفكّر في شراء التذاكر مسبقًا عبر الإنترنت خلال فترات الذروة."},"timetables":{"title":"الجداول الزمنية","items":{"sita":{"href":"https://www.positano.com/en/bus-schedule?route=amalfi_positano_sorrento#section-schedule-result","label":"Positano.com","text":"جدول حافلات SITA بوسيتانو ⇄ سورينتو على <Link>Positano.com</Link>"},"curreri":{"href":"https://www.curreriviaggi.it/","label":"curreriviaggi.it","text":"مواعيد كوريري سورينتو ⇄ مطار نابولي على <Link>curreriviaggi.it</Link>"}}}}}')}}]);