"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[514954],{514954:i=>{i.exports=JSON.parse('{"seo":{"title":"Borghi insoliti della Costiera Amalfitana","description":"Furore, Tramonti, Cetara e altri paesi autentici dove trovare artigianato e cucina locale senza folle."},"linkLabel":"Borghi insoliti della Costiera Amalfitana","intro":["Cinque borghi pi\xf9 tranquilli della Costiera Amalfitana che meritano una deviazione, con indicazioni su come raggiungerli."],"tocTitle":"In questa pagina","sections":[{"id":"cetara","title":"Cetara","body":["Tradizione delle alici e spiaggia tranquilla con barche da pesca in attivit\xe0.","Raggiungibile con bus SITA o traghetti stagionali da Amalfi o Salerno."]},{"id":"minori","title":"Minori/Maiori","body":["Lungomare pianeggianti e pasticcerie famose per un ritmo piu rilassato.","Arriva in bus o traghetto; abbina il Sentiero dei Limoni tra i due borghi."]},{"id":"atrani","title":"Atrani","body":["Piazzetta raccolta a est di Amalfi con vicoli stretti e accesso alla spiaggia.","A piedi da Amalfi in pochi minuti o una fermata di bus locale."]},{"id":"vietri","title":"Vietri sul Mare","body":["Capitale delle ceramiche vicino a Salerno con botteghe lungo le strade.","Treno fino a Salerno, poi breve bus o traghetto."]},{"id":"furore","title":"Furore","body":["Fiordo scenografico con spiaggia ridotta: arriva presto per foto tranquille.","Usa i bus SITA verso Amalfi; i traghetti estivi a volte fermano nei pressi."]}],"faqs":[{"q":"Posso visitare piu borghi in un giorno?","a":["Si: collega Cetara, Vietri e Minori in traghetto o bus. Parti presto e controlla gli orari di ritorno."]},{"q":"I piccoli borghi accettano carte?","a":["Porta contanti per bar e chioschi; molti hanno minimo 10 EUR o sono solo cash."]}]}')}}]);