"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[518336],{518336:t=>{t.exports=JSON.parse('{"meta":{"title":"पोसीटानो से कैप्रि फेरी द्वारा | हॉस्टल ब्रिकेटे","description":"हॉस्टल ब्रिकेटे से पोसीटानो के बंदरगाह तक पैदल मार्ग, टिकट, बोर्डिंग सुझाव और कैप्रि जाने वाली फेरी की चरण-दर-चरण जानकारी।"},"header":{"eyebrow":"हॉस्टल ब्रिकेटे ट्रैवल गाइड","title":"पोसीटानो से कैप्रि – फेरी","description":"इन चरणों का पालन करें ताकि आप हॉस्टल ब्रिकेटे से पोसीटानो के बंदरगाह तक पैदल जा सकें, फेरी टिकट खरीद सकें और कैप्रि के लिए सवार हो सकें। इसमें समय-संबंधी व्यावहारिक सुझाव और ऑपरेटर टिप्स शामिल हैं।"},"tip":{"label":"सुझाव","intro":"समुद्र में लहरें तेज हों तो विकल्प चाहिए? खोलें","linkLabel":"यहाँ कैसे पहुँचे अवलोकन","outro":"और बैकअप मार्ग देखें।"},"journeyOverview":{"title":"यात्रा का अवलोकन","paragraphs":["पोसीटानो और कैप्रि के बीच फेरी सेवाएं अधिकतर वसंत से शुरुआती पतझड़ तक चलती हैं और ऊँची लहरों में रद्द हो सकती हैं। ऑपरेटर और पोत के आधार पर यात्रा समय आमतौर पर 30–60 मिनट होता है।"],"linksIntro":"समय तुलना करें और टिकट बुक करें:","linksSeparator":"\xb7","linksSuffix":".","links":{"capriNet":"Capri.net – Capri ↔ Positano","positanoCom":"Positano.com (समेकित समय-सारणी)"}},"photoGuide":{"title":"फ़ोटो गाइड (संदर्भ)","description":"पैदल मार्ग के प्रत्येक चरण और बंदरगाह पर फेरी टिकट क्षेत्र के दृश्य संदर्भ।","items":{"mapOverview":{"alt":"हॉस्टल ब्रिकेटे से पोसीटानो के बंदरगाह तक पैदल मार्ग का नक्शा","caption":"नक्शा अवलोकन: हॉस्टल से पोसीटानो का बंदरगाह"},"spiaggiaApproach":{"alt":"पोसीटानो के स्पियाज्जा ग्रांदे और पर्यटक बंदरगाह की ओर जाने का दृश्य","caption":"स्पियाज्जा ग्रांदे (पर्यटक बंदरगाह) की ओर मार्ग"},"spiaggiaDetail":{"alt":"स्पियाज्जा ग्रांदे के दाहिने हिस्से में स्थित बंदरगाह को दर्शाता विस्तृत नक्शा","caption":"विस्तृत नक्शा: स्पियाज्जा ग्रांदे पर बंदरगाह का स्थान"},"ticketBooths":{"alt":"पोसीटानो बंदरगाह पर फेरी ऑपरेटरों के टिकट काउंटर और अगली प्रस्थान की सूचना","caption":"फेरी ऑपरेटर टिकट काउंटर"},"point1":{"alt":"बिंदु 1: हॉस्टल ब्रिकेटे के पास वाया जी. मारकोनी पर तबाक्की के पास चौराहा","caption":"बिंदु 1: तबाक्की चौराहा (दाहिने मुड़ें)"},"point2":{"alt":"बिंदु 2: पहला हेयरपिन मोड़ जहाँ से सड़क छोड़कर सीढ़ियाँ ली जाती हैं","caption":"बिंदु 2: सड़क छोड़ें, सीढ़ियाँ लें"},"point3":{"alt":"बिंदु 3: सीढ़ियाँ समाप्त होकर सड़क तक पहुंचने का स्थान","caption":"बिंदु 3: सीढ़ियाँ सड़क पर समाप्त"},"point4":{"alt":"बिंदु 4: सड़क पार करें, थोड़ा बाएँ चलें और फिर सीढ़ियाँ लें","caption":"बिंदु 4: सड़क पार, बाएँ मुड़ें, सीढ़ियाँ"},"point5":{"alt":"बिंदु 5: बीच और बंदरगाह की ओर जाने वाला मेहराबदार मार्ग","caption":"बिंदु 5: बंदरगाह की ओर मेहराब"}}},"toPort":{"title":"पोसीटानो के बंदरगाह तक पैदल जाएँ","steps":["हॉस्टल के मुख्य द्वार से बाहर निकलें।","सीढ़ियाँ उतरकर सड़क पर आएँ, बाएँ मुड़ें और लगभग 100 मीटर (≈330 फ़ुट) चलें।","पहले चौराहे (बिंदु 1) पर दाहिने मुड़ें; तबाक्की (दाहिनी ओर की पहली इमारत) के पास से जाएँ।","ढलान पर नीचे जाएँ; पहले हेयरपिन (बिंदु 2) पर सड़क छोड़कर सामने की सीढ़ियाँ लें।","सीढ़ियों के नीचे सड़क (बिंदु 3) पर पहुँचें; सड़क पार करें और सीधे अगली सीढ़ियों पर जाएँ।","जब वे सीढ़ियाँ सड़क पर समाप्त हों, सड़क पार करें, बाएँ मुड़ें और लगभग 10 मीटर (≈30 फ़ुट) चलकर बिंदु 4 तक पहुँचें।","सड़क से दाहिने उतरें और सीढ़ियों से नीचे जाते रहें।","नीचे पहुँचकर दाहिने मुड़ें, बिंदु 5 तक आएँ और दाहिने ओर वाले मेहराब के नीचे आगे बढ़ें।","बंदरगाह (स्पियाज्जा ग्रांदे पियर) बीच के दाहिने हिस्से में लगभग 50 मीटर (≈160 फ़ुट) आगे है।"]},"tickets":{"title":"टिकट खरीदें और सवार हों","tips":["बंदरगाह पर हर फेरी ऑपरेटर का अपना काउंटर होता है।","काउंटर के पास लगा बोर्ड उसी ऑपरेटर की अगली प्रस्थान दिखाता है।","सीधे ऑपरेटर से टिकट खरीदें। किसी वाउचर को बदलने और कतार में लगने के लिए 20–30 मिनट पहले पहुँचें।","टिकट लेने के बाद पियर के छोर तक जाएँ और बोर्डिंग शुरू होने की प्रतीक्षा करें।"]},"arrival":{"title":"कैप्रि पहुँचने पर","paragraphs":["कैप्रि के पर्यटक बंदरगाह (मरीना ग्रांदे) पर उतरें। अपनी योजना के अनुसार फनिक्युलर, टैक्सी या बस के संकेतों का अनुसरण करें। उच्च सीज़न में कतारें लंबी हो सकती हैं—व्यस्त सप्ताहांत में वापसी टिकट पहले से खरीदना मददगार होता है।","पोसीटानो लौटने के लिए देर दोपहर की सेवाओं और मौसम चेतावनियों की जाँच करें। यदि फेरियाँ रद्द हों, तो वापसी को स्थगित करने या अगले दिन सोरेंटो के रास्ते यात्रा करने पर विचार करें।"],"linkIntro":"पोसीटानो में सामान के साथ पैदल चलने के सुझाव यहाँ देखें:","linkLabel":"फेरी डॉक → सामान सहित हॉस्टल ब्रिकेटे"}}')}}]);