"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[53179],{53179:e=>{e.exports=JSON.parse('{"seo":{"title":"अमाल्फी कोस्ट ड्राइविंग: रोड ट्रिप टिप्स","description":"सड़क नियम, ZTL, पार्किंग की हकीकत, वैकल्पिक नंबर प्लेट दिन और कब ड्राइविंग न करें — साथ ही सुरक्षित विकल्प।"},"linkLabel":"अमाल्फी कोस्ट ड्राइविंग: रोड ट्रिप टिप्स","intro":["अमाल्फी कोस्ट पर ड्राइविंग रोमांचक भी है और चुनौतीपूर्ण भी — लेन संकरी हैं, नियम मौसम के साथ बदलते हैं और पार्किंग कम मिलती है।","यह सुझाव तय करने में मदद करेंगे कि कब ड्राइव करें, वाहन कहाँ खड़ा करें और कब कार को पीछे छोड़ देना बेहतर है।"],"sections":[{"id":"rules","title":"नियम और प्रतिबंध","body":["रवानगी से पहले ZTL ज़ोन, वैकल्पिक नंबर प्लेट कैलेंडर और वाहन की चौड़ाई की सीमाएँ जाँचें — गर्मियों के सप्ताहांत में बड़े वाहनों को मुख्य हिस्सों से रोका जा सकता है।","गति कम रखें, स्थानीय लोगों की तरह ब्लाइंड मोड़ों पर हॉर्न बजाएँ और ओवरटेक करते स्कूटरों पर नज़र रखें।"]},{"id":"parking","title":"पार्किंग की हकीकत","body":["Positano/Amalfi में निजी गैरेज पहले से बुक करें और समय, ऊँचाई सीमा तथा कार्ड या नकद जमा की आवश्यकता की पुष्टि करें।","पीक महीनों में €35–€60 प्रति दिन (लगभग ₹3,200–₹5,400) का अनुमान रखें; सड़क पार्किंग दुर्लभ और कड़ाई से नियंत्रित है।"]},{"id":"when-to-park","title":"कब कार छोड़ें","body":["छोटी यात्राओं के लिए Sorrento या Salerno में पार्क करें और गाँवों के भीतर तनाव व खर्च से बचने के लिए बस या फेरी से आगे बढ़ें।","तूफ़ान, त्योहार या देर रात लौटने के समय ड्राइविंग से बचें — सड़क बंद होने और थकान के कारण सार्वजनिक परिवहन या बुक ट्रांसफ़र अधिक सुरक्षित रहते हैं।"]}],"tips":["कॉन्टैक्टलेस भुगतान के साथ छोटे नोट भी रखें; कुछ गैरेज रिमोट या रात भर पहुँच के लिए अतिरिक्त जमा लेते हैं।","वाइनरी डिनर या देर रात के कॉन्सर्ट के बाद थके हुए होकर चट्टानी सड़क पर ड्राइव न करें — पहले से टैक्सी/ट्रांसफ़र बुक करें।"],"faqs":[{"q":"क्या किसी खास परमिट की ज़रूरत है?","a":["नहीं, लेकिन वैकल्पिक नंबर प्लेट नियम और ZTL का पालन ज़रूरी है — प्रवेश से पहले Prefettura या नगर नोटिस जाँचें।"]},{"q":"क्या मैं सड़क किनारे पार्क कर सकता हूँ?","a":["सिर्फ़ स्पष्ट रूप से निशान लगे बे में; बस स्टॉप या मोड़ों को रोकने पर तुरंत जुर्माना या टो किया जा सकता है।"]}]}')}}]);