"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[537537],{537537:e=>{e.exports=JSON.parse('{"seo":{"title":"نصائح الإقامة الطويلة خارج الموسم في بوسيتانو","description":"الاتصال، الإغلاقات، وإيقاع الحياة اليومية خلال الأشهر الهادئة."},"linkLabel":"نصائح الإقامة الطويلة خارج الموسم","intro":["البقاء لبضعة أسابيع في خارج الموسم يعني طقسًا أبرد، عبارات أقل، ووقتًا أطول للعيش كأحد السكان."],"sections":[{"id":"housing","title":"السكن والأسعار","body":["تفاوض على إقامة شهرية مباشرة مع بيوت الضيافة في الأحياء العليا من بوزيتانو؛ التخفيضات بين 20–30٪ شائعة من نوفمبر إلى مارس.","فكر في اتخاذ برايانو أو مينوري قاعدة إذا أردت سوبرماركتس ومسارات مستوية؛ تنقل بالحافلة عندما تشتاق لأمسيات بوزيتانو."]},{"id":"weather","title":"الطقس وما ينبغي حمله","body":["توقع درجات حرارة نهارية بين 12–16 م\xb0 (54–61 ف\xb0) مع أمطار متفرقة — احزم طبقات، ومعطفًا مضادًا للماء، وأحذية بقبضة جيدة للسلالم المبتلة.","احمل مظلة مدمجة؛ فالرياح تمر عبر الوادي ما يجعل الهبّات أقوى مما تشير إليه النشرة الجوية."]},{"id":"work-life","title":"العمل وإيقاع اليوم","body":["تقل ساعات عمل كثير من المقاهي، لذا خطط جلسات العمل معتمدًا على شبكة واي فاي ثابتة في الهوستل أو شقتك.","اطهِ بنفسك أكثر — توفر الأسواق في ميتا أو سورينتو خيارات أوسع، وتكون الحافلات أهدأ لرحلات التسوق."]},{"id":"community","title":"الحفاظ على الحياة الاجتماعية","body":["انضم إلى مجموعات فيسبوك أو محادثات واتساب المحلية لمبادلات اللغة والوجبات المجتمعية.","تطوع في حملات تنظيف البلدة أو الفعاليات الثقافية؛ تنشر البلدية الجداول على اللوحات قرب بيازا دي موليني."]}],"tips":["احتفظ ببعض اليورو المعدنية للسخانات والمغاسل — ما تزال أجهزة الشتاء تعمل عبر عملات معدنية.","تابع جداول الحافلات والعبّارات المخفضة أسبوعيًا؛ تتغير المواعيد حول العطلات."],"faqs":[{"q":"هل تبقى المطاعم مفتوحة؟","a":["يبقى عدد قليل يعمل طوال العام في بوزيتانو، لكن الخيارات الأوسع تكون في أمالفي وسورينتو — خطط لعشاءك عندما تزور تلك المدن."]},{"q":"هل مسار آلهة آمن في الشتاء؟","a":["نعم في الأيام الجافة، لكن تجنب السير بعد أمطار غزيرة؛ تصبح الصخور زلقة وتزداد الانهيارات الصغيرة."]}],"fallback":{"tocTitle":"في هذه الصفحة","intro":[],"toc":[{"href":"#connectivity","label":"الاتصال"},{"href":"#groceries","label":"المستلزمات والبقالة"},{"href":"#transport","label":"النقل خارج الموسم"},{"href":"#weather","label":"الطقس والتحضير"},{"href":"#faqs","label":"الأسئلة الشائعة"}],"sections":[{"id":"connectivity","title":"الاتصال","body":["اجمع بين مقاهي موثوقة بالواي فاي وشرائح eSIM احتياطية؛ تحقق من خرائط التغطية (TIM/Vodafone/WindTre)."]},{"id":"groceries","title":"المستلزمات والبقالة","body":["تختلف ساعات العمل خارج الموسم؛ تزود في وقت مبكر من اليوم وأيام الأسبوع."]},{"id":"transport","title":"النقل خارج الموسم","body":["تُقلّص العبارات رحلاتها؛ اعتمد على الحافلات وخطط لأيام المطر/الرياح."]},{"id":"weather","title":"الطقس والتحضير","body":["احمل طبقات ووسائل حماية من المطر؛ وأحذية جيدة للسلالم المبتلة."]}],"faqsTitle":"الأسئلة الشائعة","faqs":[{"q":"هل يبقى شيء مفتوحًا في الشتاء؟","a":["نعم، لكن الخيارات أقل. ركز على البقالة، والمشي، والإطلالات، والمدن المجاورة."]}]}}')}}]);