"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[562626],{562626:n=>{n.exports=JSON.parse('{"seo":{"title":"Hostel Brikette → bến ph\xe0, mang h\xe0nh l\xfd","description":"Lộ tr\xecnh từng bước từ hostel tới bến ph\xe0 Positano bằng xe bu\xfdt Interno v\xe0 dịch vụ khu\xe2n v\xe1c."},"linkLabel":"Hostel Brikette → bến ph\xe0, mang h\xe0nh l\xfd","intro":["Rời Hostel Brikette với nhiều h\xe0nh l\xfd? H\xe3y đi xe bu\xfdt Interno Positano xuống dốc v\xe0 nhờ đội khu\xe2n v\xe1c tại bến chuyển c\xe1c vali nặng để bạn ra ph\xe0 m\xe0 kh\xf4ng phải vật lộn với cầu thang Positano."],"beforeList":["Kiểm tra lịch xe bu\xfdt Interno Positano hiện tại — đ\xe2y l\xe0 [lịch tr\xecnh v\xe0 gi\xe1 v\xe9 mới nhất](https://forum.amalfi.com/t/mobility-amalfi-coast-bus-positano-nocelle-praiano/26).","Mua v\xe9 Interno Positano tại quầy tabacchi cạnh Chiesa Nuova trước khi l\xean xe (hoặc nhờ lễ t\xe2n x\xe1c nhận c\xf2n v\xe9).","Sắp xếp dịch vụ khu\xe2n v\xe1c nếu bạn c\xf3 h\xe0nh l\xfd cồng kềnh — lễ t\xe2n c\xf3 thể gọi trước, hoặc xem [hướng dẫn dịch vụ khu\xe2n v\xe1c](guide:porterServices) để biết số điện thoại v\xe0 gi\xe1 cả."],"sections":[{"id":"hostel-to-stop","title":"Từ cổng hostel đến điểm dừng Chiesa Nuova","body":["L\xe0m thủ tục trả ph\xf2ng, sau đ\xf3 đi xuống cầu thang ch\xednh tới Viale Pasitea v\xe0 rẽ tr\xe1i (xuống dốc). Đi bộ hơn 100 m\xe9t để tới quầy tabacchi/quầy b\xe1o m\xe0 bạn đ\xe3 d\xf9ng khi đến nơi.","Điểm dừng xe bu\xfdt Interno Positano nằm ngay cạnh quầy tabacchi. H\xe3y chuẩn bị sẵn v\xe9; nếu cần mua th\xeam, h\xe3y v\xe0o trong mua trước khi xe đến."]},{"id":"bus-to-mulini","title":"Đi xe bu\xfdt Interno Positano tới Piazza dei Mulini","body":["L\xean xe bu\xfdt Interno Positano — bảng điểm đến sẽ ghi \\"interno positano\\". Giữ h\xe0nh l\xfd tr\xean đ\xf9i hoặc ở khu vực giữa để lối đi th\xf4ng tho\xe1ng.","Ngồi khoảng 10 ph\xfat đến điểm Piazza dei Mulini (điểm dừng lớn cuối c\xf9ng trước khu đi bộ). Nếu kh\xf4ng chắc, h\xe3y b\xe1o với t\xe0i xế rằng bạn đang đi ra b\xe3i biển."]},{"id":"mulini-to-dock","title":"Từ Piazza dei Mulini đến bến ph\xe0","body":["Xuống xe, gặp đội khu\xe2n v\xe1c nếu bạn đ\xe3 đặt trước v\xe0 đi thẳng v\xe0o lối đi d\xe0nh cho người đi bộ dẫn tới Spiaggia Grande.","Đi dọc lối ven biển khoảng 7 ph\xfat — d\xf9ng [lộ tr\xecnh n\xe0y](https://www.google.com/maps/dir/Brikette+Hostel,+Via+G.+Marconi,+358,+84017+Positano+SA/Positano+Spiaggia/@40.6290767,14.4840629,16z/data=!4m14!4m13!1m5!1m1!1s0x133b97669dd0003b:0xf11f22a59b1b00da!2m2!1d14.480818!2d40.629634!1m5!1m1!1s0x133b976916d1a44d:0xf1e08d61658b4291!2m2!1d14.4878892!2d40.6276549!3e2) để đi tr\xean những đoạn dốc thoải nhất.","Tại cầu t\xe0u, l\xe0m thủ tục với h\xe3ng ph\xe0 của bạn. Đội khu\xe2n v\xe1c sẽ chuyển h\xe0nh l\xfd ri\xeang; h\xe3y giữ hộ chiếu, thuốc men v\xe0 đồ gi\xe1 trị b\xean người."]}],"stepsList":["Mua v\xe9 xe bu\xfdt Interno Positano tại quầy tabacchi Chiesa Nuova v\xe0 x\xe1c nhận dịch vụ khu\xe2n v\xe1c.","Đi bộ 100 m xuống dốc từ hostel tới điểm dừng Chiesa Nuova v\xe0 chờ ở ph\xeda hướng biển của Viale Pasitea.","Đi xe bu\xfdt Interno Positano đến Piazza dei Mulini (điểm dừng lớn cuối c\xf9ng trước b\xe3i biển).","Gặp đội khu\xe2n v\xe1c v\xe0 đi dọc lối ven biển tới bến ph\xe0 trong khi họ chuyển h\xe0nh l\xfd nặng."],"kneesList":["Đi xe bu\xfdt Interno Positano gi\xfap bạn tr\xe1nh h\xe0ng trăm bậc dốc bằng một qu\xe3ng đi bộ ngắn, bằng phẳng.","Đội khu\xe2n v\xe1c c\xf3 thể chở vali tr\xean xe đẩy ri\xeang n\xean bạn chỉ cần mang bal\xf4 nhỏ tr\xean lối đi ven biển."],"kneesDockPrefix":"Cần lộ tr\xecnh leo ngược khi quay về? Xem","kneesDockLinkLabel":"hướng dẫn đến Chiesa Nuova","kneesPorterPrefix":"Muốn biết gi\xe1 v\xe0 c\xe1ch đặt dịch vụ khu\xe2n v\xe1c? Xem","kneesPorterLinkLabel":"hướng dẫn dịch vụ khu\xe2n v\xe1c","faqs":[{"q":"T\xf4i c\xf3 thể mua v\xe9 Interno Positano ở đ\xe2u gần hostel?","a":["Quầy tabacchi/quầy b\xe1o cạnh điểm dừng Chiesa Nuova lu\xf4n c\xf3 sẵn v\xe9 v\xe0 lễ t\xe2n c\xf3 thể x\xe1c nhận c\xf2n v\xe9 trong ng\xe0y. T\xe0i xế kh\xf4ng b\xe1n v\xe9 tr\xean xe."]},{"q":"T\xf4i n\xean đặt đội khu\xe2n v\xe1c trước bao l\xe2u?","a":["V\xe0o m\xf9a h\xe8 h\xe3y gọi trước 30–45 ph\xfat trước giờ bạn muốn rời đi để họ gặp bạn tại Piazza dei Mulini. M\xf9a vắng kh\xe1ch, 15 ph\xfat thường l\xe0 đủ — lễ t\xe2n c\xf3 thể gọi gi\xfap bạn."]},{"q":"Nếu xe bu\xfdt Interno kh\xf4ng chạy khi t\xf4i đi th\xec sao?","a":["Nếu dịch vụ tạm dừng v\xe0o đ\xeam muộn, h\xe3y nhờ đội khu\xe2n v\xe1c mang h\xe0nh l\xfd xuống tận nơi v\xe0 bạn tự đi bộ dọc lối ven biển, hoặc đi xe bu\xfdt SITA về hướng Sponda (chạy muộn hơn) rồi tiếp tục đi bộ xuống dốc."]}],"howtoSteps":["X\xe1c nhận v\xe9 v\xe0 dịch vụ khu\xe2n v\xe1c tại Chiesa Nuova","Đi bộ xuống dốc từ hostel đến điểm dừng Chiesa Nuova","Đi xe bu\xfdt Interno Positano tới Piazza dei Mulini","Gặp đội khu\xe2n v\xe1c v\xe0 đi bộ tới cầu ph\xe0"]}')}}]);