"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[566012],{566012:n=>{n.exports=JSON.parse('{"seo":{"title":"Đi Capri trong ng\xe0y từ Positano","description":"L\xean kế hoạch đi Capri trong ng\xe0y: ph\xe0, thời gian v\xe0 những điểm nhất định phải gh\xe9."},"linkLabel":"Đi Capri trong ng\xe0y từ Positano","intro":["Du lịch Capri trong ng\xe0y từ Positano: xuất ph\xe1t sớm, mang gọn nhẹ v\xe0 l\xean lịch v\xf2ng tr\xf2n c\xe2n bằng giữa Anacapri, cảnh đẹp v\xe0 chuyến về dễ d\xe0ng."],"toc":[{"href":"#morning","label":"Bản dịch đang được chuẩn bị."},{"href":"#midday","label":"Bản dịch đang được chuẩn bị."},{"href":"#afternoon","label":"Buổi chiều — thị trấn Capri & quay về"},{"href":"#gallery","label":"Bộ sưu tập ảnh"},{"href":"#faqs","label":"C\xe2u hỏi thường gặp"}],"sections":[{"id":"morning","title":"Bản dịch đang được chuẩn bị.","body":["Bắt chuyến ph\xe0 đầu ti\xean để đến trước d\xf2ng kh\xe1ch t\xe0u biển, sau đ\xf3 đi xe bu\xfdt l\xean Anacapri v\xe0 đi c\xe1p treo Monte Solaro nếu bạn th\xedch độ cao.","Ăn s\xe1ng nhẹ ở Piazza Vittoria v\xe0 mua v\xe9 ph\xe0 lượt về ngay lập tức để chốt giờ khởi h\xe0nh."]},{"id":"midday","title":"Bản dịch đang được chuẩn bị.","body":["Đi bộ hoặc bắt xe bu\xfdt xuống Marina Piccola để tắm hoặc chụp ảnh; mang kem chống nắng th\xe2n thiện với san h\xf4 v\xe0 \xedt nhất 1 l\xedt nước mỗi người.","Mua panini hoặc salad đ\xf3ng hộp ở tiệm deli thay v\xec ăn trưa l\xe2u để c\xf3 thời gian leo ngược trở lại m\xe0 kh\xf4ng vội."]},{"id":"afternoon","title":"Buổi chiều — thị trấn Capri & quay về","body":["Đi t\xe0u điện leo n\xfai l\xean thị trấn Capri, dạo Via Camerelle, Giardini di Augusto v\xe0 thưởng thức kem/espresso cuối ng\xe0y.","C\xf3 mặt tại Marina Grande \xedt nhất 30 ph\xfat trước giờ ph\xe0 v\xec c\xf3 thể phải xếp h\xe0ng t\xe0u điện hoặc gửi h\xe0nh l\xfd."]}],"galleryTitle":"Bộ sưu tập ảnh","gallery":{"items":{"monteSolaro":{"alt":"Quang cảnh từ Monte Solaro, Capri","caption":"To\xe0n cảnh Monte Solaro"},"gardensOfAugustus":{"alt":"Vườn Augustus, Capri","caption":"Giardini di Augusto"}}},"faqsTitle":"C\xe2u hỏi thường gặp","howTo":{"steps":[{"name":"Kiểm tra lịch ph\xe0 theo m\xf9a","text":"X\xe1c nhận chuyến s\xe1ng v\xe0 chuyến cuối quay về Positano."},{"name":"Đến bến sớm","text":"M\xf9a cao điểm n\xean c\xf3 mặt trước 20–30 ph\xfat."},{"name":"L\xean kế hoạch tuyến tr\xean đảo","text":"Bản dịch đang được chuẩn bị."}]},"tipsTitle":"Mẹo trong ng\xe0y","tips":["Mua v\xe9 ph\xe0 lượt đi v\xe0 về c\xf9ng l\xfac để chủ động điều chỉnh lịch tr\xecnh m\xe0 kh\xf4ng phải t\xecm chỗ trống v\xe0o ph\xfat cuối.","Mang balo nhỏ với nước, kem chống nắng v\xe0 tiền mặt — ATM tr\xean Capri c\xf3 thể hết tiền v\xe0o tuần cao điểm.","Tải bản đồ ngoại tuyến v\xe0 lịch xe bu\xfdt của Capri; t\xedn hiệu yếu quanh Anacapri v\xe0 khu chờ Blue Grotto."],"warningsTitle":"Cảnh b\xe1o cần biết","warnings":["C\xe1p treo v\xe0 t\xe0u điện leo n\xfai đ\xf4ng sau 15:00; lỡ lượt c\xf3 thể phải chờ chuyến ph\xe0 standby.","Biển động c\xf3 thể huỷ chuyến chiều m\xe0 kh\xf4ng b\xe1o trước — kiểm tra th\xf4ng b\xe1o buổi s\xe1ng v\xe0 chuẩn bị phương \xe1n qua Sorrento.","Blue Grotto thường đ\xf3ng khi gi\xf3 hoặc s\xf3ng; một số nh\xe0 b\xe1n v\xe9 vẫn mở n\xean h\xe3y x\xe1c nhận hoạt động trước khi xếp h\xe0ng."],"faqs":[{"q":"C\xf3 kịp gh\xe9 Blue Grotto kh\xf4ng?","a":["Chỉ n\xean đi nếu đ\xf3 l\xe0 ưu ti\xean; cần thuyền shuttle v\xe0 chờ 60–90 ph\xfat, dễ l\xe0m chật lịch tr\xecnh c\xf2n lại."]},{"q":"C\xf3 cần đặt v\xe9 trước?","a":["M\xf9a cao điểm n\xean giữ chỗ ph\xe0 buổi s\xe1ng v\xe0 c\xe2n nhắc mua v\xe9 c\xe1p treo hoặc tour thuyền online; m\xf9a giao m\xf9a thường c\xf3 thể mua trực tiếp."]}]}')}}]);