"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[566773],{566773:e=>{e.exports=JSON.parse('{"seo":{"title":"دليل نادي شاطئ أريينزو","description":"احجز مقاعد الاسترخاء، وقرّر بين النزول المؤلف من 300 درجة أو قارب النقل، وضع خطة للطعام والمواصلات إلى نادي شاطئ أريينزو."},"linkLabel":"دليل نادي شاطئ أريينزو","intro":["يقع أريينزو شرق بوسيتانو مباشرةً بشمس بعد ظهر طويلة، ومياه فيروزية، وسلّم درامي ينحدر من طريق ساحل أمالفي. إنه أكثر الليدو حيوية ويمكن الوصول إليه بسهولة من هوستل بريكيت، لكن بسبب الدرج وسياسة الحجز المسبق ستحتاج إلى خطة قبل الانطلاق.","استخدم هذا الدليل لتثبيت حجزك، وتقرر ما إذا كنت ستستقل قارب النقل أو تمشي نزولاً عبر 300 درجة، ولتضع ميزانية للغداء والمشروبات والعودة."],"toc":[{"href":"#why-go","label":"لماذا تختار أريينزو"},{"href":"#getting-there","label":"كيفية الوصول"},{"href":"#layout","label":"التصميم والخدمات"},{"href":"#packages","label":"الباقات وخيارات الطعام"},{"href":"#heading-back","label":"العودة"}],"sections":[{"id":"why-go","title":"لماذا يناسب أريينزو نزلاء هوستل بريكيت","body":["يعد أريينزو أقرب نادي شاطئي متكامل الخدمات خارج الازدحام في سبياجيا غراندي. التراسات المنحوتة في الجرف تلتقط أشعة الشمس حتى أوائل المساء، والمياه تظل صافية للسباحة، ويُبقي البار الأجواء حيوية من دون أن تتحول إلى سهرات ليلية.","يمكنك تقسيم مجموعتك بين باقات تشمل مقاعد الاسترخاء والغداء أو حجز منصة في الصف الأمامي ليوم فخم. مع بعض التخطيط تتجنب الطوابير وتعود إلى الهوستل قبل مشروبات الغروب على التراس."]},{"id":"getting-there","title":"الوصول: قارب النقل أم 300 درجة سلم","body":["الطريق الأسهل هو قارب النقل التابع لنادي شاطئ أريينزو الذي يغادر من سبياجيا غراندي. تعمل القوارب تقريبًا كل 30 دقيقة بدءًا من 09:30، مع آخر رحلة ذهاب حوالى 13:30. يتضمن تأكيد الحجز رمز QR—أبرزه على الرصيف واحتفظ بجدول العودة في متناولك.","يمكنك المشي إذا فضلت تجنب القارب. استقل حافلة سيتا باتجاه أمالفي (اصعد من موقف سبوندا على بُعد 10 دقائق نزولاً من الهوستل) وانزل عند موقف أريينزو المُلصق. من هناك انزل 300 درجة عبر شرفات الليمون حتى مدخل النادي.","يُنصح بتجنب الوصول بالسيارة أو السكوتر؛ أماكن الوقوف محدودة للغاية وشرطة المرور تفرض غرامات على المركبات التي تعيق المنعطفات."]},{"id":"layout","title":"التصميم، المرافق، وتغطية الشمس","body":["يعود السطح الرئيسي إلى نادي شاطئ أريينزو مع مقاعد برتقالية، وخدمة مناشف، ودُشات بمياه عذبة، وبار حيوي. يوجد شريط مجاني أصغر في أقصى اليمين بالقرب من رصيف القارب، لكن المساحة تختفي بعد 10:30 وستحتاج رغم ذلك إلى التعامل مع الدرج وجميع أمتعتك.","تصل الشمس إلى الصفَّين الأولين حتى حوالى 17:30 في الصيف قبل أن يلقي الجرف بظلاله. اطلب من الطاقم تعديل المظلات مع تغير الضوء، خاصة إذا كنت تفضّل يوم تسمير كامل.","تتضمن باقات اليوم الدُّشات بمياه عذبة، والخزائن، وغرف تبديل الملابس. شبكة الواي فاي متقطعة؛ نزّل قوائم التشغيل أو تحديثات العبارات قبل الوصول."]},{"id":"packages","title":"الباقات، الطعام، والحجوزات","body":["تبدأ أسرّة اليوم القياسية حوالى 70 يورو لشخصين وتشمل مظلة، ومناشف، ومشروبين ترحيبيين، وقارب النقل. المنصات في الصف الأمامي وكبائن بريستيج تتراوح بين 120 و200 يورو حسب الشهر.","يُقدَّم الغداء من 12:30 مع باستا المأكولات البحرية والسلطات والأطباق المشوية. توقّع أن تتراوح الأطباق بين 18 و28 يورو؛ احجز طاولة عند تسجيل الدخول لتجنب ازدحام الساعة 14:00.","تُدار الحجوزات مباشرة عبر موقع أريينزو الإلكتروني أو من خلال مراسلتهم على واتساب. تؤمّن تفاصيل الدفع مقعدك؛ الإلغاءات خلال 48 ساعة عادةً ما تتكلف رسومًا، لذا تحقق من توقعات الطقس وخطط العبارات أولاً."]},{"id":"heading-back","title":"العودة إلى هوستل بريكيت","body":["تغادر قوارب العودة أريينزو كل نصف ساعة بدءًا من حوالى 15:00؛ يغادر آخر قارب عادةً عند 18:00 لكن المواعيد تتغير خلال المواسم الانتقالية—تحقق من اللوحة بالقرب من الاستقبال.","إذا نزلت سيرًا، خصص 15–20 دقيقة لصعود الدرج إلى الطريق. تمر الحافلات المتجهة إلى بوسيتانو كل 30–40 دقيقة بعد الظهر؛ ثبّت تذكرتك على متن الحافلة واحتفظ بأخرى إضافية للعودة المتأخرة.","هل ترغب في سباحة ليلية؟ رتّب مسبقًا سيارة أجرة عبر الاستقبال أو خدمة الكونسيرج الرقمية، إذ إن تطبيقات استدعاء السيارات لا تعمل بثبات على طول الساحل."]}],"essentialsTitle":"ما يجب توقعه","essentials":["الحجوزات إلزامية في موسم الذروة—احجز عبر الإنترنت أو عبر واتساب قبل مغادرة الهوستل.","اختر بين قارب النقل من سبياجيا غراندي أو الدرج المؤلف من 300 درجة من موقف حافلة سيتا.","ليدو متكامل الخدمات مع بار ومطعم ودُش وخدمة مناشف متضمنة في باقات اليوم."],"typicalCostsTitle":"التكاليف المعتادة","typicalCosts":["سرير يومي لشخصين مع قارب النقل: 70–90 يورو حسب الموسم.","منصة أو كابانا في الصف الأمامي: 120–200 يورو لضيفين.","الأطباق الرئيسية للغداء: 18–28 يورو، الكوكتيلات 12–15 يورو، المياه المعبأة 3–4 يورو."],"tipsTitle":"نصائح سريعة","tips":["التقط لقطة شاشة لتأكيد حجزك؛ إشارة الهاتف المحمول تضعف على طول الدرج وعند الرصيف.","أحضر زجاجة ماء قابلة لإعادة الاستخدام—التعبئة متاحة في البار وتقلل التكاليف في الأيام الحارة.","قارن خيارات الساحل الأخرى في %LINK:positanoBeaches|نظرة عامة على الشواطئ% إذا كنت ترغب في بديل أكثر هدوءًا أو مجانيًا."],"warningsTitle":"معلومات مهمة","warnings":["لا تُعاد الأموال نقدًا عند الوصول المتأخر؛ اتصل مسبقًا إذا كنت متأخرًا ليحتفظ الطاقم بمقاعدك.","الجزء المجاني صغير جدًا ويتطلب منك رغم ذلك التعامل مع الدرج كاملاً ومعك أمتعتك.","تؤدي ظروف الأمواج أحيانًا إلى إيقاف قارب النقل—سيرسل الطاقم رسائل نصية بالتحديثات، لكن احتفظ بخطة الحافلة جاهزة."],"faqsTitle":"أسئلة شائعة حول نادي شاطئ أريينزو","faqs":[{"q":"كم من الوقت يجب أن أحجز مسبقًا؟","a":["احجز قبل ثلاثة إلى خمسة أيام على الأقل بين يونيو وسبتمبر، خاصة في عطلات نهاية الأسبوع أو للمنصات الأمامية. غالبًا ما يتوفر حجز اليوم التالي في الموسم الانتقالي (أبريل، مايو، أكتوبر) لكن تأكد من جدول قارب النقل."]},{"q":"هل يمكنني الزيارة من دون شراء باقة؟","a":["نعم، لكن الخيارات محدودة. يوجد شريط صغير عام بجوار النادي ويمتلئ بحلول منتصف الصباح. لن تتمكن من استخدام الحمامات أو الدُّش من دون باقة، وستحتاج رغم ذلك إلى التعامل مع الدرج الطويل."]},{"q":"ماذا ينبغي أن أرتدي لصعود الدرج؟","a":["ارتدِ صندلًا متينًا أو حذاءً رياضيًا للنزول. احمل الأغراض الثقيلة في حقيبة ظهر وتوقف عند المنصات الوسطى. أثناء الصعود، خذ فترات راحة في الظل وترطّب قبل ركوب الحافلة أو سيارة الأجرة."]}],"gallery":{"title":"لمحات مصورة لشاطئ أرينزو","items":[{"alt":"تراسات نادي شاطئ أرينزو","caption":"منصات على الجرف مع كراسي برتقالية"},{"alt":"مرسى قارب النقل إلى أرينزو","caption":"الصعود إلى القارب من شاطئ سبياجيا غراندي"},{"alt":"درج النزول إلى أرينزو","caption":"300 درجة بين مدرجات الليمون"}]}}')}}]);