"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[578141],{578141:e=>{e.exports=JSON.parse('{"meta":{"title":"Positano to Amalfi by Ferry | Hostel Brikette","description":"Step-by-step ferry trip from Positano to Amalfi: tickets, where to board at Spiaggia Grande, schedules, and what to expect on arrival."},"hero":{"eyebrow":"Hostel Brikette Travel Guide","title":"Positano to Amalfi — Ferry","description":"Follow these steps to travel from Positano to Amalfi by ferry. You’ll find tips on tickets, where to board in Positano, what to expect on arrival, and links to live schedules."},"tip":{"label":"Tip","copy":"Need other transport options? Open the <link>How to Get Here overview</link> for buses, mixed routes, and airport transfers."},"sections":{"overview":{"title":"Journey overview","points":["Seasonal ferries link Positano and Amalfi with scenic coastal views.","Services may vary by season and weather; always check the day-of schedule.","Allow extra time in peak months for queues at the pier."]},"tickets":{"title":"Tickets and schedules","intro":"Check latest times and prices on <travelmar>Travelmar</travelmar> and <positano>Positano.com — Ferry schedules</positano>.","points":["Buy tickets online in advance during high season or at operator booths by the pier.","Arrive 15–20 minutes before departure to exchange vouchers and queue for boarding.","Keep your ticket or QR code accessible for staff checks."]},"positanoPier":{"title":"Where to board in Positano","points":["Departures are from Positano’s main pier on Spiaggia Grande.","Operator booths line the waterfront; signage shows the next departure for each company.","Confirm the correct pier on your ticket; staff can direct you if unsure."]},"amalfiArrival":{"title":"Arriving in Amalfi","points":["Boats arrive at Amalfi’s waterfront piers near Piazza Flavio Gioia.","The historic center and promenade are directly adjacent to the port.","For onward buses (e.g., Ravello or Sorrento), follow signs to the SITA stop near the waterfront."]}},"externalLinks":{"travelmar":"https://www.travelmar.it/en/home","positano":"https://www.positano.com/en/ferry-schedule"}}')}}]);