"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[609624],{609624:n=>{n.exports=JSON.parse('{"seo":{"title":"B\xe3i biển đẹp nhất ở Positano","description":"Tổng quan chi tiết về c\xe1c b\xe3i biển tại Positano, Praiano, Furore, Sorrento, Amalfi v\xe0 chuyến đi Capri trong ng\xe0y."},"linkLabel":"B\xe3i biển đẹp nhất ở Positano","intro":["So s\xe1nh c\xe1c b\xe3i biển Positano v\xe0 những vịnh nhỏ c\xf3 thể đi trong ng\xe0y trước khi bạn quyết định thu\xea ghế. Phần tổng quan n\xe0y n\xeau bật gi\xe1 cả, khu miễn ph\xed v\xe0 bầu kh\xf4ng kh\xed chung để bạn chọn điểm ph\xf9 hợp với ng\xe2n s\xe1ch v\xe0 năng lượng của m\xecnh.","Chọn bất kỳ t\xean b\xe3i biển n\xe0o trong bảng hoặc phần t\xf3m tắt b\xean dưới để mở hướng dẫn chi tiết với chỉ đường, gợi \xfd ăn uống v\xe0 hướng dẫn từng bước."],"galleryTitle":"Điểm nhấn b\xe3i biển Positano","toc":[{"href":"#overview","label":"C\xe1ch lựa chọn"},{"href":"#positano-beaches","label":"B\xe3i biển Positano"},{"href":"#praiano-beaches","label":"Praiano"},{"href":"#furore-beaches","label":"Furore"},{"href":"#sorrento-beaches","label":"Sorrento"},{"href":"#amalfi-beaches","label":"Amalfi"},{"href":"#logistics","label":"Đi lại"},{"href":"#faqs","label":"C\xe2u hỏi thường gặp"}],"sections":[{"id":"overview","title":"C\xe1ch chọn b\xe3i biển ph\xf9 hợp","body":["Positano c\xf3 cả những đoạn b\xe3i sỏi miễn ph\xed lẫn c\xe1c lido tổ chức b\xe0i bản. Gi\xe1 thu\xea ghế dao động từ 15 € ở c\xe2u lạc bộ nhỏ tới 400 € tại c\xe1c boong cao cấp như La Scogliera, v\xec vậy h\xe3y x\xe1c định sớm bạn muốn được phục vụ trọn g\xf3i hay chỉ cần một chỗ trải khăn.","Bảng so s\xe1nh của ch\xfang t\xf4i nh\xf3m c\xe1c b\xe3i theo thị trấn. Mỗi mục liệt k\xea gi\xe1 ghế khởi điểm, c\xf3 khu miễn ph\xed hay kh\xf4ng v\xe0 v\xe0i ưu nhược điểm nhanh để bạn c\xe2n bằng giữa nắng, đ\xe1m đ\xf4ng v\xe0 sự tiện lợi."]},{"id":"positano-beaches","title":"C\xe1c b\xe3i biển Positano trong nh\xe1y mắt","body":["Fornillo lu\xf4n l\xe0 b\xe3i y\xeau th\xedch của kh\xe1ch hostel nhờ kh\xf4ng kh\xed thư gi\xe3n, hai khu miễn ph\xed (một khu ẩn kh\xe1 y\xean tĩnh) v\xe0 c\xe1c c\xe2u lạc bộ như Pupetto, Grassi, Ferdinando, La Marinella với gi\xe1 khoảng 15 €. Đường leo về lại rất dốc n\xean nhiều người cắt qua b\xe3i ch\xednh rồi đi xe bu\xfdt l\xean — xem %LINK:fornilloBeachGuide|hướng dẫn b\xe3i Fornillo% để biết th\xf4ng tin nh\xe0 vận h\xe0nh v\xe0 sơ đồ khu miễn ph\xed.","Spiaggia Grande mang lại khung cảnh bưu thiếp với đường ch\xe2n trời Positano ph\xeda sau những chiếc \xf4. Gi\xe1 ở đ\xe2y cao hơn: L\'Incanto khoảng 25 €, trong khi boong VIP của La Scogliera 100–400 €. Giữa b\xe3i c\xf3 một dải c\xf4ng cộng nhưng đa số kh\xe1ch vẫn chuộng sự y\xean tĩnh của Fornillo — h\xe3y theo %LINK:positanoMainBeachWalkDown|chỉ dẫn đi bộ% hoặc %LINK:positanoMainBeachBusDown|hướng dẫn đi xe bu\xfdt% để tới nhanh, rồi d\xf9ng %LINK:positanoMainBeachBusBack|mẹo đi xe bu\xfdt quay về% nếu bạn muốn tr\xe1nh leo dốc.","Arienzo kết hợp nắng chiều k\xe9o d\xe0i với h\xe0nh tr\xecnh xuống cầu thang đầy ấn tượng. C\xe1c g\xf3i ghế tắm nắng k\xe8m bữa trưa v\xe0 thuyền đưa đ\xf3n từ khoảng 60 € v\xe0 bao gồm dịch vụ thuyền taxi của c\xe2u lạc bộ — rất đ\xe1ng gi\xe1 v\xec tự đi v\xe0o b\xe3i kh\xe1 kh\xf3.","Laurito l\xe0 vịnh nhỏ gọn do Da Adolfo (miễn ph\xed ghế khi bạn ăn uống) v\xe0 Villa Tre Ville (từ 30 €) phục vụ. B\xe3i c\xf3 nắng muộn trong ng\xe0y nhưng c\xe1ch dễ nhất để tới chỉ l\xe0 đi thuyền shuttle của họ; khu c\xf4ng cộng cực kỳ nhỏ."]},{"id":"praiano-beaches","title":"Gợi \xfd đi Praiano trong ng\xe0y","body":["B\xe3i Gavitella ở Praiano l\xe0 thi\xean đường l\xfac ho\xe0ng h\xf4n với s\xe2n thượng nh\xe0 h\xe0ng ho\xe0n hảo cho aperitivo v\xe0 bữa tối. Kh\xf4ng c\xf3 khu miễn ph\xed v\xe0 buổi trưa b\xea t\xf4ng hắt nhiệt mạnh n\xean h\xe3y đến v\xe0o cuối chiều.","Marina di Praia l\xe0 vịnh sỏi hẹp được bao quanh bởi thuyền ch\xe0i v\xe0 v\xe1ch đ\xe1. B\xean cạnh c\xe1c lido vẫn c\xf2n một đoạn miễn ph\xed nhỏ — l\xfd tưởng nếu bạn muốn đổi kh\xf4ng kh\xed m\xe0 vẫn c\xf3 dịch vụ."]},{"id":"furore-beaches","title":"Vịnh hẹp kiểu fjord của Furore","body":["Fiordo di Furore l\xe0 biểu tượng Instagram của Amalfi Coast với c\xe2y cầu đ\xe1 bắc ngang v\xe0 điểm nhảy v\xe1ch. V\xe0o cửa miễn ph\xed v\xe0 chi ph\xed ph\xe1t sinh thấp, nhưng diện t\xedch cực kỳ hạn chế n\xean h\xe3y đến sớm hoặc tr\xe1nh giờ cao điểm."]},{"id":"sorrento-beaches","title":"Hồ tắm Regina Giovanna ở Sorrento","body":["Regina Giovanna Bath (Bagni della Regina Giovanna) gần Sorrento l\xe0 hồ bơi tự nhi\xean kho\xe9t v\xe0o v\xe1ch đ\xe1. V\xe0o cửa miễn ph\xed, nước m\xe1t lạnh v\xe0 vẫn c\xf2n kh\xe1 y\xean ả so với c\xe1c b\xe3i ch\xednh ở Positano — nhớ mang gi\xe0y đi nước v\xe0 đồ ăn nhẹ v\xec kh\xf4ng c\xf3 dịch vụ."]},{"id":"amalfi-beaches","title":"C\xe1c lido tại thị trấn Amalfi","body":["B\xe3i Marina Grande ch\xednh của Amalfi c\xf3 h\xe0ng loạt c\xe2u lạc bộ như Stella Maris, Marina Grande Beach Club, Silver Moon v\xe0 Bagni Mar di Cobalto. Kỳ vọng set-up lido cổ điển với phục vụ b\xe0n, v\xf2i sen v\xe0 tầm nh\xecn hướng nh\xe0 thờ Duomo — h\xe3y đặt trước v\xe0o m\xf9a cao điểm để giữ h\xe0ng ghế đầu."]},{"id":"logistics","title":"Di chuyển đến v\xe0 rời b\xe3i biển","body":["Đối với Fornillo v\xe0 Spiaggia Grande, xuất ph\xe1t từ hostel rồi đi xuống theo Viale Pasitea. Nếu nghĩ tới chuyện leo ngược sẽ nản, h\xe3y tiếp tục men theo bờ biển tới b\xe3i ch\xednh v\xe0 bắt xe bu\xfdt cam nội bộ l\xean Chiesa Nuova, c\xe1ch Hostel Brikette v\xe0i ph\xfat đi bộ.","Arienzo v\xe0 Laurito c\xf3 thuyền shuttle ri\xeang từ Spiaggia Grande; h\xe3y x\xe1c nhận chuyến về cuối c\xf9ng trước khi ở lại uống aperitivo. Với Praiano, Furore, Sorrento hoặc Amalfi, h\xe3y xem c\xe1c hướng dẫn li\xean kết để biết tuyến xe bu\xfdt, lịch ph\xe0 v\xe0 c\xe1ch di chuyển chi tiết."]}],"prosCons":[{"title":"Fornillo","guideKey":"fornilloBeachGuide","pros":["Ghế tắm nắng từ 15 €","Hai khu miễn ph\xed lớn v\xe0 khu b\xed mật","Kh\xf4ng kh\xed thư gi\xe3n"],"cons":["Đường leo về dốc","\xcdt b\xf3ng r\xe2m nếu kh\xf4ng thu\xea ghế"]},{"title":"Spiaggia Grande","guideKey":"positanoMainBeachWalkDown","pros":["Đường ch\xe2n trời Positano biểu tượng","Kết nối ph\xe0 trung t\xe2m","C\xf3 dải c\xf4ng cộng"],"cons":["Ghế đắt nhất khu vực","Rất đ\xf4ng v\xe0o buổi trưa"]},{"title":"Arienzo","pros":["Nắng chiều k\xe9o d\xe0i","G\xf3i combo gồm bữa trưa v\xe0 thuyền","Tầm nh\xecn ngoạn mục"],"cons":["H\xe0ng trăm bậc thang","Kh\xf3 v\xe0o nếu kh\xf4ng đi shuttle của c\xe2u lạc bộ"]},{"title":"Laurito","pros":["Nắng muộn trong ng\xe0y","Da Adolfo nổi tiếng với bữa trưa","Villa Tre Ville phục vụ cao cấp"],"cons":["Khu c\xf4ng cộng rất nhỏ","Phụ thuộc v\xe0o giờ thuyền taxi"],"guideKey":"lauritoBeachGuide"},{"title":"Gavitella (Praiano)","pros":["Aperitivo ngắm ho\xe0ng h\xf4n","S\xe2n thượng nh\xe0 h\xe0ng"],"cons":["Kh\xf4ng c\xf3 khu miễn ph\xed","Bục b\xea t\xf4ng n\xf3ng v\xe0o giữa ng\xe0y"]},{"title":"Marina di Praia","guideKey":"marinaDiPraiaBeaches","pros":["Vịnh l\xe0ng ch\xe0i duy\xean d\xe1ng","Đoạn miễn ph\xed s\xe1t c\xe1c lido"],"cons":["B\xe3i hẹp","Mất nắng sớm trong ng\xe0y"]},{"title":"Fiordo di Furore","pros":["V\xe0o cửa miễn ph\xed","Nhảy v\xe1ch đ\xe1","Ph\xf4ng cầu độc đ\xe1o"],"cons":["B\xe3i cực nhỏ","Đ\xf4ng nhanh"]},{"title":"Regina Giovanna Bath","pros":["Hồ bơi tự nhi\xean miễn ph\xed","Nước m\xe1t cho ng\xe0y n\xf3ng"],"cons":["Kh\xf4ng c\xf3 dịch vụ","Lối v\xe0o đ\xe1 dễ trơn"]},{"title":"Amalfi Marina Grande","pros":["Nhiều c\xe2u lạc bộ để chọn","Dễ d\xe0ng nối ph\xe0 v\xe0 xe bu\xfdt"],"cons":["Gi\xe1 cao","Phải đặt trước m\xf9a cao điểm"]}],"itemList":[{"name":"Fornillo","note":"B\xe3i y\xeau th\xedch của d\xe2n địa phương với ghế 15 € v\xe0 hai khu miễn ph\xed."},{"name":"Spiaggia Grande","note":"B\xe3i ch\xednh c\xf3 tầm nh\xecn biểu tượng, c\xe2u lạc bộ đắt đỏ v\xe0 dải c\xf4ng cộng giữa b\xe3i."},{"name":"Arienzo","note":"B\xe3i như s\xe2n thượng nắng đẹp, xuống 300 bậc hoặc đi thuyền taxi của c\xe2u lạc bộ."},{"name":"Laurito","note":"Vịnh nhỏ do thuyền shuttle của Da Adolfo v\xe0 Villa Tre Ville phục vụ."},{"name":"Gavitella","note":"Bục tắm Praiano l\xfd tưởng cho aperitivo ngắm ho\xe0ng h\xf4n (chỉ d\xe0nh cho c\xe2u lạc bộ)."},{"name":"Marina di Praia","note":"B\xe3i l\xe0ng ch\xe0i với một đoạn miễn ph\xed nhỏ cạnh c\xe1c lido."},{"name":"Fiordo di Furore","note":"Vịnh cầu kịch t\xednh v\xe0o cửa miễn ph\xed nhưng diện t\xedch hạn chế."},{"name":"Regina Giovanna Bath","note":"Hồ bơi b\xed mật ở Sorrento — miễn ph\xed nhưng nhớ mang đồ d\xf9ng."},{"name":"Amalfi Marina Grande","note":"D\xe3y lido cổ điển của Amalfi đối diện nh\xe0 thờ Duomo."}],"faqsTitle":"C\xe2u hỏi thường gặp về b\xe3i biển Positano","faqs":[{"q":"Ở Positano c\xf3 khu b\xe3i biển miễn ph\xed n\xe0o?","a":["Fornillo c\xf3 đoạn miễn ph\xed lớn nhất c\xf9ng một khu y\xean tĩnh ẩn ph\xeda sau c\xe1c c\xe2u lạc bộ. Spiaggia Grande cũng giữ một dải c\xf4ng cộng giữa c\xe1c boong trả ph\xed, trong khi Laurito chỉ c\xf3 một khoảnh nhỏ cạnh cầu t\xe0u của Da Adolfo."]},{"q":"L\xe0m sao quay lại Hostel Brikette sau một ng\xe0y tắm biển?","a":["Từ Fornillo, men theo lối ven biển tới Spiaggia Grande rồi bắt xe bu\xfdt cam nội bộ l\xean Chiesa Nuova. Xe dừng c\xe1ch hostel v\xe0i bước ch\xe2n v\xe0 gi\xfap bạn khỏi phải leo dốc."]},{"q":"C\xf3 cần đặt trước c\xe2u lạc bộ b\xe3i biển kh\xf4ng?","a":["H\xe3y đặt trước Arienzo, Da Adolfo hoặc Villa Tre Ville ở Laurito v\xe0 h\xe0ng ghế đầu của Spiaggia Grande — cuối tuần chỗ hết rất nhanh. C\xe1c c\xe2u lạc bộ nhỏ ở Fornillo thường nhận kh\xe1ch tới sớm trong ng\xe0y."]}],"gallery":[{"alt":"To\xe0n cảnh Spiaggia Grande từ tr\xean cao","caption":"Spiaggia Grande"},{"alt":"Lối đi xuống b\xe3i Fornillo","caption":"Fornillo"}]}')}}]);