"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[621754],{621754:n=>{n.exports=JSON.parse('{"deals":{"loyaltyCta":{"heading":"Ưu đ\xe3i th\xeam cho kh\xe1ch quay lại","body":"Tham gia danh s\xe1ch đặt ph\xf2ng trực tiếp của ch\xfang t\xf4i để nhận ưu đ\xe3i mở b\xe1n sớm, mẹo du lịch theo m\xf9a v\xe0 m\xe3 giảm th\xeam 5% cho lần đặt tiếp theo tr\xean website.","button":"Đăng k\xfd nhận ưu đ\xe3i"}},"about":{"mission":{"heading":"Sứ mệnh của ch\xfang t\xf4i","paragraphs":["Từ năm 1995, Hostel Brikette đ\xe3 gi\xfap du kh\xe1ch trải nghiệm Positano với ng\xe2n s\xe1ch hợp l\xfd m\xe0 kh\xf4ng bỏ lỡ tầm nh\xecn to\xe0n cảnh bờ biển Amalfi.","Ch\xfang t\xf4i đặt sự minh bạch l\xean h\xe0ng đầu: bảng gi\xe1 r\xf5 r\xe0ng, kh\xf4ng phụ ph\xed ẩn, ch\xednh s\xe1ch dễ hiểu v\xe0 đội ngũ tại chỗ lu\xf4n sẵn s\xe0ng hỗ trợ khi bạn cần thay đổi hoặc c\xf3 y\xeau cầu đặc biệt.","Đội ngũ địa phương cung cấp kế hoạch di chuyển, mẹo mang h\xe0nh l\xfd 23 kg / 50 lb l\xean xuống cầu thang v\xe0 gợi \xfd hoạt động để bạn tận hưởng trọn vẹn từng ph\xfat tr\xean bờ biển."]},"values":{"heading":"Our values","items":[{"title":"Bản dịch đang được chuẩn bị.","body":"Bản dịch đang được chuẩn bị."},{"title":"Bản dịch đang được chuẩn bị.","body":"Bản dịch đang được chuẩn bị."},{"title":"Warm hospitality","body":"Bản dịch đang được chuẩn bị."}]}},"careers":{"hiringCta":{"heading":"Bản dịch đang được chuẩn bị.","body":"Bản dịch đang được chuẩn bị.","button":"Apply now"}},"experiences":{"meetTheHosts":{"heading":"Gặp gỡ đội ngũ tr\xean s\xe2n thượng","intro":"Họ l\xe0 những người giữ cho s\xe2n thượng lu\xf4n s\xf4i động, tổ chức sự kiện ngắm ho\xe0ng h\xf4n, dẫn dắt c\xe1c chuyến đi bộ đường d\xe0i v\xe0 giải đ\xe1p c\xe2u hỏi của bạn ngay cả trước khi bạn đến Positano.","hosts":[{"name":"Chiara","role":"Phụ tr\xe1ch trải nghiệm","bio":"Điều phối quầy bar ho\xe0ng h\xf4n, giới thiệu nh\xe0 sản xuất địa phương v\xe0 đảm bảo mỗi buổi nếm thử đều c\xf3 m\xf3n hợp khẩu vị mọi người. Nếu bạn đang kỷ niệm hay muốn tổ chức sự kiện ri\xeang, h\xe3y gặp c\xf4 ấy tr\xean s\xe2n thượng từ 18:00.","imageAlt":"Chiara phục vụ spritz tại quầy bar s\xe2n thượng.","availability":"Gặp Chiara tại quầy bar hầu hết c\xe1c buổi tối hoặc li\xean hệ qua dịch vụ concierge kỹ thuật số của ch\xfang t\xf4i."},{"name":"Luca","role":"Hướng dẫn vi\xean địa phương","bio":"Sinh ra ở Praiano, anh ấy thuộc mọi cung đường tr\xean bờ biển Amalfi. Luca gi\xfap bạn chọn giữa đường m\xf2n Sentiero degli Dei (Path of the Gods), chuyến thuyền b\xecnh minh hay h\xe0nh tr\xecnh trong ng\xe0y tới Ravello — với kế hoạch di chuyển đ\xe3 chuẩn bị sẵn.","imageAlt":"Luca chỉ tay về ph\xeda đường ch\xe2n trời từ một điểm ngắm cảnh tr\xean bờ biển Amalfi.","availability":"Gặp anh ấy tại lễ t\xe2n v\xe0o s\xe1ng sớm, hoặc đặt chỗ tham gia c\xe1c chuyến đi bộ th\xf4ng qua concierge."},{"name":"Mariana","role":"Concierge kỹ thuật số","bio":"Phản hồi qua WhatsApp v\xe0 Messenger với th\xf4ng b\xe1o ph\xe0, gợi \xfd nh\xe0 h\xe0ng v\xe0 lời nhắc s\xe1t giờ. Nếu thời tiết thay đổi, c\xf4 ấy gửi những phương \xe1n thay thế nhẹ nh\xe0ng, kh\xf4ng \xe1p lực.","imageAlt":"M\xe0n h\xecnh điện thoại hiển thị cuộc tr\xf2 chuyện với Mariana, concierge kỹ thuật số.","availability":"Nhắn tin bất cứ l\xfac n\xe0o — c\xf4 ấy kết nối theo thời gian thực với đội ngũ lễ t\xe2n."}]}}}')}}]);