"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[627230],{627230:e=>{e.exports=JSON.parse('{"seo":{"title":"Tramonti & Amalfi Coast Wineries","description":"Wineries to visit, transport notes, and tasting etiquette."},"linkLabel":"Tramonti & Amalfi Coast Wineries","intro":["Discover Tramonti\'s hillside wineries for DOC tastings away from the coast crowds."],"sections":[{"id":"why","title":"Why Tramonti","body":["Centuries-old pergola vineyards grow on volcanic soil, producing Costa d\'Amalfi DOC reds and whites.","Family-run estates like Tenuta San Francesco and Reale welcome small groups for intimate tastings."]},{"id":"booking","title":"Booking tastings","body":["Reserve 48 hours ahead via email or WhatsApp; tastings with light bites cost €25–€35 per person.","Ask if transport is included—some wineries organise round-trip shuttles from Amalfi or Maiori."]},{"id":"getting","title":"Getting there","body":["Drive or hire a driver for the 18 km (11 mi) climb from Positano; roads are narrow but paved.","Public option: bus to Amalfi, then a local Tramonti bus. Plan buffers on the return as evening services are limited."]}],"tips":["Pack a reusable bottle—many wineries offer spring water to cleanse your palate between pours.","Ship wine via courier rather than carrying bottles if you are flying with hand luggage."],"faqs":[{"q":"How long is a tasting?","a":["Most visits last 90 minutes including vineyard tour, cellar walkthrough, and three to four wines."]},{"q":"Can I visit without a car?","a":["Yes, but arrange transfers in advance. Shared taxis split between four travellers keep costs reasonable."]}]}')}}]);