"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[644719],{644719:a=>{a.exports=JSON.parse('{"meta":{"title":"Positano do Ravello autobusem | Hostel Brikette","description":"Instrukcja krok po kroku z Positano do Ravello przez Amalfi na autobusach SITA: wskaz\xf3wki biletowe, rozkład, szczeg\xf3ły przystanku Chiesa Nuova i podpowiedzi dotyczące przesiadki."},"hero":{"eyebrow":"Przewodnik podr\xf3żny Hostel Brikette","title":"Positano do Ravello – przez Amalfi autobusem","description":"Podr\xf3żuj z Positano do Ravello z prostą przesiadką w Amalfi. Ten przewodnik pokazuje, gdzie kupić bilety SITA, jak czytać rozkład jazdy i czego oczekiwać podczas przesiadki w Amalfi — oraz jak dojść z Hostel Brikette na przystanek w Positano."},"cta":{"eyebrow":"Wskaz\xf3wka","beforeLink":"Potrzebujesz wszystkich opcji transportu? Otw\xf3rz","linkLabel":"przegląd Jak do nas dotrzeć","afterLink":"aby sprawdzić promy, taks\xf3wki i trasy łączone."},"journey":{"title":"Przegląd podr\xf3ży","body":"Zaplanuj około 1,5–2,5 godziny w zależności od przesiadek. Najpierw wsiądź w Positano (Chiesa Nuova) do autobusu SITA do Amalfi, a następnie przesiądź się na autobus SITA do Ravello."},"sita":{"title":"Autobusy SITA i bilety","items":["Autobusy na Wybrzeżu Amalfitańskim obsługuje SITA (SITA Sud Srl).","Kup bilety w kioskach lub sklepach tytoniowych (tabacchi) przed wejściem.","Miej bilet pod ręką; kierowcy kasują go, stemplując lub odrywając fragment przy wejściu.","W sezonie spodziewaj się tłum\xf3w; przyjdź kilka minut wcześniej, by ustawić się w kolejce."]},"timetable":{"title":"Rozkłady jazdy","beforeLink":"Najpierw pojedziesz do Amalfi i tam przesiądziesz się do Ravello. Sprawdź aktualne rozkłady na","linkLabel":"Positano.com (przegląd rozkładu SITA)","afterLink":".","linkHref":"https://www.positano.com/en/bus-schedule","readingHeading":"Jak czytać rozkład","tips":["Wiersze w pierwszej kolumnie wypisują każdy przystanek na trasie.","Kolumny odpowiadają zaplanowanym kursom oznaczonym literami.","Podr\xf3żujesz od poniedziałku do soboty? Korzystaj z kolumn „F” i „G”.","Podr\xf3żujesz w niedzielę lub święto? Sprawdź kolumny „G” i „H”."]},"positanoStop":{"title":"Przystanek Positano (Chiesa Nuova)","items":["Najbliższy przystanek SITA to Positano (Chiesa Nuova) przy Via G. Marconi.","Znajduje się około 140 metr\xf3w (490 st\xf3p) od wejścia do hostelu.","Na autobus poczekasz przed kawiarnią/barem „Bar Internazionale”."],"caption":"Przyjdź kilka minut wcześniej, aby mieć pewność miejsca; w lecie autobusy szybko się zapełniają.","alt":"Przystanek SITA Positano Chiesa Nuova przed Bar Internazionale"},"walk":{"title":"Spacer z hostelu na przystanek","items":["Wyjdź z hostelu, zejdź schodami do gł\xf3wnej drogi i skręć w lewo.","Idź dalej Via G. Marconi przez około 100 metr\xf3w (330 st\xf3p).","Bar Internazionale będzie po lewej stronie; przystanek SITA znajduje się tuż przed nim."]},"buyTickets":{"title":"Zakup bilet\xf3w autobusowych","items":["Kup bilety w Bar Internazionale lub pobliskim tabacchi.","Potrzebujesz bilet\xf3w na odcinki: Positano → Amalfi oraz Amalfi → Ravello.","W zależności od dostępnej taryfy możesz otrzymać jeden lub dwa bilety."]},"toAmalfi":{"title":"Podr\xf3ż autobusem do Amalfi","items":["Przejdź na drugą stronę ulicy od Bar Internazionale na przystanek w kierunku Amalfi.","Ustaw się w kolejce i wsiądź do autobusu z napisem Amalfi; skasuj bilet.","Wysiądź na dworcu autobusowym w Amalfi, aby złapać przesiadkę do Ravello."]},"change":{"title":"Przesiadka w Amalfi","items":["W oczekiwaniu na kolejny autobus możesz kr\xf3tko zwiedzić Amalfi.","Wr\xf3ć w okolice dworca autobusowego, aby wsiąść do autobusu do Ravello (linie 5110/5120).","Skasuj drugi bilet przy wejściu."]},"arrive":{"title":"Przyjazd do Ravello","items":["Ravello leży powyżej Amalfi; wiele atrakcji skupia się wok\xf3ł Piazza Duomo.","Kieruj się za lokalnymi znakami do zakwaterowania lub gł\xf3wnego placu.","Jeśli docierasz p\xf3źno, rozważ taks\xf3wkę z Amalfi, gdy połączeń jest niewiele."]}}')}}]);