"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[655514],{655514:i=>{i.exports=JSON.parse('{"seo":{"title":"Нестандартные деревни Амальфи","description":"Менее известные городки, как туда добраться и что их отличает."},"linkLabel":"Нестандартные деревни на побережье","intro":["Пять спокойных деревушек Амальфитанского побережья, куда стоит заглянуть, и как до них добраться."],"tocTitle":"На этой странице","sections":[{"id":"cetara","title":"Cetara","body":["Традиции анчоусов и тихий пляж с рабочими рыбацкими лодками.","Добраться можно на автобусе SITA или сезонном пароме из Амальфи или Салерно."]},{"id":"minori","title":"Minori/Maiori","body":["Ровные набережные и любимые кондитерские для размеренного темпа.","Приезжайте автобусом или паромом и совместите с прогулкой по лимонной тропе между близнецами."]},{"id":"atrani","title":"Atrani","body":["Уютная площадь в паре минут восточнее Амальфи, узкие улочки и небольшой пляж.","Дойдите пешком из Амальфи за пару минут или проедьте одну остановку на местном автобусе."]},{"id":"vietri","title":"Vietri sul Mare","body":["Столица керамики у Салерно, мастерские выстроились вдоль улиц.","Сначала доезжайте поездом до Салерно, затем короткая поездка на автобусе или пароме."]},{"id":"furore","title":"Furore","body":["Эффектный фьорд с ограниченным пространством на пляже — приезжайте рано для спокойных снимков.","Используйте автобусы SITA в сторону Амальфи; летом некоторые паромы делают здесь остановку."]}],"faqs":[{"q":"Можно посетить несколько деревень за день?","a":["Да — соедините Cetara, Vietri и Minori автобусами или паромами. Начинайте рано и следите за расписанием обратных рейсов."]},{"q":"Принимают ли карты в маленьких деревнях?","a":["Берите наличные для кафе и киосков: многие ставят минимум €10 или работают только за наличные."]}]}')}}]);