"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[656098],{656098:t=>{t.exports=JSON.parse('{"seo":{"title":"عطلة نهاية أسبوع 48 ساعة في بوسيتانو","description":"خطة عملية ليومين: الشواطئ، الإطلالات، خيار درب الآلهة، وأماكن الأكل الاقتصادي."},"linkLabel":"عطلة نهاية أسبوع 48 ساعة في بوسيتانو","intro":["عطلة نهاية أسبوع من 48 ساعة في بوزيتانو ممكنة إذا سافرت بخفة، رتبت الوصول مبكرًا، وطاردت الإطلالات خارج أوقات الحر الشديدة."],"sections":[{"id":"day1-morning","title":"اليوم 1 — الوصول والصباح","body":["وصل بأول حافلة SITA أو عبّارة، اترك الحقائب في مكان الإقامة أو التخزين، وتناول إسبرسو مع سفولياتيلا في فيا دي موليني.","انزل إلى شاطئ فورنيلو للسباحة قبل الظهر حين تكون النوادي هادئة والأماكن العامة متاحة."]},{"id":"day1-afternoon","title":"اليوم 1 — بعد الظهر والغروب","body":["اركب الحافلة المحلية إلى نوتشيليه لتجربة قصيرة من درب الآلهة؛ عد في الوقت المناسب لغروب الشمس في فيا كريستوفورو كولومبو.","احجز عشاءً على شرفة أو التقط طعامًا من الديلي لنزهة على شرفة الهوستل."]},{"id":"day2-morning","title":"اليوم 2 — صباح حسب رغبتك","body":["الخيار أ: عبّارة إلى أمالفي ثم انتقال سريع إلى حدائق فيلا تشيمبروني في رافيللو؛ الخيار ب: جولة قارب مشتركة لإطلالات الساحل.","احزم فطورًا من المخبز لتبدأ باكرًا من دون انتظار خدمة المقاهي."]},{"id":"day2-afternoon","title":"اليوم 2 — الختام والمغادرة","body":["خصص وقتًا للتسوق من صانعي الصنادل أو مشاغل السيراميك قبل الغداء.","استلم الحقائب قبل 90 دقيقة من المغادرة لتواجه السلالم والطوابير عند المرسى أو محطة الحافلات."]}],"tips":["احمل حقيبة نهارية دون 10 كجم لتتمكن من صعود السلالم بسرعة.","خزّن جداول العبّارات والحافلات دون اتصال؛ تغييرات عطلة نهاية الأسبوع شائعة."],"faqs":[{"q":"هل تكفي ليلة واحدة؟","a":["نعم إذا ركزت على المعالم وبقيت قرب المركز، لكن ليلتان تمنحان وتيرة أهدأ وخطة بديلة للطقس."]},{"q":"أين أشاهد الغروب؟","a":["فيا كريستوفورو كولومبو وتراس الهوستل خيارات سهلة؛ حانات الشاطئ جيدة أيضًا لكن أُعد لدفع أسعار أعلى."]}]}')}}]);