"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[660982],{660982:e=>{e.exports=JSON.parse('{"seo":{"title":"طعام رخيص في بوسيتانو","description":"مطاعم ومخابز ووجبات خفيفة بأسعار مناسبة مع نصائح للطلب وتجنب الرسوم الإضافية."},"linkLabel":"طعام رخيص في بوسيتانو","heroAlt":"طعام سريع وبأسعار مناسبة في بوسيتانو","intro":["تناول طعامًا جيدًا في بوسيتانو من دون إنفاق كبير عبر المزج بين الوجبات الجاهزة، ونزهات الديلي، والمخابز المحلية."],"sections":[{"id":"grab-and-go","title":"أساسيات سريعة الالتقاط","body":["التقط بيتزا بالتقطيع أو بانيني أو أرنشيني من المقاهي على طول فيا دي موليني قبل الظهيرة لتفادي الطوابير.","اطلب الإسبريسو واقفًا عند البار (\xabal banco\xbb) لتحصل على السعر المحلي نحو 1.20 يورو بدلًا من خدمة الطاولات."]},{"id":"casual-dining","title":"مطاعم جلوس غير رسمية","body":["ابحث عن التراتوريا فوق مستوى الشاطئ حيث يكون الكوبيرتو منخفضًا ويُقدَّم نبيذ البيت بالقرعة.","شارك المقبلات، وتجاوز الحلوى، واطلب ماء الحنفية (\xabacqua del rubinetto\xbb) لتبقي الفاتورة تحت السيطرة."]},{"id":"picnics","title":"نزهات ذات إطلالة","body":["اشترِ الأجبان والفوكاتشيا والفواكه من المتاجر الصغيرة ثم نظّم نزهة على تراس الهوستل أو في القسم العام من فورنيلو.","احمل أدوات مائدة ومناديل قابلة لإعادة الاستخدام لتتجنب النفايات المخصصة للاستخدام الواحد والمشتريات الإضافية."]},{"id":"sweet-breaks","title":"حلويات واستراحات منعشة","body":["جرّب غرانيتا الليمون من العربات في الشارع مقابل نحو 3 يورو بدلًا من كوكتيلات البار الأعلى سعرًا.","اقسم شريحة دليزيّا الليمون أو تيراميسو من المخبز لتحصل على حلوى معقولة التكلفة."]}],"tips":["اسأل عن \xabmenu del giorno\xbb أو عروض الغداء — كثير من الأماكن تجمع المعكرونة والماء والقهوة بأقل من 18 يورو.","احمل حاوية قابلة للطي لحفظ البواقي والاستمتاع بها لاحقًا في الهوستل."],"recommendationsTitle":"أين تتناول الطعام","recommendations":[{"name":"مخبز كولّينا","note":"بانيني، بيتزا بالتقطيع، سلطات، ومخبوزات جاهزة لوجبات سريعة."},{"name":"أقسام الديلي المحلية","note":"اطلب أطباق الديلي، الفوكاتشيا، ومستحضرات النزهات بعيدًا عن أسعار الواجهة البحرية."},{"name":"المتاجر الصغيرة وأكشاك الفاكهة","note":"زوّد نفسك بالفواكه، وقوارير الماء الكبيرة، والوجبات الخفيفة لنزهات التراس."}],"gallery":{"title":"معرض الصور","items":[{"alt":"بانيني من ديلي محلي","caption":"بانيني وديلي"},{"alt":"نزهة مع إطلالة على بوسيتانو","caption":"مناظر للنزهات"}]},"faqs":[{"q":"هل يمكنني إعادة تعبئة الماء مجانًا؟","a":["نعم — املأ القوارير في الهوستل أو في النوافير العامة مثل التي قرب كيييزا نوفا؛ احرص على حمل 750 مل على الأقل."]},{"q":"هل توجد خيارات نباتية بأسعار معقولة؟","a":["بالتأكيد — اطلب بانيني كابريزي، شرائح باذنجان بارميجيانا، أو معكرونة بصلصة الطماطم من التراتوريا البسيطة."]}],"fallbackIntro":["بوسيتانو مليئة بالخيارات الاقتصادية إذا عرفت أين تبحث."],"fallbackRecommendations":[{"name":"مخبز كولّينا","note":"بانيني، بيتزا بالتقطيع، سلطات، ومخبوزات جاهزة لوجبات سريعة."},{"name":"أقسام الديلي المحلية","note":"اطلب أطباق الديلي، الفوكاتشيا، ومستحضرات النزهات بعيدًا عن أسعار الواجهة البحرية."},{"name":"المتاجر الصغيرة وأكشاك الفاكهة","note":"زوّد نفسك بالفواكه، وقوارير الماء الكبيرة، والوجبات الخفيفة لنزهات التراس."}],"fallbackGalleryItems":[{"alt":"بانيني من ديلي محلي","caption":"بانيني وديلي"},{"alt":"نزهة مع إطلالة على بوسيتانو","caption":"مناظر للنزهات"}],"fallbackFaqs":[{"q":"هل تقبل المتاجر البطاقات؟","a":["معظمها يفعل، لكن حمل سيولة صغيرة يساعد في الأكشاك أو لتجاوز الحد الأدنى للبطاقات."]},{"q":"هل تُفرض رسوم تعبئة للوجبات الجاهزة؟","a":["البعض يضيف رسوم تغليف بسيطة؛ أحضر حاوية قابلة لإعادة الاستخدام لتوفر المال وتقلل النفايات."]}]}')}}]);