"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[661138],{661138:o=>{o.exports=JSON.parse('{"seo":{"title":"48-घंटे का Positano वीकेंड","description":"दो दिनों में Positano का सर्वश्रेष्ठ देखने, खाने और शाम बिताने का कॉम्पैक्ट प्लान।"},"linkLabel":"48 घंटे का Positano वीकेंड","intro":["48 घंटे का Positano वीकेंड संभव है अगर आप हल्का ट्रैवल करें, आगमन जल्दी रखें, और पीक गर्मी के बाहर व्यूज़ देखें।"],"sections":[{"id":"day1-morning","title":"दिन 1 — आगमन और सुबह","body":["पहली SITA बस या फेरी से पहुँचें, सामान स्टे या स्टोरेज में छोड़ें, और Via dei Mulini पर espresso व sfogliatella लें।","दोपहर से पहले Fornillo Beach तक नीचे जाएँ ताकि लिडो शांत हों और पब्लिक स्पॉट खाली मिलें।"]},{"id":"day1-afternoon","title":"दिन 1 — दोपहर और सूर्यास्त","body":["लोकल बस से Nocelle जाएँ और Path of the Gods का छोटा सा टेस्टर करें; Via Cristoforo Colombo पर सूर्यास्त के लिए समय पर लौटें।","टेरेस पर डिनर बुक करें या हॉस्टल बालकनी पर पिकनिक के लिए डेली फूड ले लें।"]},{"id":"day2-morning","title":"दिन 2 — सुबह","body":["विकल्प A: Amalfi के लिए फेरी लेकर Ravello के Villa Cimbrone गार्डन्स तक त्वरित हॉप; विकल्प B: कोस्टलाइन व्यूज़ के लिए साझा बोट टूर जॉइन करें।","बेकरी से नाश्ता पैक करें ताकि कैफ़े सेवा का इंतजार किए बिना जल्दी शुरू कर सकें।"]},{"id":"day2-afternoon","title":"दिन 2 — समापन और प्रस्थान","body":["लंच से पहले कारीगर सैंडल मेकर्स या सिरेमिक स्टूडियो में शॉपिंग कर लें।","डिपार्चर से 90 मिनट पहले सामान लें ताकि सीढ़ियों और डॉक/बस स्टॉप की कतारों के लिए समय रहे।"]}],"tips":["10 kg (22 lb) से कम का डेपैक रखें ताकि सीढ़ियों में जल्दी चल सकें।","फेरी और बस टाइमटेबल ऑफलाइन सेव करें; वीकेंड सर्विस में बदलाव आम हैं।"],"faqs":[{"q":"क्या एक रात का ठहराव पर्याप्त है?","a":["हाँ, अगर आप हाइलाइट्स पर फोकस करें और सेंटर में रहें, लेकिन दो रातें ज्यादा शांत गति और मौसम का बैकअप देती हैं।"]},{"q":"सूर्यास्त कहाँ देखें?","a":["Via Cristoforo Colombo और हॉस्टल टेरेस आसान विकल्प हैं; बीच बार भी चलेंगे लेकिन कीमतें ज्यादा रहेंगी।"]}]}')}}]);