"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[662286],{662286:e=>{e.exports=JSON.parse('{"seo":{"title":"مصانع النبيذ في ترامونتي وساحل أمالفي","description":"كيفية زيارة مصانع النبيذ الجبلية، نصائح الحجز، وطرق النقل الآمنة للعودة."},"linkLabel":"مصانع النبيذ في ترامونتي وساحل أمالفي","intro":["اكتشف مصانع النبيذ على تلال ترامونتي لتذوق نبيذ DOC بعيدًا عن زحام الساحل."],"sections":[{"id":"why","title":"لماذا ترامونتي","body":["تتناثر كروم برغولا العريقة على تربة بركانية، ما ينتج نبيذ Costa d\'Amalfi DOC الأحمر والأبيض.","ترحب العقارات العائلية مثل Tenuta San Francesco وReale بمجموعات صغيرة لتجارب تذوق حميمة."]},{"id":"booking","title":"حجز جلسات التذوق","body":["احجز قبل 48 ساعة عبر البريد الإلكتروني أو واتساب؛ تكلف جلسات التذوق مع لقيمات خفيفة 25–35 يورو للشخص.","اسأل إن كان النقل مشمولًا—بعض مصانع النبيذ تنظم رحلات ذهابًا وإيابًا من أمالفي أو مايوري."]},{"id":"getting","title":"كيفية الوصول","body":["قد بنفسك أو استعن بسائق للرحلة الصاعدة لمسافة 18 كم (11 ميلًا) من بوزيتانو؛ الطرق ضيقة لكنها معبّدة.","الخيار العام: حافلة إلى أمالفي، ثم حافلة محلية إلى ترامونتي. خطط لهوامش في العودة لأن الخدمات المسائية محدودة."]}],"tips":["احمل زجاجة قابلة لإعادة الاستخدام—تقدم العديد من مصانع النبيذ مياه ينابيع لتنظيف الحنك بين الرشفات.","اشحن زجاجات النبيذ عبر شركة نقل بدلًا من حملها إذا كنت تسافر بأمتعة يد فقط."],"faqs":[{"q":"ما مدة جلسة التذوق؟","a":["تستغرق معظم الزيارات 90 دقيقة تشمل جولة في الكروم والقبو وتذوق ثلاثة إلى أربعة أنواع."]},{"q":"هل يمكنني الزيارة من دون سيارة؟","a":["نعم، لكن رتب وسائل النقل مسبقًا. مشاركة سيارة أجرة بين أربعة مسافرين تبقي التكلفة معقولة."]}]}')}}]);