"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[667947],{667947:n=>{n.exports=JSON.parse('{"intro":["Spiaggia Grande l\xe0 b\xe3i biển bưu thiếp của Positano: một h\xecnh lưỡi liềm rải sỏi rộng với đường ch\xe2n trời tr\xean đỉnh v\xe1ch đ\xe1 xếp chồng l\xean nhau sau những chiếc \xf4 v\xe0 ph\xe0 đến v\xe0 đi cả ng\xe0y.","Bơi ra ngo\xe0i v\xe0 quay trở lại thị trấn l\xe0 điều kh\xf4ng thể n\xe0o qu\xean, nhưng trải nghiệm tr\xean bờ phụ thuộc v\xe0o vị tr\xed bạn ngồi - từ khu vực tự do trung t\xe2m đến những chiếc s\xe0n cực kỳ cao cấp được chạm khắc v\xe0o đ\xe1."],"toc":[{"href":"#layout","label":"Bố tr\xed & c\xe1c khu vực trọng điểm"},{"href":"#free-zone","label":"Phần b\xe3i biển miễn ph\xed"},{"href":"#residents","label":"Dải d\xe2n cư"},{"href":"#lincanto","label":"C\xe2u lạc bộ b\xe3i biển L\'Incanto"},{"href":"#la-scogliera","label":"S\xe0n La Scogliera"},{"href":"#fornillo-link","label":"Đường đi đến Fornillo"}],"sections":[{"id":"layout","title":"Bố tr\xed v\xe0 c\xe1c khu vực trọng điểm","body":["Hướng ra biển, bến ph\xe0 c\xf4ng cộng v\xe0 cầu phao taxi nước nằm ở b\xean phải, dải đất tự do ch\xednh trải d\xe0i khắp trung t\xe2m v\xe0 c\xe1c c\xe2u lạc bộ trả ph\xed xếp dọc c\xe1c tảng đ\xe1 b\xean tr\xe1i.","Bản đồ được đ\xe1nh số tr\xean b\xe3i biển thường gắn nh\xe3n số 1 l\xe0 khu vực c\xf4ng cộng, số 2 l\xe0 dải đất của cư d\xe2n Positano v\xe0 số 3/#4 l\xe0 hai nh\xe0 khai th\xe1c phải trả ph\xed: L\'Incanto tr\xean c\xe1t v\xe0 La Scogliera tr\xean boong n\xe2ng."]},{"id":"free-zone","title":"Khu vực b\xe3i biển miễn ph\xed (#1)","body":["Khu vực miễn ph\xed rộng nhất nằm giữa hai c\xe2u lạc bộ tư nh\xe2n. H\xe3y đến trước 10:00 để c\xf3 chỗ ngồi ở h\xe0ng ghế đầu v\xe0 mang theo một tấm thảm hoặc khăn d\xe0y - những vi\xean sỏi ấm l\xean nhanh ch\xf3ng v\xe0 g\xe2y kh\xf3 chịu nếu kh\xf4ng c\xf3 lớp đệm.","Ph\xf2ng tắm v\xe0 nh\xe0 vệ sinh c\xf4ng cộng được bố tr\xed cạnh lối v\xe0o bến ph\xe0. Đồ ăn nhẹ đến từ c\xe1c ki-ốt ph\xeda sau b\xe3i biển hoặc quầy b\xe1n đồ ăn mang đi tại qu\xe1n Blu Bar của L\'Incanto (thanh to\xe1n ri\xeang)."]},{"id":"residents","title":"Dải d\xe2n cư (#2)","body":["Một khu vực c\xf3 h\xe0ng r\xe0o b\xean cạnh khu vực c\xf4ng cộng được d\xe0nh ri\xeang cho cư d\xe2n Positano. Mong nh\xe2n vi\xean cứu hộ kiểm tra d\xe2y đeo cổ tay; du kh\xe1ch n\xean t\xf4n trọng biển b\xe1o v\xe0 b\xe1m v\xe0o dải ghế miễn ph\xed hoặc ghế d\xe0i của c\xe2u lạc bộ.","Nếu khu vực c\xf4ng cộng đ\xf4ng đ\xfac, h\xe3y tiếp tục đi dọc theo lối đi dạo v\xe0 đi theo lối đi bộ đến Fornillo để c\xf3 th\xeam kh\xf4ng gian — bạn chỉ mất năm ph\xfat tản bộ khi đến tr\xean đường."]},{"id":"lincanto","title":"C\xe2u lạc bộ b\xe3i biển L\'Incanto (#3)","body":["L\'Incanto bao phủ phần lớn b\xe3i c\xe1t với những h\xe0ng ghế d\xe0i m\xe0u cam, v\xf2i sen, ph\xf2ng thay đồ v\xe0 dịch vụ bồi b\xe0n. Y\xeau cầu € 25– € 35 mỗi người cho một bộ ti\xeau chuẩn trong m\xf9a cao điểm, với việc n\xe2ng cấp h\xe0ng ghế đầu hoặc ch\xf2i c\xf3 chi ph\xed cao hơn.","Đặt trước qua WhatsApp hoặc email v\xe0o th\xe1ng 7–th\xe1ng 8; việc đến trước 09:30 thường được chấp nhận trong những th\xe1ng bận rộn. Blu Bar (nh\xe0 h\xe0ng b\xecnh d\xe2n của họ) hướng ra lối đi dạo v\xe0 thuận tiện cho việc thưởng thức pizza, salad v\xe0 rượu khai vị m\xe0 kh\xf4ng cần rời khỏi ghế d\xe0i của bạn."]},{"id":"la-scogliera","title":"Boong La Scogliera (#4)","body":["La Scogliera nằm tr\xean những tảng đ\xe1 ngo\xe0i c\xf9ng b\xean tr\xe1i với s\xe0n gỗ tếch, giường kiểu Bali v\xe0 thang biển dẫn thẳng xuống v\xf9ng nước s\xe2u. C\xe1c g\xf3i c\xf3 gi\xe1 khởi điểm khoảng €100 cho ghế nằm ở h\xe0ng ghế sau v\xe0 c\xf3 thể vượt qu\xe1 €400 cho ghế VIP to\xe0n cảnh c\xf3 bao gồm rượu prosecco.","Dịch vụ c\xf3 khăn trải b\xe0n m\xe0u trắng: nh\xe0 h\xe0ng ở tầng tr\xean nh\xecn ra bến cảng v\xe0 nh\xe2n vi\xean gọi taxi nước nếu bạn muốn đến Laurito hoặc Arienzo để ngắm ho\xe0ng h\xf4n. Quy định về trang phục chuyển sang trang phục lịch sự sau 18:00."]},{"id":"fornillo-link","title":"Đường đi bộ đến Fornillo","body":["Để chuyển đổi c\xe1c b\xe3i biển m\xe0 kh\xf4ng cần leo n\xfai, h\xe3y đi đến đầu xa của cảng nơi t\xe0u thuyền bốc dỡ h\xe0ng. Đi b\xean phải v\xe0 đi qua cổng v\xf2m c\xf3 biển b\xe1o để đến lối đi ven biển.","Đi theo lối đi dạo trong khoảng năm ph\xfat; n\xf3 len lỏi qua c\xe1c qu\xe1n c\xe0 ph\xea v\xe0 điểm ngắm cảnh trước khi đưa bạn đến lối v\xe0o ch\xednh của Fornillo. Gh\xe9p nối chuyến đi dạo với %LINK:fornilloBeachGuide|Fornillo beach guide% của ch\xfang t\xf4i để biết th\xf4ng tin chi tiết về c\xe2u lạc bộ ở ph\xeda b\xean kia."]}],"tipsTitle":"Mẹo nhanh","tips":["Đi bộ xuống Viale Pasitea từ nh\xe0 trọ trong 20–25 ph\xfat, dừng lại ở c\xe1c điểm quan s\xe1t ven đường để chụp ảnh trước khi gh\xe9 v\xe0o Piazza dei Mulini v\xe0 lối đi dạo Spiaggia Grande.","Th\xedch b\xe1nh xe hơn? Bắt xe bu\xfdt nội địa m\xe0u cam từ Chiesa Nuova đến c\xe1c điểm dừng Sponda hoặc Piazza dei Mulini; mua v\xe9 €1,50 tại Bar Internazionale trước khi l\xean m\xe1y bay.","Sau một ng\xe0y đi biển, h\xe3y bắt xe bu\xfdt quốc tế ngược từ bến ph\xe0 b\xean cạnh bến ph\xe0 để bỏ qua chặng leo dốc đến Hostel Brikette.","Lập kế hoạch ng\xe2n s\xe1ch d\xe0nh cho ph\xf2ng chờ của bạn với %LINK:positanoBeaches|Positano beaches overview% của ch\xfang t\xf4i — n\xf3 so s\xe1nh gi\xe1 cả v\xe0 c\xe1c khu vực miễn ph\xed tr\xean mọi vịnh nhỏ gần đ\xf3."],"warningsTitle":"Điều cần biết","warnings":["Spiaggia Grande ho\xe0n to\xe0n được l\xe1t đ\xe1 cuội: mang theo gi\xe0y hoặc d\xe9p đi nước để dễ v\xe0o nước hơn.","Ghế d\xe0i v\xe0 d\xf9 che nắng ở h\xe0ng ghế đầu sẽ hết h\xe0ng v\xe0o những ng\xe0y sắp tới trong th\xe1ng 8 - h\xe3y đặt trước nếu bạn cần đảm bảo c\xf3 b\xf3ng r\xe2m.","M\xe1y thẻ thỉnh thoảng ngoại tuyến; mang theo tiền mặt cho ki-ốt, c\xe2n ghế v\xe0 t\xe0i xế xe bu\xfdt nội địa."],"faqsTitle":"C\xe2u hỏi thường gặp về Spiaggia Grande","faqs":[{"q":"T\xf4i c\xf3 cần đặt trước L\'Incanto hoặc La Scogliera kh\xf4ng?","a":["Đối với L\'Incanto, việc đặt trước l\xe0 điều cần thiết v\xe0o cuối th\xe1ng 7 v\xe0 th\xe1ng 8, nhưng c\xe1c buổi s\xe1ng v\xe0o m\xf9a cao điểm c\xf3 chỗ trống trước 09:30. La Scogliera y\xeau cầu đặt trước tất cả c\xe1c bộ b\xe0i cao cấp - gửi email hoặc WhatsApp trước \xedt nhất một ng\xe0y để kh\xf3a giường."]},{"q":"Khu vực miễn ph\xed c\xf3 v\xf2i sen v\xe0 ph\xf2ng tắm kh\xf4ng?","a":["C\xf3. Ph\xf2ng tắm c\xf4ng cộng nằm gần bến ph\xe0 v\xe0 nh\xe0 vệ sinh th\xe0nh phố nằm ph\xeda sau lối đi dạo. Giữ tiền xu tiện dụng cho c\xe1c cửa quay; cơ sở c\xe2u lạc bộ tư nh\xe2n được d\xe0nh ri\xeang cho kh\xe1ch trả tiền."]},{"q":"T\xf4i c\xf3 thể để t\xfai ở đ\xe2u trước khi đi biển?","a":["Gửi h\xe0nh l\xfd tại %LINK:luggageStorage|Positano luggage storage% gần Piazza dei Mulini hoặc sử dụng ph\xf2ng chứa đồ của chỗ ở của bạn. T\xfai x\xe1ch kh\xf4ng được ph\xe9p mang l\xean boong của La Scogliera v\xe0 phải ngồi sau ghế d\xe0i tại L\'Incanto."]}]}')}}]);