"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[669189],{669189:e=>{e.exports=JSON.parse('{"labels":{"onThisPage":"في هذه الصفحة","before":"قبل أن تنطلق","steps":"المسار خطوة بخطوة","knees":"الركبتان وحمل الأمتعة","faqs":"الأسئلة الشائعة"},"folkloreAmalfi":{"intro":["حوريات البحر والقديسون والبحارة — قصص تلوّن ساحل أمالفي والأماكن التي تشير إليها اليوم."],"sections":[{"title":"حكايا الصيادين","body":["تفسّر أساطير القديسين الحماة وتقلبات البحر المفاجئة الطقوس والمهرجانات المحلية."]},{"title":"أساطير أسماء الأماكن","body":["كيف اكتسبت الأسماء والمعالم حكاياتها عبر القرون."]}]},"compareBasesPositanoSorrentoAmalfi":{"intro":[],"toc":[{"href":"#positano","label":"بوسيتانو"},{"href":"#sorrento","label":"سورينتو"},{"href":"#amalfi","label":"أمالفي"},{"href":"#who","label":"من يناسب كل قاعدة"}],"sections":[{"id":"positano","title":"بوسيتانو","body":["مناظر أيقونية والكثير من الدرج؛ مثالية لمن يريد الاستيقاظ وسط المشهد البانورامي.","تتطلب المواصلات تخطيطًا، خصوصًا مع العبّارات المبكرة والحافلات المتأخرة."]},{"id":"sorrento","title":"سورينتو","body":["أفضل الروابط وشتى أنواع الإقامة لمعظم الميزانيات.","المناظر أقل درامية من بوسيتانو، لكن العملية مرتفعة."]},{"id":"amalfi","title":"أمالفي","body":["مركز رئيسي للعبّارات والحافلات مع وصول سهل إلى رافيللو.","توقّع تنوعًا في الإقامات وخيارات مواصلات مسائية متوسطة."]},{"id":"who","title":"من يناسب كل قاعدة","body":["بوسيتانو لمن يبحث عن مشاهد فاخرة وحياة اجتماعية في الهوستل.","سورينتو للباحثين عن قيمة عالية وروابط نقل سريعة.","أمالفي لمن يريد توازنًا في الوصول على امتداد الساحل."]}],"faqs":[],"faqsTitle":"الأسئلة الشائعة"},"interrailItalyRailPassAmalfiCoast":{"toc":[{"href":"#routes","label":"المسارات الأساسية"},{"href":"#supplements","label":"الرسوم الإضافية والحجوزات"},{"href":"#last-mile","label":"آخر جزء إلى بوسيتانو"},{"href":"#faqs","label":"الأسئلة الشائعة"}],"sections":[{"id":"routes","title":"المسارات الأساسية","body":["روما → نابولي/ساليرنو عبر القطارات السريعة؛ قطارات محلية إلى سورينتو؛ وحافلات إلى بوسيتانو."]},{"id":"supplements","title":"الرسوم الإضافية والحجوزات","body":["قطارات فريتشاروسا/إيتالو تتطلب عادةً حجز مقاعد؛ القطارات الإقليمية لا تتطلب ذلك لكنها قد تكون مكتظة."]},{"id":"last-mile","title":"آخر جزء إلى بوسيتانو","body":["من نابولي: قطار سيركومفيسوفيانا إلى سورينتو ثم حافلة SITA. من ساليرنو: عبّارة أو حافلة في الموسم."]}],"faqsTitle":"الأسئلة الشائعة","faqs":[{"q":"هل تستحق التذكرة الشاملة قيمتها؟","a":["فقط إذا استخدمت عددًا كافيًا من الرحلات الطويلة؛ خلاف ذلك قد تكون التذاكر الفردية أرخص."]}]},"photographyGuidePositano":{"toc":[{"href":"#classics","label":"زوايا كلاسيكية"},{"href":"#crowds","label":"تجنب الازدحام"},{"href":"#etiquette","label":"آداب التصوير"}],"sections":[{"id":"classics","title":"زوايا كلاسيكية","body":["منحنيات فيا باسيتيّا العليا، ممر فورنيلو، ورصيف الميناء في الاتجاه العكسي — الأفضل عند الشروق أو الساعة الذهبية."]},{"id":"crowds","title":"تجنب الازدحام","body":["انطلق مبكرًا، أو انتظر بعد الغروب لتصوير ساعة الزرقة دون حشود الزوار اليوميين."]},{"id":"etiquette","title":"آداب التصوير","body":["شارك المساحة، افسح المجال في الدرجات الضيقة، ولا تحجب واجهات المتاجر."]}]},"simsAtms":{"intro":["شرائح eSIM مريحة وغالبًا أرخص؛ إذا احتجت شريحة فعلية أو صرافًا آليًا، خطط مسبقًا في موسم الذروة."],"faqs":[{"q":"هل يمكنني شراء شريحة محلية في بوسيتانو؟","a":["المتاجر محدودة؛ فكّر في شرائح eSIM أو الشراء في نابولي/سورينتو للحصول على أسعار أفضل."]},{"q":"هل التغطية موثوقة؟","a":["التغطية جيدة عمومًا داخل البلدة، مع مناطق أضعف على المسارات والمنحدرات."]},{"q":"هل تتوفر أجهزة صراف آلي؟","a":["توجد أجهزة صراف لكن الرسوم تختلف؛ فضّل الأجهزة التابعة للبنوك واحتفظ ببطاقة احتياطية."]}]},"ferrySchedules":{"intro":["العبّارات إلى بوسيتانو موسمية. في الصيف يربط عدة مشغلين نابولي وسورينتو وأمالفي وكابري ببوسيتانو. أكّد دائمًا عبر موقع المشغل، فالجداول تتغيّر حسب الطقس والطلب."],"tipsTitle":"نصائح ذكية للعبّارة","tips":["تحقق من تحديثات المغادرة مساء اليوم السابق، فسرعة الرياح أو اضطراب البحر قد تلغي الرحلات دون إنذار.","احجز التذاكر عبر الإنترنت في موسم الذروة لتفادي نفاد الرحلات عند أكشاك الميناء.","احمل بعض اليورو نقدًا إذا كنت تخطط لاستخدام خدمة الحمالين من الرصيف إلى أعلى البلدة.","احتفظ بجدول حافلات SITA كخطة بديلة عندما يكون البحر هائجًا ولا تعمل العبارات."],"sections":[{"id":"seasonality","title":"الموسمية","body":["تعمل العبّارات أساسًا من أبريل إلى أكتوبر. خدمات الشتاء محدودة أو متوقفة — استخدم الحافلات بدلاً منها."]},{"id":"tips","title":"نصائح سريعة","body":["موسم الذروة: أبريل–أكتوبر مع خدمات محدودة أو معدومة في الشتاء.","احضر مبكرًا قبل المغادرة للانضمام إلى الطابور والتحقق من التذاكر.","الطريق من الميناء إلى الهوستل صعودي؛ فكّر في الحمّالين أو الحافلة إلى \\"كيييزا نوفا\\".","للحصول على نصائح وصول إضافية، راجع مقال المساعدة لدينا \\"الوصول عبر العبّارة\\"."]}],"faqs":[{"q":"هل تعمل العبّارات إلى بوسيتانو طوال العام؟","a":["العبّارات موسمية (عادةً من أبريل إلى أكتوبر). في الشتاء تكون الحافلات أو سيارات الأجرة هي الخيارات الأساسية."]},{"q":"أين أتحقق من جداول العبّارات اللحظية؟","a":["استخدم مواقع المشغلين (مثل NLG وTravelmar وAlilauro) أو مجمّعات مثل DirectFerries خلال الصيف."]},{"q":"أي رصيف أستخدمه في بوسيتانو؟","a":["الرصيف الرئيسي في سبياجيا غراندي. تحقق من تذكرتك لمعرفة مسار الصعود الدقيق في الأيام المزدحمة."]},{"q":"متى يجب أن أصل قبل المغادرة؟","a":["احضر قبل 20–30 دقيقة؛ الطوابير شائعة في أشهر الذروة (يونيو–سبتمبر)."]},{"q":"هل صعود الطريق من الميناء إلى هوستل بريكيت صعب؟","a":["الصعود يحتوي على درجات كثيرة. فكّر في خدمة حمّالين أو الحافلة إلى كيييزا نوفا إذا كانت حقائبك ثقيلة."]}],"gallery":[{"alt":"منطقة رصيف بوسيتانو","caption":"رصيف سبياجيا غراندي"},{"alt":"عبّارة تقترب من أمالفي","caption":"عبّارات على طول الساحل"}],"faqsTitle":"أسئلة شائعة عن العبارات إلى بوسيتانو","galleryTitle":"لقطات مميزة للعبّارة"},"drivingAmalfi":{"intro":["قيادة طريق SS163 جميلة لكنها متطلبة. فكّر أولاً في العبّارات والحافلات؛ وإن قدت فخطط للممرات الضيقة، وقلة المواقف، والقيود الموسمية."]},"ecoFriendlyAmalfi":{"intro":["اخفض بصمتك البيئية أثناء الاستمتاع بالساحل. امشِ في مسارات الدرج، واختر العبّارات بدل السيارات في الموسم، واملأ المياه، واحمل مخلفاتك."],"toc":[{"href":"#getting-around","label":"الحركة والتنقل"},{"href":"#refills","label":"إعادة التعبئة والنفايات"},{"href":"#trails","label":"آداب المسارات"}],"sections":[{"id":"getting-around","title":"الحركة والتنقل","body":["فضّل العبّارات والحافلات على السيارات الخاصة؛ امشِ بين القرى القريبة عندما يكون ذلك آمنًا.","سافر خارج الذروة لتخفيف الضغط وتجنب الازدحام على الساحل."]},{"id":"refills","title":"إعادة التعبئة والنفايات","body":["استخدم النوافير العامة أو اطلب تعبئة من المقاهي؛ احمل زجاجة قابلة لإعادة الاستخدام.","افرز النفايات قدر الإمكان وتجنب أدوات الشاطئ ذات الاستخدام الواحد."]},{"id":"trails","title":"آداب المسارات","body":["افسح المجال في المقاطع الضيقة، واحمل كل مخلفاتك، واحترم الملكيات الخاصة.","ابدأ مبكرًا لتفادي الحرارة وازدحام المسارات."]}],"faqs":[],"faqsTitle":"الأسئلة الشائعة"},"itinerariesPillar":{"intro":"لا تحتاج إلى سيارة للاستمتاع بساحل أمالفي. استخدم العبّارات للتنقل الساحلي، والحافلات لقرى القمم، واضبط مواعيدك لتفادي أوقات الذروة. ابدأ بهذه القوالب ثم خصّصها.","toc":{"twoDays":"يومان: التركيز على بوسيتانو","threeDays":"3 أيام: أضف كابري أو أمالفي+رافيللو","sevenDays":"7 أيام: حلقة كاملة","tips":"نصائح التوقيت والمواصلات","faqs":"الأسئلة الشائعة"},"twoDaysHeading":"يومان: التركيز على بوسيتانو","twoDaysItems":["اليوم 1: البلدة القديمة في بوسيتانو، فورنيلو عبر الطريق الساحلي، غروب من منعطفات فيا باسيتيّا.","اليوم 2: <pathOfTheGodsLink>درب الآلهة</pathOfTheGodsLink> من نوتشيليه؛ استجمام على الشاطئ بعد ذلك."],"threeDaysHeading":"3 أيام: أضف كابري أو رافيللو","threeDaysIntro":"اختر أحدهما:","threeDaysOptions":["رحلة يومية إلى كابري: عبّارة مبكرة → مارينا غراندي → تلفريك أناتكابري → حلقة مارينا بيكولا.","أمالفي + رافيللو: حافلة إلى أمالفي ثم متابعة إلى رافيللو للحدائق والإطلالات؛ عُد بالحافلة/العبّارة."],"sevenDaysHeading":"7 أيام: حلقة كاملة","sevenDaysItems":["قاعدة بوسيتانو (ليلتان إلى ثلاث)","أمالفي/رافيللو (ليلتان)","كابري (ليلة واحدة) أو سورينتو (ليلتان) لتسهيل التنقل"],"tipsHeading":"نصائح التوقيت والمواصلات","tips":["استخدم العبّارات بين 10:00 و16:00 بحذر في أيام الذروة؛ انطلق مبكرًا أو لاحقًا.","اشترِ تذاكر الحافلة مسبقًا؛ وثّقها عند الصعود.","عند اضطراب البحر، انتقل إلى خطط الحافلات؛ تابع تنبيهات المشغلين."],"faqHeading":"الأسئلة الشائعة","faqs":[{"q":"هل تستحق السيارة العناء؟","a":["ندرة المواقف ومناطق المرور المحدودة ونظام تبديل اللوحات تجعل السفر دون سيارة أسهل في معظم العام."]}]},"amalfiTownGuide":{"headings":{"highlights":"أبرز المعالم","gettingThere":"الوصول","tips":"نصائح"},"highlights":["كاتدرائية أمالفي ودير بارادايسو.","متحف الورق (متحف موزيو ديلا كارتا) — فريد في تاريخ أمالفي.","الممشى البحري والآيس كريم على الدرج."],"gettingThere":"من بوسيتانو، تعد العبّارات (في الموسم) خيارًا مريحًا ومشهدًا جميلًا. بخلاف ذلك استقل حافلة SITA؛ توقّع طوابير في أوقات الذروة. اشترِ التذاكر مسبقًا.","tips":["وصل باكرًا؛ زر حدائق رافيللو بالحافلة من أمالفي ليوم كامل.","احمل واقيًا من الشمس؛ الظل محدود على الرصيف."]},"sevenDayNoCar":{"intro":"اجعل قاعدتك 3 ليالٍ في بوسيتانو، وليلتين في أمالفي/رافيللو، وليلة إلى ليلتين في كابري أو سورينتو لتسهيل التنقل.","toc":{"onThisPage":"في هذه الصفحة","overview":"نظرة عامة","dayByDay":"يوم بيوم","tips":"نصائح المواصلات والتوقيت","faqs":"الأسئلة الشائعة"},"days":[{"id":"day1","label":"اليوم 1","items":["الوصول إلى بوسيتانو؛ البلدة القديمة وفورنيلو"]},{"id":"day2","label":"اليوم 2","items":["درب الآلهة من نوتشيليه"]},{"id":"day3","label":"اليوم 3","items":["رحلة يومية إلى كابري أو شاطئ + جولة بالقارب"]},{"id":"day4","label":"اليوم 4","items":["الانتقال إلى أمالفي؛ الكاتدرائية؛ غروب على الرصيف"]},{"id":"day5","label":"اليوم 5","items":["حدائق رافيللو (فيلا روفولو/فيلا تشيمبروني)"]},{"id":"day6","label":"اليوم 6","items":["مبيت في كابري أو الانتقال إلى سورينتو"]},{"id":"day7","label":"اليوم 7","items":["المغادرة"]}],"tips":["العبّارات موسمية؛ احتفظ بخطة حافلات احتياطية إذا أصبح البحر هائجًا.","سافر قبل 10:00 أو بعد 16:00 في أيام الذروة لتجنب الطوابير."],"faq":[{"q":"هل يجب أن أغيّر قاعدة الإقامة؟","a":["قاعدتان تقللان من حمل الأمتعة المتكرر وتحافظان على كفاءة التنقل."]}]},"chiesaNuovaDepartures":{"intro":["تغادر النزل للحاق بالحافلة إلى أمالفي أو سورينتو؟ يوضح لك هذا المسار القصير أسرع طريق من هوستيل بريكيت إلى موقف حافلة كييزيّا نوفا/بار إنتيرناتسيونالي ويقدّم بعض النصائح."],"sections":[{"title":"لماذا هذا المسار مناسب","body":["تبلغ المسافة 120 مترًا (400 قدم) سيرًا من مدخل النزل إلى الموقف، على الطريق الرئيسي.","إذا كنت متجهًا نحو أمالفي، فالموقف مقابل بار إنتيرناتسيونالي. اصطف في جانب الطريق المطلّ على البحر. وإذا كنت متجهًا نحو سورينتو، فستنتظر خارج البار مباشرة."]},{"title":"التذاكر والتوقيت","body":["اشترِ تذكرة SITA من دكّان التبغ أو من داخل البار. لا تحاول ركوب الحافلة من دون تذكرة، إذ لا يمكنك شراؤها على متن الحافلة. قدّم تذكرتك للسائق عند الصعود. وإذا كنت ستلحق بأول حافلة في الصباح، فاشترِ تذكرتك في اليوم السابق وتعال إلينا في الاستقبال لترتيب تسجيل المغادرة.","تمتلئ الحافلات في الصيف كثيرًا. خطّط وفقًا لذلك، خصوصًا في أوقات الذروة."]}],"beforeList":[],"stepsList":["اخرج من Hostel Brikette، اصعد الدرج القصير إلى فياليه بازيتيا، ثم انعطف يسارًا.","اتبع الرصيف لمسافة تقارب 120 مترًا (400 قدم) على طول الطريق الرئيسي حتى تصل إلى بار إنتيرناتسيونالي."],"stepsMapEmbedUrl":"https://www.google.com/maps/embed?origin=mfe&mode=walking&pb=!1m5!4m4!4m1!2sHostel+Brikette!4m1!2sBar+Internazionale","kneesList":[],"kneesDockPrefix":"هل تبدأ مع الأمتعة من رصيف العبّارات؟ استخدم","kneesDockLinkLabel":"مسار الأمتعة من رصيف العبّارات","kneesPorterPrefix":"هل تحتاج أسعار الحمالين أو المساعدة في الحجز؟ اطّلع على","kneesPorterLinkLabel":"دليل خدمة الحمالين","faqs":[{"q":"هل تتوقف الحافلات مباشرة خارج النزل؟","a":"تتوقف عند بار إنتيرناتسيونالي، على بعد نحو 120 مترًا (400 قدم) نزولًا من المدخل."},{"q":"هل يمكنني شراء التذاكر في اللحظة الأخيرة؟","a":"نعم، يبيع متجر التبغ بجوار الموقف تذاكر SITA، لكن توقّع طوابير في أوقات الذروة."}],"howtoSteps":["غادر Hostel Brikette واتبع فياليه بازيتيا نزولًا","انضم إلى الطابور عند بار إنتيرناتسيونالي وفعّل التذكرة عند الصعود"],"faqsTitle":"الأسئلة الشائعة"}}')}}]);