"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[713355],{713355:n=>{n.exports=JSON.parse('{"seo":{"title":"B\xe3i biển Fiordo di Furore → Hostel Brikette bằng xe bu\xfdt","description":"L\xe0m theo c\xe1c bước n\xe0y để tới điểm dừng SITA ph\xeda tr\xean Fiordo di Furore, đi về Positano v\xe0 ho\xe0n tất đoạn đi bộ ngắn tới Hostel Brikette."},"linkLabel":"B\xe3i biển Fiordo di Furore → Hostel Brikette bằng xe bu\xfdt","intro":["Fiordo di Furore nằm dưới đường ven biển Amalfi v\xe0 kh\xf4ng c\xf3 quầy v\xe9 hay cửa h\xe0ng khi bạn xuống nước. H\xe3y d\xf9ng hướng dẫn quay về n\xe0y để leo lại l\xean đường, bắt xe bu\xfdt SITA về Positano v\xe0 đi bộ tới Hostel Brikette một c\xe1ch chủ động."],"warningsTitle":"Trước khi rời b\xe3i biển","warnings":["Mua v\xe9 SITA trước — gần b\xe3i kh\xf4ng c\xf3 nơi b\xe1n v\xe9 v\xe0 t\xe0i xế c\xf3 thể từ chối nếu bạn kh\xf4ng c\xf3 v\xe9 đ\xe3 đ\xf3ng dấu."],"sections":[{"id":"walk-to-road","title":"Leo lại l\xean đường","body":["Theo cầu thang từ b\xe3i sỏi l\xean đường ven biển Amalfi.","Điểm dừng xe bu\xfdt SITA nằm ngay nơi cầu thang gặp khu vực đỗ xe nhỏ; chờ ở ph\xeda n\xfai của con đường."]},{"id":"ride-the-bus","title":"Đi xe bu\xfdt SITA về hướng Positano","body":["L\xean xe SITA đi hướng Positano — tuyến n\xe0y dừng theo y\xeau cầu n\xean số điểm dừng trung gian phụ thuộc v\xe0o h\xe0nh kh\xe1ch kh\xe1c.","H\xe0nh tr\xecnh men theo bờ biển khoảng 10,8 km trước khi tới Positano."]},{"id":"get-off","title":"Xuống tại Bar Internazionale / Chiesa Nuova","body":["Bấm chu\xf4ng dừng ngay sau khi xe bu\xfdt rời đường hầm ph\xeda tr\xean Positano v\xe0 để \xfd qu\xe1n tabacchi Bar Internazionale.","Xuống ở điểm dừng Chiesa Nuova; đ\xe2y l\xe0 điểm SITA gần Hostel Brikette nhất."]},{"id":"walk-to-hostel","title":"Đi bộ đoạn cuối tới Hostel Brikette","body":["Tiếp tục đi c\xf9ng chiều với xe bu\xfdt hơn 100 m\xe9t.","Rẽ phải v\xe0o cầu thang cạnh qu\xe1n bar v\xe0 leo l\xean cửa ch\xednh của hostel."]}],"tipsTitle":"Mẹo khi quay về","tips":["Kiểm tra lịch mới nhất tr\xean concierge số của hostel để biết chuyến SITA kế tiếp.","D\xf9ng %LINK:positanoBeaches|tổng quan b\xe3i biển Positano% để l\xean lịch c\xe1c lần tắm biển kh\xe1c ph\xf9 hợp với h\xe0nh tr\xecnh Fiordo di Furore.","Nếu xe bu\xfdt đầy hoặc bị hủy, h\xe3y sẵn s\xe0ng vẫy taxi được cấp ph\xe9p ngay tại khu vực đỗ xe."],"faqsTitle":"C\xe2u hỏi thường gặp","faqs":[{"q":"T\xf4i n\xean chờ xe ở đ\xe2u?","a":["Đứng ở ph\xeda s\xe1t n\xfai của con đường tr\xean đỉnh cầu thang — xe bu\xfdt hướng Positano dừng ngay cạnh lan can chắn."]},{"q":"Nếu t\xf4i kh\xf4ng c\xf3 v\xe9 th\xec sao?","a":["T\xe0i xế y\xeau cầu v\xe9 SITA đ\xe3 đ\xf3ng dấu trước khi l\xean xe. H\xe3y mua v\xe9 khứ hồi tại Positano hoặc hỏi lễ t\xe2n về t\xecnh trạng v\xe9 trước khi rời hostel."]}]}')}}]);