"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[723759],{723759:e=>{e.exports=JSON.parse('{"meta":{"title":"Salerno to Positano Bus Guide – Hostels, Stops, and Timetable Tips","description":"Step-by-step directions for travelling from Salerno to Positano by SITA Sud bus via Amalfi. Learn where to buy tickets, how to read the timetable, and the best stop for Hostel Brikette."},"hero":{"eyebrow":"Hostel Brikette Travel Guide","heading":"Salerno to Positano – By Bus (via Amalfi)","body":"This guide covers the SITA bus journey from Salerno to Positano with a change in Amalfi. It includes ticket tips, how to read the timetable, and where to get off in Positano for the shortest walk to the hostel."},"tip":{"title":"Tip","guideLinkLabel":"How to Get Here overview","body":"Need every transport option? Open the <GuideLink>How to Get Here overview</GuideLink> for ferry, taxi, and mixed itineraries."},"journey":{"title":"Journey overview","description":"From Salerno, take a SITA bus to Amalfi, then transfer to the SITA bus bound for Positano. In Positano, alight at <Strong>Chiesa Nuova</Strong> for the shortest walk to the hostel."},"tickets":{"title":"SITA buses and tickets","description":"SITA Sud operates buses along this route. Tickets are sold at newsstands and tabacchi. When you board, the driver may stamp or partially tear your ticket to validate it — keep it until you exit."},"timetable":{"title":"Bus timetables","description":"You will travel <Strong>Salerno → Amalfi</Strong> then <Strong>Amalfi → Positano</Strong>.","link":"Consult the latest SITA schedule on <ScheduleLink>{{label}}</ScheduleLink>.","scheduleLinkLabel":"Positano.com","rowsTitle":"Reading the rows","columnsTitle":"Reading the columns","combineTitle":"Combine rows + columns","rows":["The rows in the first column list the bus stops along the route."],"columns":["Each column corresponds to a scheduled journey (labelled with a letter).","Mon–Sat: focus on columns \'F\' and \'G\'.","Sundays/holidays: focus on columns \'G\' and \'H\'."],"combine":["Pick your origin and destination rows, then read across to the chosen column to find times.","Example: departure from Salerno → row \'Salerno\'; arrival at Positano (Chiesa Nuova) → row \'Positano (Chiesa Nuova)\'."]},"positanoStop":{"title":"Positano (Chiesa Nuova) stop","facts":["Nearest stop: Positano (Chiesa Nuova) on Via G. Marconi.","Roughly 140 metres (490 feet) from the hostel entrance.","Outside the cafe/bar \'Bar Internazionale\'."]},"walk":{"title":"Walk to Hostel Brikette","steps":["Walk a little over 100 metres (330 feet).","Turn right to follow the steps up to the hostel’s main door.","Approaching the hostel, look for our sign by the second-floor terrace.","Continue past the sign and take the stairs on the right side of the road to the entrance."]}}')}}]);