"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[724141],{724141:t=>{t.exports=JSON.parse('{"meta":{"title":"سياسة ملفات تعريف الارتباط | Hostel Brikette","description":"كيف يستخدم Hostel Brikette ملفات تعريف الارتباط والتقنيات المشابهة على موقعنا، وما الخيارات المتاحة لك."},"headings":{"pageTitle":"سياسة ملفات تعريف الارتباط","subtitle":"ملفات تعريف الارتباط، التخزين المحلي، وخدمات الجهات الخارجية"},"lastUpdated":{"label":"آخر تحديث","date":"2026-01-09"},"toc":{"title":"في هذه الصفحة","aria":"جدول محتويات سياسة ملفات تعريف الارتباط"},"sections":{"intro":{"title":"نظرة عامة","body":["توضح سياسة ملفات تعريف الارتباط هذه ما هي ملفات تعريف الارتباط، وما إذا كنا نستخدمها على هذا الموقع، وكيف قد تستخدم خدمات الجهات الخارجية ملفات تعريف الارتباط عندما تتفاعل مع محتواها."]},"whatAreCookies":{"title":"ما هي ملفات تعريف الارتباط","body":["ملفات تعريف الارتباط هي ملفات نصية صغيرة يمكن للمواقع تخزينها على جهازك لتذكر المعلومات بين الزيارات. وتشمل التقنيات المشابهة التخزين المحلي وتخزين الجلسة في متصفحك."]},"ourUse":{"title":"كيفية استخدامنا لملفات تعريف الارتباط على هذا الموقع","body":["نهدف إلى تقليل استخدام ملفات تعريف الارتباط قدر الإمكان. عندما يكون ذلك ممكنًا، نستخدم تخزين المتصفح (التخزين المحلي) للتفضيلات البسيطة بدلاً من ملفات تعريف الارتباط.","إذا أدخلنا ملفات تعريف الارتباط في المستقبل (على سبيل المثال للأمان الأساسي أو التحليلات أو الخدمات المضمنة)، فسنقوم بتحديث هذه السياسة."]},"localStorage":{"title":"التخزين المحلي (التفضيلات)","body":["يسمح التخزين المحلي للموقع بتذكر بعض التفضيلات على جهازك. على سبيل المثال، قد يُستخدم للاحتفاظ بتفضيلات الواجهة بين الصفحات.","يمكنك مسح التخزين المحلي من إعدادات المتصفح."]},"thirdParties":{"title":"خدمات الجهات الخارجية","body":["قد تُقدَّم بعض الروابط أو التجارب المضمنة من جهات خارجية (مثل الخرائط أو مزودي الحجز). قد تقوم هذه الجهات بتعيين ملفات تعريف الارتباط أو استخدام تقنيات مشابهة وفقًا لسياساتها الخاصة.","عند اتباع رابط إلى موقع جهة خارجية، تنطبق سياسات الخصوصية وملفات تعريف الارتباط الخاصة بهم."]},"choices":{"title":"خياراتك","body":["تتيح لك معظم المتصفحات حظر ملفات تعريف الارتباط أو حذفها ومسح بيانات الموقع مثل التخزين المحلي. قد يؤثر حظر بعض التقنيات على الوظائف.","يمكنك أيضًا استخدام أوضاع التصفح الخاص أو إضافات المتصفح للحد من التتبع."]},"contact":{"title":"اتصال","body":["إذا كانت لديك أسئلة حول سياسة ملفات تعريف الارتباط هذه، فتواصل معنا على {{email}}."]}},"footer":{"backToTop":"العودة إلى الأعلى"}}')}}]);