"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[72456],{72456:t=>{t.exports=JSON.parse('{"seo":{"title":"لوميناريا دي سان دومينيكو (برايانو)","description":"مهرجان الشموع في برايانو مطلع أغسطس: المواعيد، كيفية الوصول، أفضل نقاط المشاهدة، وآداب التصوير."},"linkLabel":"لوميناريا دي سان دومينيكو (برايانو)","intro":["خطط لزيارة مهرجان لوميناريا دي سان دومينيكو في برايانو: أزقة مضاءة بالشموع، ألعاب نارية، ومشاهدة تحترم السكان."],"sections":[{"id":"basics","title":"أساسيات المهرجان","body":["يقام الحدث في الأسبوع الأول من أغسطس، وتبلغ الأمسيات ذروتها بين 1–4 أغسطس عندما تضيء آلاف الشموع ساحة بيازا سان جينارو.","وصل قبل الغروب لمشاهدة المتطوعين وهم يضعون الشموع ولحجز موقع مشاهدة جيد."]},{"id":"transport","title":"الوصول والمغادرة","body":["استقل حافلة SITA من بوزيتانو (20 دقيقة) أو احجز خدمة نقل مشتركة؛ تغادر آخر الحافلات عائدةً نحو منتصف الليل لكن تحقق في اليوم نفسه.","إذا كنت تخطط للبقاء حتى وقت متأخر، نسق سيارة أجرة مسبقًا أو اقضِ الليل في برايانو لتجنب الانتظار الطويل بعد الألعاب النارية."]},{"id":"etiquette","title":"آداب المشاهدة","body":["اترك الشموع سليمة لأهل البلدة؛ تجنب الدخول إلى المناطق المحاطة بالحبال أو إغلاق الممرات بالحامل الثلاثي.","استخدم حاملًا ثلاثي الأرجل أو سطحًا ثابتًا للتصوير الطويل، لكن ابتعد عن مداخل الكنيسة ومسارات الطوارئ."]}],"tips":["احمل زجاجة قابلة لإعادة التعبئة (500 مل / 17 أونصة) ومصباحًا صغيرًا للمشي في أزقة برايانو بعد حلول الظلام.","احترم أوقات الهدوء بعد انتهاء الألعاب النارية؛ فالسكان ينظفون الشمع حتى وقت متأخر من الليل."],"faqs":[{"q":"هل المهرجان مجاني؟","a":["نعم، المشاهدة مجانية. ادعم الأكشاك المحلية بشراء وجبة خفيفة أو التبرع في الكنيسة للمساهمة في تمويل الشموع للسنوات القادمة."]},{"q":"ماذا لو هطل المطر؟","a":["قد يؤدي المطر الغزير إلى تأجيل عروض الشموع؛ يعلن المنظمون التحديثات عبر صفحة بلدية برايانو على فيسبوك. خصص أمسية بديلة لزيارتك."]}],"event":{"date":"1–4 أغسطس (أمسيات الذروة)","location":"بيازا سان جينارو، برايانو","tips":["وصل قبل الغروب لمشاهدة المتطوعين وهم يرتبون الشموع.","احمل مصباحًا صغيرًا أو ضوء الهاتف عند العودة عبر الأزقة المظلمة."]},"gallery":{"terraceAlt":"فوانيس متوهجة على شرفة في برايانو ليلًا","coastAlt":"ساحل برايانو عند الغسق خلال مهرجان لوميناريا دي سان دومينيكو"}}')}}]);