"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[72508],{72508:n=>{n.exports=JSON.parse('{"seo":{"title":"Gavitella Beach Guide","description":"L\xean kế hoạch cho một ng\xe0y ngắm cảnh ho\xe0ng h\xf4n tại c\xe1c c\xe2u lạc bộ b\xe3i biển La Gavitella v\xe0 One Fire của Praiano với c\xe1c mẹo vận chuyển, đặt chỗ v\xe0 trả lại."},"linkLabel":"Gavitella Beach Guide","intro":["Vịnh Gavitella của Praiano quay mặt về ph\xeda t\xe2y n\xean nhận được nắng chiều k\xe9o d\xe0i c\xf9ng khoảnh khắc ho\xe0ng h\xf4n v\xe0ng \xf3ng hướng về Positano v\xe0 Capri. Hai lido — La Gavitella v\xe0 One Fire — c\xf9ng chia sẻ dải bờ hẹp nằm cạnh một khu vực c\xf4ng cộng nhỏ được đục v\xe0o v\xe1ch đ\xe1.","H\xe3y chuẩn bị cho một trải nghiệm được chăm ch\xfat v\xe0 cần đặt trước: cầu thang dốc giới hạn lượng kh\xe1ch, c\xe1c c\xe2u lạc bộ điều phối t\xe0u đưa đ\xf3n v\xe0 buổi tối xoay quanh bữa hải sản hoặc tiệc ho\xe0ng h\xf4n do DJ dẫn dắt."],"toc":[{"href":"#overview","label":"Bố cục & bầu kh\xf4ng kh\xed"},{"href":"#access","label":"C\xe1ch đến Gavitella"},{"href":"#gavitella-club","label":"La Gavitella Beach Club"},{"href":"#one-fire","label":"One Fire Beach Club"},{"href":"#amenities","label":"Tiện \xedch & kế hoạch"}],"sections":[{"id":"overview","title":"Bố cục & bầu kh\xf4ng kh\xed","body":["B\xe3i biển nằm ph\xeda dưới quảng trường Piazza San Gennaro của Praiano. Khoảng 400 bậc thang len lỏi giữa c\xe1c biệt thự v\xe0 vườn chanh bậc thang, dẫn xuống một bục đ\xe1, một gờ sỏi v\xe0 dải khu vực tự do mảnh giữa hai lido.","La Gavitella nằm b\xean tr\xe1i (khi hướng ra biển) với b\xe0n ăn nh\xe0 h\xe0ng, s\xe2n tắm nắng v\xe0 cầu cảng đ\xf3n kh\xe1ch d\xf9ng bữa tối. One Fire trải d\xe0i theo bến phải với ghế tắm nắng rực rỡ, bục DJ v\xe0 cầu t\xe0u d\xe0nh cho thuyền tiệc."]},{"id":"access","title":"C\xe1ch đến Gavitella","body":["Từ Hostel Brikette hoặc trung t\xe2m Positano, bắt xe bu\xfdt SITA hướng Amalfi v\xe0 xuống tại trạm Piazza San Gennaro của Praiano; d\xe0nh ra 10–15 ph\xfat để đi xuống cầu thang đến điểm đ\xe3 đặt chỗ.","Xe shuttle interno của Praiano di chuyển v\xf2ng giữa tuyến đường ph\xeda tr\xean v\xe0 Marina di Praia — h\xe3y d\xf9ng chuyến n\xe0y khi quay về để khỏi phải leo h\xe0ng trăm bậc sau ho\xe0ng h\xf4n. Khi biển \xeam, cả hai c\xe2u lạc bộ cũng c\xf3 thể sắp xếp thuyền đưa đ\xf3n từ Positano hoặc Marina di Praia."]},{"id":"gavitella-club","title":"La Gavitella Beach Club","body":["La Gavitella hướng đến những bữa trưa thong thả, giờ aperitivo v\xe0 chuyến t\xe0u v\xf2ng quanh quần đảo Li Galli l\xfac ho\xe0ng h\xf4n. Bếp tập trung v\xe0o hải sản theo m\xf9a — đĩa crudo, paccheri c\xe1 địa phương v\xe0 thực đơn tr\xe1ng miệng đậm hương chanh.","Nhớ n\xeau nhu cầu di chuyển khi đặt chỗ: nh\xe2n vi\xean sẽ điều phối t\xe0u nhỏ cho kh\xe1ch d\xf9ng bữa v\xe0 c\xf3 thể giữ h\xe0nh l\xfd gi\xfap bạn trong l\xfac bơi lội trước chuyến du ngoạn aperitif."]},{"id":"one-fire","title":"One Fire Beach Club","body":["One Fire l\xe0 b\xe3i biển tiệc t\xf9ng của Bờ biển Amalfi, nổi tiếng với nghi thức ăn dưa hấu buổi chiều, c\xe1c set DJ v\xe0 m\xe0n khuấy động đ\xe1m đ\xf4ng khớp nhịp c\xf9ng mặt trời lặn.","Đặt ghế tắm nắng hoặc cabana qua WhatsApp — tiền cọc l\xe0 điều thường thấy v\xe0o th\xe1ng 7 v\xe0 8. Khi s\xf3ng y\xean, t\xe0u của họ sẽ đ\xf3n nh\xf3m kh\xe1ch từ Positano v\xe0 đưa về sau khi \xe2m nhạc kết th\xfac."]},{"id":"amenities","title":"Tiện \xedch & kế hoạch","body":["Cả hai c\xe2u lạc bộ đều c\xf3 v\xf2i sen, ph\xf2ng thay đồ v\xe0 tủ kh\xf3a trả ph\xed; khăn tắm thường được bao gồm trong gi\xe1 ghế tắm nắng. Mang theo kem chống nắng th\xe2n thiện với rạn san h\xf4 v\xe0 gi\xe0y đi nước để di chuyển tr\xean bục đ\xe1 v\xe0 lối xuống sỏi.","Một dải c\xf4ng cộng nhỏ t\xe1ch hai lido. Kh\xf4ng gian hạn chế n\xean h\xe3y đến trước 10:00 c\xf9ng tấm thảm của ri\xeang bạn v\xe0 sẵn s\xe0ng tinh thần rằng nh\xe2n vi\xean sẽ thu dọn ghế nếu s\xf3ng đ\xe1nh dạt v\xe0o bờ."]}],"tipsTitle":"Mẹo nhanh","tips":["D\xf9ng %LINK:positanoAmalfi|hướng dẫn đi lại Positano ↔ Amalfi của ch\xfang t\xf4i% để l\xean kế hoạch chuyến SITA đến Piazza San Gennaro trước khi bước xuống 400 bậc thang.","Xem %LINK:sitaTickets|mẹo mua v\xe9 xe bu\xfdt SITA% cho chặng quay về; tuyến interno của Praiano gi\xfap bạn khỏi phải leo dốc trong b\xf3ng tối.","Đặt lu\xf4n nh\xe0 h\xe0ng tr\xean v\xe1ch đ\xe1 của La Gavitella khi giữ chỗ ghế tắm nắng — thực đơn thử hải sản v\xe0 chuyến t\xe0u ho\xe0ng h\xf4n rất đ\xe1ng tiền.","So s\xe1nh ghế tắm nắng, khu vực miễn ph\xed v\xe0 c\xe1c vịnh thay thế trong %LINK:positanoBeaches|tổng quan b\xe3i biển Positano% trước khi quyết định."],"warningsTitle":"Lưu \xfd","warnings":["Mỗi chiều c\xf3 khoảng 400 bậc thang gập ghềnh; Gavitella kh\xf4ng ph\xf9 hợp với xe lăn hoặc h\xe0nh l\xfd cồng kềnh.","Khu vực miễn ph\xed c\xf3 thể biến mất khi biển động — h\xe3y chuẩn bị phương \xe1n dự ph\xf2ng cho những ng\xe0y gi\xf3 lớn.","T\xe0u đưa đ\xf3n sẽ dừng khi biển chuyển động, v\xec vậy h\xe3y x\xe1c nhận chuyến về trước khi c\xe1c sự kiện ho\xe0ng h\xf4n bắt đầu."],"faqsTitle":"C\xe2u hỏi thường gặp về Gavitella","faqs":[{"q":"L\xe0m sao để đến Gavitella từ Hostel Brikette?","a":["Đi bộ đến trạm Chiesa Nuova v\xe0 bắt xe bu\xfdt SITA hướng Amalfi. Xuống tại Piazza San Gennaro của Praiano rồi theo cầu thang c\xf3 biển chỉ dẫn khoảng 400 bậc xuống b\xe3i biển. Khi biển lặng, bạn cũng c\xf3 thể đặt trước t\xe0u đưa đ\xf3n từ bến ch\xednh Positano."]},{"q":"T\xf4i c\xf3 cần đặt chỗ trước kh\xf4ng?","a":["C\xf3. Li\xean hệ La Gavitella hoặc One Fire qua WhatsApp hay biểu mẫu trực tuyến, cung cấp thời gian đến v\xe0 hỏi về khoản đặt cọc. Cuối tuần v\xe0 khung giờ ho\xe0ng h\xf4n từ th\xe1ng 6 đến th\xe1ng 9 thường k\xedn nhanh."]},{"q":"Nếu kh\xf4ng thu\xea ghế tắm nắng th\xec c\xf3 khu miễn ph\xed kh\xf4ng?","a":["Giữa hai c\xe2u lạc bộ c\xf3 một dải c\xf4ng cộng hẹp. Mang theo thảm hoặc gi\xe0y đi nước, đến sớm v\xe0 tu\xe2n thủ d\xe2y hoặc lối đi m\xe0 nh\xe2n vi\xean bố tr\xed cho thuyền ra v\xe0o."]},{"q":"T\xf4i c\xf3 thể ở lại d\xf9ng bữa tối hoặc tham gia sự kiện muộn kh\xf4ng?","a":["La Gavitella phục vụ bữa tối tr\xean s\xe2n thượng v\xe0 thường kết hợp với chuyến t\xe0u ngắn. One Fire tổ chức tiệc ho\xe0ng h\xf4n kết th\xfac v\xe0o khoảng chuyến interno cuối c\xf9ng. H\xe3y x\xe1c nhận phương tiện quay lại với nh\xe2n vi\xean để khỏi phải leo cầu thang trong đ\xeam."]}]}')}}]);