"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[73717],{73717:t=>{t.exports=JSON.parse('{"meta":{"title":"कुकी नीति | Hostel Brikette","description":"Hostel Brikette हमारी वेबसाइट पर कुकीज़ और समान तकनीकों का उपयोग कैसे करता है और आपके पास क्या विकल्प हैं।"},"headings":{"pageTitle":"कुकी नीति","subtitle":"कुकीज़, स्थानीय भंडारण और तृतीय‑पक्ष सेवाएँ"},"lastUpdated":{"label":"अंतिम अपडेट","date":"2026-01-09"},"toc":{"title":"इस पृष्ठ पर","aria":"कुकी नीति की विषय‑सूची"},"sections":{"intro":{"title":"सारांश","body":["यह कुकी नीति बताती है कि कुकीज़ क्या हैं, क्या हम इस वेबसाइट पर उनका उपयोग करते हैं, और जब आप उनके कंटेंट से इंटरैक्ट करते हैं तो तृतीय‑पक्ष सेवाएँ कुकीज़ कैसे उपयोग कर सकती हैं।"]},"whatAreCookies":{"title":"कुकीज़ क्या हैं","body":["कुकीज़ छोटे टेक्स्ट फ़ाइलें हैं जिन्हें वेबसाइटें आपके डिवाइस पर सहेज सकती हैं ताकि विज़िट के बीच जानकारी याद रखी जा सके। समान तकनीकों में आपके ब्राउज़र का स्थानीय भंडारण और सत्र भंडारण शामिल है।"]},"ourUse":{"title":"हम इस वेबसाइट पर कुकीज़ का उपयोग कैसे करते हैं","body":["हम कुकीज़ का उपयोग न्यूनतम रखने का प्रयास करते हैं। जब संभव हो, हम साधारण प्राथमिकताओं के लिए कुकीज़ के बजाय ब्राउज़र स्टोरेज (स्थानीय भंडारण) का उपयोग करते हैं।","यदि हम भविष्य में कुकीज़ का उपयोग शुरू करते हैं (उदाहरण के लिए आवश्यक सुरक्षा, एनालिटिक्स या एम्बेडेड सेवाओं के लिए), तो हम इस नीति को अपडेट करेंगे।"]},"localStorage":{"title":"स्थानीय भंडारण (प्राथमिकताएँ)","body":["स्थानीय भंडारण वेबसाइट को आपके डिवाइस पर कुछ प्राथमिकताएँ याद रखने देता है। उदाहरण के लिए, यह पृष्ठों के बीच इंटरफ़ेस प्राथमिकताओं को बनाए रखने के लिए इस्तेमाल हो सकता है।","आप ब्राउज़र सेटिंग्स से स्थानीय भंडारण साफ़ कर सकते हैं।"]},"thirdParties":{"title":"तृतीय‑पक्ष सेवाएँ","body":["कुछ लिंक या एम्बेडेड अनुभव तृतीय‑पक्ष द्वारा दिए जा सकते हैं (जैसे मानचित्र या बुकिंग प्रदाता)। ये तृतीय‑पक्ष अपनी नीतियों के अनुसार कुकीज़ सेट कर सकते हैं या समान तकनीकों का उपयोग कर सकते हैं।","जब आप किसी तृतीय‑पक्ष वेबसाइट पर लिंक खोलते हैं, तो उनकी गोपनीयता और कुकी नीतियाँ लागू होती हैं।"]},"choices":{"title":"आपके विकल्प","body":["अधिकांश ब्राउज़र कुकीज़ को ब्लॉक या डिलीट करने और स्थानीय भंडारण जैसी साइट डेटा साफ़ करने की अनुमति देते हैं। कुछ तकनीकों को ब्लॉक करने से कार्यक्षमता प्रभावित हो सकती है।","आप ट्रैकिंग सीमित करने के लिए निजी ब्राउज़िंग मोड या ब्राउज़र एक्सटेंशन का भी उपयोग कर सकते हैं।"]},"contact":{"title":"संपर्क","body":["यदि आपके पास इस कुकी नीति के बारे में प्रश्न हैं, तो {{email}} पर संपर्क करें।"]}},"footer":{"backToTop":"शीर्ष पर वापस जाएँ"}}')}}]);