"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[739306],{739306:n=>{n.exports=JSON.parse('{"seo":{"title":"Path of the Gods qua shuttle Nocelle","description":"Bắt xe bu\xfdt Montepertuso–Nocelle để đi bộ khứ hồi tr\xean Sentiero degli Dei, k\xe8m lịch tr\xecnh, mẹo v\xe0 lựa chọn quay về."},"linkLabel":"Path of the Gods qua shuttle Nocelle","intro":["\xcdt thời gian hoặc muốn bỏ qua nhiều chặng trung chuyển? H\xe3y đi xe bu\xfdt Montepertuso–Nocelle từ Hostel Brikette, đi bộ đoạn đẹp nhất của Path of the Gods theo dạng khứ hồi rồi trở về bằng ch\xednh tuyến n\xe0y.","Phương \xe1n n\xe0y bỏ qua ph\xe0 v\xe0 trung chuyển Amalfi, rất ph\xf9 hợp cho ng\xe0y thư th\xe1i hoặc du kh\xe1ch muốn lịch tr\xecnh nhẹ nh\xe0ng."],"sections":[{"id":"prep","title":"Chuẩn bị tại hostel","body":["Mua hai v\xe9 xe bu\xfdt mỗi người tại Bar Internazionale: Positano → Nocelle v\xe0 Nocelle → Positano. Nh\xe2n vi\xean sẽ tư vấn g\xf3i tiết kiệm nhất.","Kiểm tra lịch Montepertuso/Nocelle mới nhất để biết chuyến cuối trước ho\xe0ng h\xf4n.","Chuẩn bị nước, đồ ăn nhẹ v\xe0 \xe1o kho\xe1c mỏng. Ki-ốt ở Nocelle b\xe1n đồ uống theo m\xf9a, nhưng tr\xean đường m\xf2n kh\xf4ng c\xf3 cửa h\xe0ng."]},{"id":"bus-to-nocelle","title":"Đi xe bu\xfdt l\xean Nocelle","body":["Chờ trước Bar Internazionale. Xe hiển thị “Montepertuso - Nocelle” ở mặt trước.","Xe đi qua Montepertuso trước khi kết th\xfac tại b\xe3i đỗ nhỏ ở Nocelle. H\xe3y ngồi y\xean tới khi xe quay đầu xuống dốc — đ\xf3 l\xe0 điểm của bạn.","Đ\xf3ng dấu v\xe9 khi l\xean xe v\xe0 giữ lại cho lượt về."]},{"id":"walk-through-town","title":"Đi xuy\xean Nocelle tới đường m\xf2n","body":["Từ b\xe3i đỗ, đi theo bậc thang d\xe0nh cho người đi bộ xuống lối ch\xednh rồi rẽ tr\xe1i.","Đi khoảng 175 m\xe9t, giữ b\xean phải tại ng\xe3 rẽ quan trọng để tr\xe1nh cầu thang xuống Positano.","Tiếp tục thẳng cho tới khi thấy biển Sentiero degli Dei v\xe0 lối đ\xe1 dẫn ra khỏi l\xe0ng."]},{"id":"hike","title":"Đi bộ xa t\xf9y th\xedch","body":["Hướng về Colle Serra để c\xf3 g\xf3c nh\xecn đẹp nhất. H\xe0nh tr\xecnh khứ hồi đầy đủ tới Bomerano d\xe0i khoảng 11,6 km, nhưng bạn c\xf3 thể quay đầu bất cứ l\xfac n\xe0o.","Đi theo vạch sơn đỏ-trắng của CAI, bước cẩn thận ở đoạn hở v\xe0 dừng lại chụp ảnh tại c\xe1c ban c\xf4ng to\xe0n cảnh.","Khi muốn quay lại, đi ngược đường cũ về Nocelle."]},{"id":"return","title":"Quay về Positano","body":["Đi lại qua Nocelle theo đường cũ, rẽ phải tại ng\xe3 rẽ quan trọng để trở lại điểm dừng xe bu\xfdt.","D\xf9ng v\xe9 lượt về cho chuyến xe xuống Bar Internazionale hoặc Chiesa Nuova. Nếu xe đầy, bạn c\xf3 thể đi bộ theo đường nhựa (khoảng 4 km) nhưng h\xe3y d\xe0nh 45–60 ph\xfat.","Đừng chọn cầu thang tắt trừ khi bạn thật sự muốn thử th\xe1ch đầu gối v\xe0 chấp nhận kết th\xfac ở ph\xeda đối diện thị trấn."]},{"id":"journey-planner","title":"Lịch tr\xecnh mẫu","body":["06:30 xe bu\xfdt → 06:50 tới Nocelle → đi bộ 3 giờ → 09:50 xe về → 10:10 tới Positano (3 giờ 40 ph\xfat từ cửa tới cửa).","08:30 khởi h\xe0nh → 08:50 tới Nocelle → 3 giờ 20 ph\xfat đi bộ → 11:50 xe về → 12:10 tới Positano (4 giờ tổng cộng).","10:30 khởi h\xe0nh → 10:50 tới Nocelle → đi bộ 3 giờ → 13:50 xe về → 14:10 tới Positano (3 giờ 40 ph\xfat)."]}],"essentialsTitle":"Vật dụng cần thiết","essentials":["Hai v\xe9 SITA mỗi người (l\xean v\xe0 xuống) c\xf9ng một v\xe9 dự ph\xf2ng.","Nước, đồ ăn nhẹ, kem chống nắng v\xe0 mũ cho đoạn đường phơi nắng.","Gi\xe0y b\xe1m tốt v\xe0 c\xf3 thể mang gậy trekking cho đoạn bậc đ\xe1."],"typicalCostsTitle":"Chi ph\xed tham khảo","typicalCosts":["V\xe9 shuttle Nocelle: €1,80–€2,40 mỗi chặng t\xf9y loại v\xe9.","Granita hoặc đồ uống tại ki-ốt Nocelle: €3–€5.","Đường m\xf2n: miễn ph\xed."],"tipsTitle":"Mẹo h\xe0nh tr\xecnh","tips":["Nhắm chuyến s\xe1ng sớm để tận hưởng thời tiết m\xe1t v\xe0 đường m\xf2n \xedt người.","Mang \xe1o kho\xe1c nhẹ — tr\xean sống n\xfai thường c\xf3 gi\xf3 ngay cả khi Positano n\xf3ng.","Giữ lại phần thưởng ở ki-ốt gần nh\xe0 thờ cho l\xfac quay về."],"warningsTitle":"Cảnh b\xe1o","warnings":["Bỏ qua biển chỉ cầu thang xuống Positano trừ khi bạn muốn một h\xe0nh tr\xecnh d\xe0i v\xe0 kết th\xfac xa hostel.","Xe bu\xfdt thưa chuyến hơn v\xe0o buổi chiều — đặt b\xe1o thức để kh\xf4ng lỡ chuyến cuối m\xe0 bạn c\xf3 thể bắt.","Đường m\xf2n kh\xf4ng c\xf3 b\xf3ng r\xe2m; tr\xe1nh đi v\xe0o l\xfac nắng gắt trong th\xe1ng 7 v\xe0 8."],"steps":[{"name":"Mua v\xe9 xe bu\xfdt","text":"Mua v\xe9 Nocelle tại Bar Internazionale trước khi rời hostel."},{"name":"Đi xe l\xean Nocelle","text":"Bắt xe Montepertuso–Nocelle v\xe0 ngồi y\xean tới khi xe quay đầu ở b\xe3i đỗ."},{"name":"T\xecm tới đường m\xf2n","text":"Theo lối đi bộ xuy\xean qua l\xe0ng, giữ b\xean phải để gặp biển Sentiero degli Dei."},{"name":"Đi bộ khứ hồi","text":"Tiến về ph\xeda Colle Serra, quay đầu khi bạn sẵn s\xe0ng trở lại."},{"name":"Về lại Positano","text":"Trở lại điểm dừng xe bu\xfdt v\xe0 đi chuyến xuống Bar Internazionale hoặc Chiesa Nuova."}],"faqsTitle":"C\xe2u hỏi thường gặp","faqs":[{"q":"T\xf4i n\xean đi bao l\xe2u trước khi quay đầu?","a":["Nhiều người đi 45–60 ph\xfat về ph\xeda Colle Serra để ngắm cảnh đẹp nhất rồi quay lại. Nếu muốn tới Bomerano, h\xe3y chừa th\xeam thời gian."]},{"q":"Xe bu\xfdt Nocelle c\xf3 tin cậy kh\xf4ng?","a":["C\xf3, nhưng chạy theo lịch cố định với khoảng nghỉ d\xe0i hơn giữa trưa. Kiểm tra lịch d\xe1n tại Bar Internazionale v\xe0 chừa th\xeam thời gian ph\xf2ng xe đầy."]},{"q":"T\xf4i c\xf3 thể đi bộ xuống thay v\xec đi xe kh\xf4ng?","a":["Bạn c\xf3 thể đi đường nhựa xuống trong 45–60 ph\xfat. Tr\xe1nh cầu thang nếu bạn kh\xf4ng chủ \xfd tập luyện v\xe0 chưa quen đi h\xe0ng ngh\xecn bậc."]}],"toc":{"onThisPage":"Nội dung ch\xednh","essentials":"Vật dụng cần thiết","costs":"Chi ph\xed tham khảo","tips":"Mẹo h\xe0nh tr\xecnh","warnings":"Cảnh b\xe1o","faqs":"C\xe2u hỏi thường gặp"}}')}}]);