"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[766079],{766079:i=>{i.exports=JSON.parse('{"seo":{"title":"Positano का इतिहास","description":"मध्यकालीन व्यापार, समुद्री कथाएँ और आधुनिक पर्यटन तक के प्रमुख मील के पत्थर।"},"linkLabel":"Positano का इतिहास","intro":["Positano के अतीत को समझें—समुद्री जड़ों से लेकर हॉलीवुड खोज तक—ताकि आपकी वॉक को ऐतिहासिक संदर्भ मिले।"],"sections":[{"id":"origins","title":"उत्पत्ति और व्यापार","body":["Positano की शुरुआत रोमन समुद्री विला हब के रूप में हुई; बाद में मध्यकालीन व्यापारी ऊन निर्यात करते और Amalfi के बेड़े के जरिए मसाले आयात करते थे।","तट के साथ बने वॉचटावर Saracen छापों की चेतावनी देते थे—आज कई व्यूपॉइंट के रूप में बचे हैं।"]},{"id":"20th","title":"20वीं सदी","body":["द्वितीय विश्व युद्ध के बाद Steinbeck जैसे कलाकारों और लेखकों ने Positano की खूबसूरती को प्रचारित किया, जिससे अर्थव्यवस्था पर्यटन की ओर मुड़ी।","1950s में बेहतर सड़कों ने Positano को बाकी इटली से जोड़ा और बुटीक होटल व आगंतुकों की लहर आई।"]},{"id":"today","title":"आधुनिक समय","body":["आज पर्यटन शहर का मुख्य आधार है; फेरी कोटा और पैदल क्षेत्रों के जरिए संरक्षण और भीड़ का संतुलन रखा जाता है।","स्थानीय कारीगर अब भी सैंडल और सिरेमिक बनाते हैं, जिससे विरासत वाली कारीगरी आतिथ्य के साथ जीवित रहती है।"]}],"tips":["Santa Maria Assunta के नीचे स्थित Roman Villa म्यूज़ियम देखें—यहाँ 1987 में निकाली गई मोज़ेक्स हैं।","Via dei Mulini पर सूचना पट्टियाँ देखें जो प्रमुख ऐतिहासिक इमारतों को हाइलाइट करती हैं।"],"faqs":[{"q":"साइट पर मैं और कहाँ सीख सकता/सकती हूँ?","a":["MAR Positano म्यूज़ियम में आर्कियोलॉजी और मल्टीमीडिया डिस्प्ले हैं—बारिश वाले दिनों के लिए बढ़िया।"]},{"q":"यहाँ इतनी सीढ़ियाँ क्यों हैं?","a":["शहर को चट्टानों के अनुरूप वर्टिकल बनाया गया; टेरेस के बीच जल्दी आने‑जाने के लिए सड़कों की जगह सीढ़ियाँ बनीं।"]}]}')}}]);