"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[769256],{769256:e=>{e.exports=JSON.parse('{"seo":{"title":"Limoncello & Local Cuisine on the Amalfi Coast","description":"What to try in Positano: limoncello, seafood staples, lemon desserts, and ordering etiquette."},"linkLabel":"Limoncello & Local Cuisine","intro":["Lemons and limoncello define Amalfi flavours — from tasting flights to sweet finales.","Here’s where to sip, what to buy, and how to cook with them back home."],"tocTitle":"On this page","sections":[{"id":"tasting","title":"Tasting","body":["Visit small botteghe for artisan limoncello and ask for a sample to compare styles.","Pair tastings with lemon gelato, sorbet, or pastries to gauge sweetness and balance."]},{"id":"purchase","title":"Buying & shipping","body":["Check airline liquid limits; pack bottles securely in checked luggage.","Look for IGP Costa d’Amalfi labels and recyclable packaging from local producers."]},{"id":"cooking","title":"Cooking with lemons","body":["Use zest and juice in salads, seafood pasta, and desserts for brightness.","Store lemons in a cool, dark spot and repurpose peels for syrups or marinades."]}],"tips":["Support small producers — flavours are often more intense than tourist-trap brands.","Skip overpriced souvenir shops near the main beach; head a street or two back for better value."],"faqs":[{"q":"Can I carry limoncello in hand luggage?","a":["Not over 100 ml — pack standard bottles in checked bags with padding."]},{"q":"Are there alcohol-free alternatives?","a":["Try fresh lemonade, granita, or lemon syrups if you prefer to skip alcohol."]}],"gallery":{"title":"Photo gallery","items":[{"alt":"Positano vintage poster with lemons","caption":"Vintage lemons"},{"alt":"Terrace table with limoncello glasses","caption":"Terrace limoncello"}]}}')}}]);