"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[773868],{773868:n=>{n.exports=JSON.parse('{"seo":{"title":"Fornillo Beach guide","description":"Bản đồ bố tr\xed, c\xe1c khu vực miễn ph\xed v\xe0 lời khuy\xean về c\xe2u lạc bộ b\xe3i biển cho Pupetto, Da Ferdinando, Grassi v\xe0 La Marinella - c\xf9ng với c\xe1c lối tắt đến v\xe0 đi từ nh\xe0 trọ."},"linkLabel":"Hướng dẫn về B\xe3i biển Fornillo: c\xe2u lạc bộ & điểm miễn ph\xed","intro":["B\xe3i biển Fornillo l\xe0 một vịnh nhỏ thư gi\xe3n c\xe1ch nh\xe0 trọ một qu\xe3ng đi bộ ngắn. C\xe1c c\xe2u lạc bộ b\xe3i biển giữ mức gi\xe1 nhẹ nh\xe0ng hơn Spiaggia Grande, c\xe1c khu vực c\xf4ng cộng rộng r\xe3i v\xe0 bầu kh\xf4ng kh\xed thoải m\xe1i ngay cả trong những tuần cao điểm.","Sử dụng lối đi bộ ven biển để di chuyển giữa c\xe1c khu vực b\xe3i biển, lấy đồ ăn từ c\xe1c qu\xe1n bar v\xe0 quay trở lại Spiaggia Grande nếu bạn muốn đi xe bu\xfdt nội địa l\xean dốc."],"toc":[{"href":"#layout","label":"Bố cục v\xe0 quyền truy cập"},{"href":"#main-free","label":"Phần miễn ph\xed ch\xednh"},{"href":"#pupetto","label":"C\xe2u lạc bộ b\xe3i biển Pupetto"},{"href":"#ferdinando","label":"Da Ferdinando"},{"href":"#grassi","label":"Fratelli Grassi"},{"href":"#marinella","label":"La Marinella"},{"href":"#secret-free","label":"Vịnh nhỏ ẩn giấu"}],"sections":[{"id":"layout","title":"Bố tr\xed v\xe0 điều h\xe0nh b\xe3i biển","body":["Hai con đường ch\xednh dẫn tới Fornillo. Lối tắt từ %LINK:hostelBriketteToFornilloBeach|Nh\xe0 nghỉ Brikette% đến ph\xeda b\xean tr\xe1i của b\xe3i biển (khi hướng ra biển); lối đi dạo ven s\xf4ng từ Spiaggia Grande đến ph\xeda b\xean tay phải.","C\xe1c c\xe2u lạc bộ b\xe3i biển chạy theo tr\xecnh tự từ điểm v\xe0o của nh\xe0 trọ hướng tới con đường ch\xednh tr\xean b\xe3i biển: Pupetto, Da Ferdinando, Fratelli Grassi v\xe0 La Marinella. Hai khu vực c\xf4ng cộng bao quanh c\xe1c lidos - một dải đất rộng tự do b\xean cạnh Pupetto v\xe0 một vịnh nhỏ hơn ngo\xe0i La Marinella."]},{"id":"main-free","title":"Phần miễn ph\xed ch\xednh","body":["Khu vực miễn ph\xed lớn nhất nằm ngay ph\xeda trước lối tắt từ nh\xe0 trọ. Đến trước 10:30 để gi\xe0nh chỗ gần mực nước; sỏi ấm l\xean nhanh ch\xf3ng n\xean h\xe3y mang theo một tấm thảm hoặc khăn d\xe0y.","Pupetto v\xe0 Da Ferdinando đều b\xe1n đồ uống mang đi v\xe0 b\xe1nh panini - ho\xe0n hảo nếu bạn muốn ăn uống m\xe0 kh\xf4ng cần thu\xea ghế d\xe0i. Theo d\xf5i thủy triều trong c\xe1c th\xe1ng vai; những cơn b\xe3o m\xf9a thu c\xf3 thể thu hẹp bờ biển trong một hoặc hai ng\xe0y."]},{"id":"pupetto","title":"C\xe2u lạc bộ b\xe3i biển Pupetto","body":["Pupetto neo ở ph\xeda b\xean tr\xe1i của Fornillo với những d\xe3y ghế d\xe0i m\xe0u cam, v\xf2i sen v\xe0 một nh\xe0 h\xe0ng đầy đủ dịch vụ. Đặt trước qua WhatsApp hoặc điện thoại v\xe0o m\xf9a h\xe8 nếu bạn muốn ngồi ở h\xe0ng ghế đầu; việc đi bộ thường ổn trước 09:30.","Những kh\xe1ch sử dụng khu vực miễn ph\xed vẫn c\xf3 thể đặt h\xe0ng mang đi từ quầy bar v\xe0 ph\xf2ng tắm của Pupetto mở cửa cho kh\xe1ch h\xe0ng trả tiền - rất tiện lợi nếu bạn quyết định n\xe2ng cấp giữa chừng trong ng\xe0y."]},{"id":"ferdinando","title":"Da Ferdinando","body":["Da Ferdinando mang đến bầu kh\xf4ng kh\xed m\xe1t mẻ của qu\xe1n bar tr\xean b\xe3i biển với những chiếc ghế d\xe0i m\xe0u xanh lam, dịch vụ cho thu\xea v\xe1n ch\xe8o v\xe0 thực đơn bữa trưa phổ biến (h\xe3y nghĩ đến m\xf3n nướng trong ng\xe0y, salad v\xe0 m\xec ống). Mong đợi gi\xe1 cao hơn Pupetto một ch\xfat nhưng vẫn hợp l\xfd cho Positano.","Taxi nước đến Laurito v\xe0 Arienzo đi ngang qua cầu cảng của Da Ferdinando - rất hữu \xedch nếu bạn định đi biển sau khi bơi buổi s\xe1ng."]},{"id":"grassi","title":"Qu\xe1n bar b\xe3i biển Fratelli Grassi","body":["Grassi nhỏ gọn với những h\xe0ng ghế d\xe0i m\xe0u đỏ gọn g\xe0ng v\xe0 quầy bar c\xf3 b\xf3ng r\xe2m phục vụ cocktail v\xe0 granita. Nh\xf3m cung cấp t\xfai kh\xf4 đựng đồ c\xf3 gi\xe1 trị nếu bạn thu\xea thuyền kayak để kh\xe1m ph\xe1 c\xe1c hang động gần đ\xf3.","V\xec kh\xf4ng gian c\xf3 hạn n\xean việc đặt trước sẽ c\xf3 \xedch v\xe0o cuối tuần. Thường c\xf3 thể đến v\xe0o giữa tuần sau 11:00 sau khi việc đặt chỗ sớm được giải quyết."]},{"id":"marinella","title":"La Marinella","body":["La Marinella nằm gần lối đi Spiaggia Grande nhất v\xe0 đ\xf3n \xe1nh nắng giữa trưa. Mong đợi dịch vụ chu đ\xe1o, đĩa ăn trưa v\xe0 t\xf9y chọn đặt ghế d\xe0i ở tầng tr\xean c\xf3 th\xeam b\xf3ng r\xe2m.","V\xe0o m\xf9a vai, La Marinella đ\xf4i khi di dời một v\xe0i chiếc ghế sang vịnh l\xe2n cận. Nh\xe2n vi\xean n\xean chừa kh\xf4ng gian cho khu vực c\xf4ng cộng nhưng h\xe3y chuẩn bị tinh thần cho việc di chuyển chặt chẽ hơn khi biển động."]},{"id":"secret-free","title":"Phần miễn ph\xed \'B\xed mật\'","body":["Ngo\xe0i La Marinella, một vịnh nhỏ c\xf3 khu vực c\xf4ng cộng y\xean tĩnh hơn với thềm đ\xe1 cuội v\xe0 mặt nước tĩnh lặng. Mang theo kem chống nắng v\xe0 gi\xe0y đi nước th\xe2n thiện với rạn san h\xf4 - lối v\xe0o c\xf3 nhiều đ\xe1 v\xe0 nh\xedm biển c\xf3 thể tụ tập gần r\xeca.","La Marinella thỉnh thoảng đặt những chiếc ghế d\xe0i dự ph\xf2ng ở đ\xe2y v\xe0o những ng\xe0y gi\xf4ng b\xe3o. Khu vực n\xe0y ch\xednh thức vẫn miễn ph\xed, v\xec vậy h\xe3y cảm thấy thoải m\xe1i khi sử dụng kh\xf4ng gian nếu bạn đến trước; chỉ chừa chỗ cho ghế hoặc thuyền kayak."]}],"tipsTitle":"Mẹo nhanh","tips":["Đi theo %LINK:hostelBriketteToFornilloBeach|tuyến đường tắt% để xuống dốc nhanh nhất (15–20 ph\xfat).","Khi bạn đ\xe3 sẵn s\xe0ng khởi h\xe0nh, h\xe3y bắt %LINK:fornilloBeachToBrikette|tuyến đường trở về% dọc theo lối đi dạo v\xe0 bắt xe bu\xfdt nội địa quay trở lại Chiesa Nuova.","So s\xe1nh gi\xe1 cả v\xe0 c\xe1c vịnh thay thế trong %LINK:positanoBeaches|Tổng quan về b\xe3i biển Positano% của ch\xfang t\xf4i trước khi bạn đặt ghế nằm."],"warningsTitle":"Thật tốt khi biết","warnings":["Fornillo c\xf3 nhiều đ\xe1 cuội - h\xe3y mang gi\xe0y hoặc d\xe9p đi trong nước để đi dọc bờ biển.","B\xf3ng r\xe2m bị hạn chế sau buổi s\xe1ng muộn, v\xec vậy h\xe3y l\xean kế hoạch thu\xea d\xf9 che nắng hoặc mang theo mũ v\xe0 kem chống nắng.","M\xe1y thẻ thỉnh thoảng ngoại tuyến; mang theo một \xedt tiền mặt để thanh to\xe1n v\xe0 số dư tr\xean ghế d\xe0i."],"faqsTitle":"C\xe2u hỏi thường gặp về Fornillo","faqs":[{"q":"T\xf4i c\xf3 cần đặt trước ghế nằm kh\xf4ng?","a":["Trợ gi\xfap đặt ph\xf2ng v\xe0o cuối tuần m\xf9a h\xe8. Pupetto v\xe0 La Marinella trả lời qua điện thoại hoặc WhatsApp; Da Ferdinando v\xe0 Grassi cũng theo d\xf5i tin nhắn trực tiếp tr\xean Instagram. Những người đến sớm trước 09:30 thường c\xf3 thể gi\xe0nh được chỗ ngồi kh\xf4ng cần hẹn trước."]},{"q":"T\xf4i c\xf3 thể mang theo đồ ăn của ri\xeang m\xecnh được kh\xf4ng?","a":["Những buổi d\xe3 ngoại được ch\xe0o đ\xf3n trong c\xe1c phần miễn ph\xed. Nếu bạn thu\xea ghế nằm, h\xe3y mang theo đồ ăn nhẹ v\xe0 chai nước trừ khi c\xe2u lạc bộ x\xe1c nhận kh\xe1c. Mỗi nh\xe0 điều h\xe0nh đều c\xf3 một qu\xe1n bar hoặc nh\xe0 h\xe0ng phục vụ những bữa ăn lớn hơn."]},{"q":"T\xf4i c\xf3 thể tắm hoặc sử dụng ph\xf2ng tắm ở đ\xe2u?","a":["Cơ sở vật chất thuộc về từng c\xe2u lạc bộ b\xe3i biển. H\xe3y hỏi quầy bar trước khi sử dụng nếu bạn đang ở khu vực c\xf4ng cộng - nhiều qu\xe1n cho ph\xe9p bạn gh\xe9 thăm nhanh với một khoản ph\xed nhỏ hoặc khi bạn mua đồ ăn hoặc đồ uống."]}]}')}}]);