"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[782284],{782284:e=>{e.exports=JSON.parse('{"seo":{"title":"पाथ ऑफ द गॉड्स (नॉचेल शटल मार्ग)","description":"मोंटेपरटूसो–नॉचेल बस पकड़ें, सेंटिएरो देग्ली देई पर आउट-एंड-बैक हाइक करें और समय, टिप्स व वापसी विकल्प जानें।"},"linkLabel":"नॉचेल शटल से पाथ ऑफ द गॉड्स","intro":["समय कम है या कई ट्रांसफर नहीं करना चाहते? हॉस्टल ब्रिकेत्ते से मोंटेपरटूसो–नॉचेल बस लें, पाथ ऑफ द गॉड्स के सबसे पैनोरमिक हिस्से को आउट-एंड-बैक चलें और उसी रास्ते लौट आएं।","यह विकल्प फेरी और अमाल्फी कनेक्शन को छोड़ देता है, इसलिए शांत दिनों या हल्की यात्रा पसंद करने वाले यात्रियों के लिए आदर्श है।"],"sections":[{"id":"prep","title":"हॉस्टल में तैयारी करें","body":["प्रति व्यक्ति दो बस टिकट बार इंटरनाज़ियोनाले से खरीदें: पोसीटानो → नॉचेल और नॉचेल → पोसीटानो। स्टाफ आपको सबसे किफायती विकल्प बता सकता है।","ताज़ा मोंटेपरटूसो/नॉचेल टाइमटेबल देखें ताकि सूर्यास्त से पहले अंतिम वापसी का समय पता हो।","पानी, स्नैक्स और परतें पैक करें। मौसम में नॉचेल का कियोस्क पेय बेचता है, लेकिन ट्रेल पर कोई दुकान नहीं है।"]},{"id":"bus-to-nocelle","title":"बस में बैठकर नॉचेल पहुँचें","body":["बार इंटरनाज़ियोनाले के बाहर प्रतीक्षा करें। बस के सामने “Montepertuso - Nocelle” लिखा होगा।","बस मोंटेपरटूसो से होते हुए नॉचेल के छोटे कार पार्क तक पहुँचती है। बस जैसे ही यू-टर्न लेकर नीचे की ओर लौटने लगती है, वहीं उतरें—यही आपका स्टॉप है।","चढ़ते ही टिकट वैलिडेट करें और वापसी के लिए संभालकर रखें।"]},{"id":"walk-through-town","title":"नॉचेल से ट्रेल तक पैदल जाएं","body":["कार पार्क से सीढ़ियों द्वारा पैदल रास्ते पर उतरें और बाईं ओर मुड़ें।","करीब 175 मीटर चलें और मुख्य मोड़ पर दाईं ओर बने रहें ताकि पोसीटानो नीचे उतरने वाली सीढ़ियों से बच सकें।","सीधे चलते रहें जब तक “Sentiero degli Dei” के संकेत और गाँव से बाहर जाती पत्थर की पगडंडी न दिखे।"]},{"id":"hike","title":"जितना चाहें उतना ट्रेक करें","body":["सबसे शानदार दृश्यों के लिए कोल्ले सेरा की ओर बढ़ें। पूरा राउंड-ट्रिप बोमेरानो तक लगभग 11.6 किमी है, लेकिन आप किसी भी समय लौट सकते हैं।","लाल-सफेद CAI मार्कर का अनुसरण करें, खुली धारियों पर सावधानी से चलें और पैनोरमिक बालकनी पर रुककर तस्वीरें लें।","जब तैयार हों, उसी रास्ते नॉचेल वापस लौटें।"]},{"id":"return","title":"पोसीटानो वापस आएं","body":["नॉचेल से वही रास्ता चलते हुए लौटें और मुख्य मोड़ पर दाईं ओर मुड़कर बस स्टॉप तक पहुँचें।","वापसी टिकट का उपयोग करके बस से बार इंटरनाज़ियोनाले या चिएसा नुओवा तक जाएं। यदि बसें भरी हों तो पक्की सड़क से पैदल उतर सकते हैं (लगभग 4 किमी, 45–60 मिनट)।","सीढ़ियों वाले शॉर्टकट से बचें जब तक कि आप लंबी, घुटनों पर असर डालने वाली उतराई को जानबूझकर न करना चाहें—यह शहर के दूसरी ओर समाप्त होती है।"]},{"id":"journey-planner","title":"नमूना समय-सारिणी","body":["06:30 बस → 06:50 नॉचेल आगमन → 3 घंटे की हाइक → 09:50 वापसी बस → 10:10 पोसीटानो (कुल 3घं40मि)।","08:30 प्रस्थान → 08:50 नॉचेल → 3घं20मि हाइक → 11:50 वापसी बस → 12:10 पोसीटानो (कुल 4घं)।","10:30 प्रस्थान → 10:50 नॉचेल → 3 घंटे की हाइक → 13:50 वापसी बस → 14:10 पोसीटानो (कुल 3घं40मि)।"]}],"essentialsTitle":"जरूरी सामान","essentials":["प्रति व्यक्ति दो SITA टिकट (ऊपर और नीचे) और एक अतिरिक्त।","पानी, स्नैक्स, सनस्क्रीन और धूप वाली धारियों के लिए टोपी।","ग्रिप वाले आरामदायक जूते और असमान सीढ़ियों के लिए वैकल्पिक ट्रेकिंग पोल।"],"typicalCostsTitle":"सामान्य लागत","typicalCosts":["नॉचेल शटल टिकट: प्रति सवारी €1.80–€2.40 (कार्नेट पर निर्भर)।","नॉचेल के कियोस्क से ग्रानीटा या पेय: €3–€5।","ट्रेल प्रवेश: निःशुल्क।"],"tipsTitle":"रूट टिप्स","tips":["ठंडी हवा और शांत पगडंडियों के लिए सुबह की बसें चुनें।","एक हल्का जैकेट रखें—रिज़ पर हवा चल सकती है भले ही पोसीटानो गर्म हो।","वापसी पर चर्च के पास कियोस्क से एक छोटा ट्रीट बचाकर रखें।"],"warningsTitle":"सावधानियाँ","warnings":["सीढ़ियों वाले संकेत को नज़रअंदाज़ करें जब तक कि आप जानबूझकर लंबी उतराई नहीं करना चाहते जो हॉस्टल से काफी दूर खत्म होती है।","दोपहर में बसें कम चलती हैं—जिस अंतिम वापसी को लेना चाहते हैं, उसके लिए अलार्म सेट करें।","ट्रेल खुला है; जुलाई और अगस्त की तेज धूप में दोपहर के समय हाइक से बचें।"],"steps":[{"name":"बस टिकट खरीदें","text":"हॉस्टल से निकलने से पहले बार इंटरनाज़ियोनाले से नॉचेल टिकट लें।"},{"name":"नॉचेल की ओर सफर","text":"मोंटेपरटूसो–नॉचेल बस पकड़ें और कार पार्क पर यू-टर्न लेते समय उतरें।"},{"name":"ट्रेल तक पहुँचें","text":"गाँव के पैदल रास्ते का अनुसरण करें, दाईं ओर रहकर सेंटिएरो देग्ली देई के संकेत खोजें।"},{"name":"आउट-एंड-बैक हाइक","text":"कोल्ले सेरा की दिशा में चलें और जब चाहें तब मुड़ें।"},{"name":"पोसीटानो लौटें","text":"बस स्टॉप पर वापस जाएँ और बार इंटरनाज़ियोनाले या चिएसा नुओवा के लिए बस लें।"}],"faqsTitle":"अक्सर पूछे जाने वाले प्रश्न","faqs":[{"q":"मुझे कितनी देर तक हाइक करनी चाहिए?","a":["कई हाइकर 45–60 मिनट तक कोल्ले सेरा की ओर जाते हैं ताकि सबसे प्रसिद्ध व्यू मिल सकें, फिर लौट आते हैं। यदि बोमेरानो तक पहुँचना चाहते हैं तो अधिक समय रखें।"]},{"q":"क्या नॉचेल बस भरोसेमंद है?","a":["हाँ, लेकिन यह तय समय-सारिणी पर चलती है और दोपहर में अंतराल लंबा हो सकता है। बार इंटरनाज़ियोनाले पर प्रदर्शित समय देखें और बस भर जाने की स्थिति के लिए थोड़ा बफर रखें।"]},{"q":"क्या मैं बस के बजाय पैदल नीचे उतर सकता हूँ?","a":["आप पक्की सड़क से 45–60 मिनट में नीचे पहुँच सकते हैं। सीढ़ियों वाले मार्ग से तभी उतरें जब आप कसरत का इरादा रखते हों और हजारों सीढ़ियों के लिए तैयार हों।"]}],"toc":{"onThisPage":"इस पृष्ठ पर","essentials":"जरूरी सामान","costs":"सामान्य लागत","tips":"रूट टिप्स","warnings":"सावधानियाँ","faqs":"प्रश्नोत्तर"}}')}}]);