"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[784918],{784918:n=>{n.exports=JSON.parse('{"seo":{"title":"Xe bu\xfdt quay lại Hostel Brikette từ b\xe3i biển ch\xednh của Positano","description":"Catch the interno bus at Piazza dei Mulini and ride up to the tabacchi beside Hostel Brikette."},"linkLabel":"Xe bu\xfdt quay lại Hostel Brikette từ Spiaggia Grande","intro":["Bỏ qua hơn 600 bậc thang sau một ng\xe0y đi biển bằng c\xe1ch đi xe bu\xfdt Interno Positano m\xe0u cam trở lại Hostel Brikette.","V\xe9 c\xf3 gi\xe1 trị theo một trong hai hướng, v\xec vậy bạn c\xf3 thể mua ch\xfang tại tabacchi trước khi đi đến b\xe3i biển hoặc mua ch\xfang tr\xean đường trở về nếu cổ phiếu cho ph\xe9p."],"sections":[{"id":"walk-to-stop","title":"Đi bộ đến Piazza dei Mulini","body":["Rời khỏi Spiaggia Grande qua cổng v\xf2m b\xean cạnh cảng v\xe0 đi theo l\xe0n đường mua sắm đến quảng trường Piazza dei Mulini.","Trạm xe bu\xfdt nội bộ nằm b\xean cạnh hiệu thuốc v\xe0 c\xf9ng một tabacchi/quầy b\xe1n b\xe1o được sử dụng cho v\xe9."]},{"id":"wait-for-the-bus","title":"Chờ ở Piazza dei Mulini","body":["Kiểm tra thời gian biểu được đăng tại nơi tr\xfa ẩn hoặc th\xf4ng qua nh\xe2n vi\xean trợ gi\xfap kỹ thuật số; dịch vụ được thực hiện muộn v\xe0o ban đ\xeam v\xe0 v\xe0o m\xf9a đ\xf4ng.","Đứng b\xean cạnh hiệu thuốc v\xe0 tabacchi để bạn c\xf3 thể l\xean xe ngay khi xe bu\xfdt Interno Positano m\xe0u cam đến."]},{"id":"ride-uphill","title":"Đi xe bu\xfdt nội bộ l\xean dốc","body":["L\xean chiếc xe m\xe0u cam được đ\xe1nh dấu \\"Interno Positano\\" v\xe0 đi v\xf2ng qua thị trấn về ph\xeda Chiesa Nuova.","Lắng nghe cuộc gọi dừng hoặc theo d\xf5i g\xf3c tabacchi nơi bạn l\xean m\xe1y bay trước đ\xf3 trong ng\xe0y."]},{"id":"final-steps","title":"C\xe1c bước cuối c\xf9ng để đến kh\xe1ch sạn gi\xe1 rẻ","body":["Xuống xe tại điểm dừng tabacchi, rẽ tr\xe1i v\xe0o Viale Pasitea v\xe0 đi bộ l\xean dốc khoảng 100 m\xe9t (330 feet).","Đi cầu thang b\xean phải để trở về thẳng lối v\xe0o ch\xednh của Hostel Brikette."]}],"tipsTitle":"Lời khuy\xean về xe bu\xfdt","tips":["Mang theo v\xe9 dự ph\xf2ng để bạn c\xf3 thể l\xean xe ngay lập tức nếu xe bu\xfdt đ\xf4ng đ\xfac.","Nếu c\xe1c dịch vụ kết th\xfac sớm, h\xe3y quay lại %LINK:positanoMainBeachWalkBack|tuyến đường đi bộ% - n\xf3 đi theo c\xe1c điểm kiểm tra cầu thang của kh\xe1ch sạn gi\xe1 rẻ.","Cho nh\xe2n vi\xean lễ t\xe2n biết nếu bạn ph\xe1t hiện c\xf3 thay đổi về thời gian biểu để nh\xe2n vi\xean hỗ trợ kỹ thuật số lu\xf4n cập nhật cho c\xe1c kh\xe1ch kh\xe1c."],"extras":{"photos":{"title":"Photos","items":{"busInterior":{"alt":"Xe bu\xfdt Interno Positano ở trạm dừng b\xean bờ biển","caption":"Xe bu\xfdt Interno Positano - c\xe1c phương tiện kh\xe1c nhau nhưng nh\xe3n tuyến đường vẫn giữ nguy\xean."},"barInternazionaleStop":{"alt":"Trạm xe bu\xfdt Chiesa Nuova / Bar Internazionale","caption":"Chiesa Nuova (Bar Internazionale) - điểm dừng gần nhất với Hostel Brikette."}}}}}')}}]);