"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[792999],{792999:n=>{n.exports=JSON.parse('{"meta":{"title":"Từ Positano đến Ravello bằng ph\xe0 rồi xe bu\xfdt | Hostel Brikette","description":"Lộ tr\xecnh từng bước từ Positano tới Ravello: đi bộ xuống cảng, đi ph\xe0 đến Amalfi rồi tiếp tục xe bu\xfdt SITA l\xean Ravello; k\xe8m mẹo v\xe9, căn thời gian v\xe0 chuyển tuyến mượt m\xe0."},"hero":{"eyebrow":"Cẩm nang di chuyển của Hostel Brikette","title":"Positano đến Ravello — Ph\xe0 rồi xe bu\xfdt (qua Amalfi)","description":"D\xf9ng hướng dẫn n\xe0y để tới Ravello bằng c\xe1ch kết hợp chuyến ph\xe0 ngắm cảnh đến Amalfi v\xe0 xe bu\xfdt SITA l\xean đỉnh. B\xe0i viết bao gồm lộ tr\xecnh đi bộ xuống cảng, mẹo mua v\xe9, nơi chuyển tuyến tại Amalfi v\xe0 những g\xec chờ đ\xf3n khi đặt ch\xe2n tới Ravello."},"tip":{"label":"Mẹo","body":"Cần lựa chọn di chuyển kh\xe1c? Mở <Link>Tổng quan C\xe1ch đến đ\xe2y</Link> để xem xe bu\xfdt, dịch vụ s\xe2n bay v\xe0 c\xe1c h\xe0nh tr\xecnh kết hợp."},"overview":{"title":"Tổng quan h\xe0nh tr\xecnh","points":["Tuyến đi: Positano → Amalfi bằng ph\xe0, sau đ\xf3 Amalfi → Ravello bằng xe bu\xfdt SITA.","D\xe0nh th\xeam thời gian v\xe0o m\xf9a cao điểm cho h\xe0ng chờ v\xe0 kết nối.","Kiểm tra lịch ph\xe0 v\xe0 xe bu\xfdt cập nhật trong ng\xe0y khởi h\xe0nh."]},"walk":{"title":"Đi bộ tới cảng Positano","intro":"Bạn c\xf3 thể theo lối cầu thang quen thuộc từ hostel xuống Spiaggia Grande. Nếu mang h\xe0nh l\xfd nặng, h\xe3y c\xe2n nhắc gửi đồ với dịch vụ khu\xe2n v\xe1c để di chuyển nhẹ nh\xe0ng.","steps":["Rời Hostel Brikette, đi xuống c\xe1c bậc thang ra đường ch\xednh rồi rẽ tr\xe1i (Via G. Marconi).","Đi hơn 100 m tới ng\xe3 rẽ b\xean phải (cửa h\xe0ng tabacchi ở g\xf3c).","Nếu tiện, mua sẵn v\xe9 xe bu\xfdt Amalfi → Ravello tại cửa h\xe0ng tabacchi.","Tiếp tục xuống dốc; tại kh\xfac cua gấp đầu ti\xean, rẽ xuống lối cầu thang tắt khỏi đường.","Đi theo c\xe1c bậc thang v\xe0 lối đi xuống dốc, băng qua đường ở c\xe1c vị tr\xed được chỉ dẫn.","Tới cuối đường, rẽ phải, đi qua lối v\xf2m rồi tiếp tục khoảng 50 m tới cảng."],"link":"Th\xedch xem bằng bản đồ? Mở <Link>Chiesa Nuova → B\xe3i biển (\xedt bậc thang nhất)</Link>."},"ferry":{"title":"Mua v\xe9 ph\xe0","notes":["Tại cảng, so s\xe1nh giờ xuất ph\xe1t v\xe0 gi\xe1 tại quầy của từng h\xe3ng.","H\xe3ng phổ biến: Travelmar v\xe0 Lucibello (x\xe1c nhận chuyến kế tiếp đi Amalfi).","V\xe0o m\xf9a h\xe8, đến sớm 15–20 ph\xfat để l\xean t\xe0u ung dung."],"resources":"Xem giờ chạy v\xe0 gi\xe1 mới nhất tr\xean <TravelmarLink>Travelmar</TravelmarLink> v\xe0 <PositanoLink>Positano.com – Lịch ph\xe0</PositanoLink>."},"transfer":{"title":"Đổi tuyến tại Amalfi","notes":["Xuống t\xe0u tại Amalfi rồi đi bộ một đoạn ngắn tới khu trạm xe bu\xfdt.","Nếu chưa mua v\xe9 ở Positano, h\xe3y mua v\xe9 Amalfi → Ravello ngay tại đ\xe2y.","Xếp h\xe0ng chờ xe bu\xfdt đi Ravello; tuyến thường l\xe0 5110/5120 (kiểm tra bảng th\xf4ng tin)."],"resources":"Xem tuyến v\xe0 giờ chạy SITA tr\xean <SitaLink>Positano.com (tổng quan lịch SITA)</SitaLink>."},"arrival":{"title":"Đến Ravello","notes":["Ravello nằm tr\xean cao so với Amalfi; nhiều điểm tham quan tập trung quanh Piazza Duomo.","Từ điểm xuống xe, đi theo biển chỉ dẫn địa phương tới chỗ ở của bạn.","Nếu tới muộn hoặc phải chờ l\xe2u, c\xe2n nhắc đi taxi từ Amalfi thay v\xec đợi."]}}')}}]);