"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[799734],{799734:e=>{e.exports=JSON.parse('{"seo":{"title":"Услуги носильщиков в Позитано","description":"Как работают носильщики: где бронировать, чего ждать по ценам и когда их стоит вызывать."},"linkLabel":"Услуги носильщиков в Позитано","intro":["Носильщики помогут с тяжёлым багажом или при ограниченной мобильности. Договаривайтесь о встрече у причала или автобусной остановки, заранее подтверждайте тариф и держите ценности при себе."],"sections":[{"id":"how","title":"Как обычно проходит","body":["Согласуйте место и время (причал или остановка), сообщите название проживания и время встречи.","Обговорите стоимость до начала — тариф зависит от расстояния, количества лестниц и времени суток.","Держите ценности при себе и подпишите багаж."]},{"id":"resources","title":"Бронирование и контакты","body":["Попросите у проживания контакты надёжных носильщиков рядом с причалом и главными остановками.","Для хранения или ночных часов воспользуйтесь сервисами вроде Bounce или Radical Storage."]},{"id":"etiquette","title":"Этикет и советы","body":["Вознаграждайте за отличную работу или крупный багаж; готовьте мелкие купюры.","В пиковые периоды делитесь планом прибытия заранее, чтобы гарантировать слот."]}],"faqs":[{"q":"Нужны ли мне носильщики?","a":["Рекомендуются, если багаж тяжёлый: город изобилует лестницами и крутыми улицами."]},{"q":"Где заказать услугу?","a":["Обратитесь к вашему проживанию или к стойкам у главного пляжа и автобусных остановок."]},{"q":"Сколько это стоит?","a":["Зависит от расстояния и веса — уточните цену до начала работы."]}],"galleryTitle":"Фотогалерея","gallery":{"standsCaption":"Точки носильщиков (пример)","cartsCaption":"Тележки носильщиков"}}')}}]);