"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[805266],{805266:n=>{n.exports=JSON.parse('{"seo":{"title":"Positano cho phượt thủ tiết kiệm: lịch tr\xecnh 1-3 ng\xe0y","description":"Kế hoạch 1-3 ng\xe0y đơn giản với b\xe3i biển, điểm ngắm v\xe0 c\xe1c chuyến đi trong ng\xe0y gi\xe1 rẻ."},"linkLabel":"Positano cho phượt thủ tiết kiệm: lịch tr\xecnh 1-3 ng\xe0y","intro":["Chọn lịch tr\xecnh 1, 2 hoặc 3 ng\xe0y để giữ chi ph\xed thấp nhưng vẫn đủ view, b\xe3i biển v\xe0 chuyến đi trong ng\xe0y."],"toc":[{"href":"#day1","label":"Ng\xe0y 1: Positano cơ bản"},{"href":"#day2","label":"Ng\xe0y 2: Con Đường của C\xe1c Vị Thần + b\xe3i biển"},{"href":"#day3","label":"Ng\xe0y 3: Capri hoặc Amalfi + Ravello"},{"href":"#savings","label":"Mẹo tiết kiệm"},{"href":"#food","label":"Ăn rẻ"},{"href":"#transport","label":"Mẹo di chuyển"}],"sections":[{"id":"pacing","title":"Ưu ti\xean c\xe1c ng\xe0y leo nhiều bậc","body":["Ng\xe0y đến n\xean đi dạo khu thấp (Spiaggia Grande, Fornillo) v\xe0 để c\xe1c ng\xe0y leo nhiều bậc (Path of the Gods, bậc thang Arienzo) sau khi đ\xe3 ngủ đủ.","Nh\xf3m điểm theo cao độ để ở c\xf9ng một tầng; l\xe0m xong c\xe1c viewpoint ở tr\xean rồi mới xuống bến thuyền."]},{"id":"budget-mix","title":"Mỗi ng\xe0y một điểm trả ph\xed","body":["Ng\xe2n s\xe1ch cho một trải nghiệm trả ph\xed mỗi ng\xe0y (ph\xe0 đi Capri, tour thuyền chung, v\xe9 vườn), c\xf2n lại l\xe0 đi bộ miễn ph\xed v\xe0 tắm biển.","Mua đồ ăn nhẹ v\xe0 nước gần Chiesa Nuova; qu\xe1n ven biển thường cộng gi\xe1 cao nhất."]},{"id":"viewpoints","title":"Viewpoint kh\xf4ng tốn v\xe9","body":["Via Cristoforo Colombo cho g\xf3c bưu thiếp v\xe0o b\xecnh minh hoặc golden hour m\xe0 kh\xf4ng mất ph\xed v\xe0o.","Muốn chụp y\xean tĩnh, rẽ v\xe0o s\xe2n l\xe1t gạch cạnh Chiesa Nuova hoặc belvedere nhỏ ph\xeda tr\xean Fornillo trước 08:30."]},{"id":"swim-breaks","title":"Tắm biển tiết kiệm","body":["B\xe3i Fornillo c\xf3 khu c\xf4ng cộng miễn ph\xed; đi theo lối ven biển từ Spiaggia Grande v\xe0 chỉ thu\xea những g\xec cần.","Muốn kh\xf4ng kh\xed beach club Arienzo, đi xuống cầu thang trước 10:30 để giữ chỗ khu c\xf4ng cộng, rồi picnic với focaccia v\xe0 tr\xe1i c\xe2y."]},{"id":"evenings","title":"Buổi tối tiết kiệm","body":["Ngắm ho\xe0ng h\xf4n ở s\xe2n thượng hostel l\xe0 miễn ph\xed; pha spritz đơn giản với prosecco từ si\xeau thị v\xe0 chia sẻ đồ ăn nhẹ.","Xem bảng th\xf4ng b\xe1o ở hostel; nếu ra ngo\xe0i, đi muộn khi ph\xed v\xe0o cửa thấp hơn v\xe0 mang giấy tờ."]}],"day1Title":"1 ng\xe0y: Positano cơ bản","day1":["S\xe1ng: ngắm Spiaggia Grande, rồi đi theo lối ven biển tới Fornillo để tắm.","Trưa: pizza al taglio hoặc panini gần Chiesa Nuova; ăn ở c\xe1c s\xe2n hi\xean.","Ho\xe0ng h\xf4n: Via Cristoforo Colombo hoặc s\xe2n thượng hostel."],"day2Title":"2 ng\xe0y: th\xeam Con Đường của C\xe1c Vị Thần","day2":["S\xe1ng: %LINK:pathOfTheGodsNocelle|Sentiero degli Dei / Path of the Gods% (xe bu\xfdt tới Nocelle, đi bộ xuống Positano).","Chiều: %LINK:fornilloBeachGuide|l\xe0m m\xe1t ở b\xe3i Fornillo% hoặc ăn trưa muộn trong thị trấn."],"day3Title":"3 ng\xe0y: Capri hoặc Amalfi + Ravello","day3":["Lựa chọn A: %LINK:capriDayTrip|đi Capri trong ng\xe0y% - bắt ph\xe0 sớm nhất, l\xean lịch v\xf2ng DIY v\xe0 mang đồ ăn nhẹ.","Lựa chọn B: %LINK:positanoRavello|Amalfi + Ravello% - đi bus hoặc ph\xe0 tới Amalfi, rồi l\xean Villa Rufolo hoặc Villa Cimbrone."],"savingsTitle":"Mẹo tiết kiệm","savings":["Du lịch m\xf9a vai (th\xe1ng 4-5, cuối th\xe1ng 9) để gi\xe1 rẻ v\xe0 \xedt đ\xf4ng.","D\xf9ng xe bu\xfdt SITA cho hầu hết chặng; th\xeam một chặng ph\xe0 nếu muốn ngắm cảnh.","Ở ph\xf2ng dorm hoặc chia sẻ ph\xf2ng gần Chiesa Nuova để tiết kiệm ph\xed porter v\xe0 bậc thang.","Đổ đầy nước ở hostel hoặc v\xf2i nước c\xf4ng cộng trước khi đi.","Nếu đặt trực tiếp, d\xf9ng bữa s\xe1ng v\xe0 đồ uống buổi tối miễn ph\xed để thay một bữa.","Đi bộ giữa c\xe1c viewpoint thay v\xec đi bus; cầu thang miễn ph\xed v\xe0 nhiều cảnh đẹp."],"foodTitle":"Ăn rẻ","foodText":"Ưu ti\xean pizza al taglio, panini v\xe0 salad deli quanh Via dei Mulini v\xe0 Chiesa Nuova. Mua ở mini-market để picnic v\xe0 d\xe0nh bữa ăn ngồi b\xe0n cho một lần duy nhất.","transportTitle":"Mẹo di chuyển","transportCompareLabel":"So s\xe1nh tuyến:","transportCompareLinks":[{"key":"naplesPositano","label":"Naples → Positano"},{"key":"salernoPositano","label":"Salerno → Positano"}],"transportFerryPrefix":"Kiểm tra ","transportFerryLinkLabel":"lịch ph\xe0","transportFerrySuffix":" v\xe0 thời tiết; xe bu\xfdt rẻ hơn nhiều.","faqs":[{"q":"Kế hoạch 1 ng\xe0y tốt nhất l\xe0 g\xec?","a":["Bắt đầu ở Spiaggia Grande, đi theo lối ven biển tới Fornillo, rồi ngắm ho\xe0ng h\xf4n từ Via Cristoforo Colombo hoặc s\xe2n thượng hostel."]},{"q":"Giữ chi ph\xed di chuyển thấp bằng c\xe1ch n\xe0o?","a":["D\xf9ng xe bu\xfdt SITA cho hầu hết h\xe0nh tr\xecnh v\xe0 th\xeam một chặng ph\xe0 để ngắm cảnh; mua v\xe9 trước khi l\xean xe để tr\xe1nh bất ngờ."]},{"q":"Lịch tr\xecnh 2 ng\xe0y th\xec sao?","a":["Ng\xe0y 1 bao gồm essentials của Positano; Ng\xe0y 2 th\xeam Sentiero degli Dei qua Nocelle v\xe0 một lần tắm biển."]},{"q":"C\xf3 gợi \xfd n\xe0o cho 3 ng\xe0y kh\xf4ng?","a":["Chọn một: đi Capri trong ng\xe0y hoặc combo Amalfi + Ravello; đi sớm v\xe0 mang đồ ăn nhẹ để tiết kiệm."]}]}')}}]);