"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[843838],{843838:n=>{n.exports=JSON.parse('{"slug":"careers","meta":{"title":"Bản dịch đang được chuẩn bị.","description":"Gia nhập đội ngũ coi trọng th\xe1i độ v\xe0 nhiệt huyết hơn hồ sơ. Kh\xe1m ph\xe1 cơ hội năng động v\xe0 m\xf4i trường hiếu kh\xe1ch hiện đại tại Hostel Brikette ở Positano."},"careersSection":{"title":"Trở th\xe0nh một phần của điều đặc biệt","section1Title":"Gia nhập đội ngũ coi trọng con người hơn hồ sơ","section1Paragraph1":"Tại Hostel Brikette, ch\xfang t\xf4i đề cao t\xednh c\xe1ch, sự tận t\xe2m v\xe0 niềm say m\xea dịch vụ hơn mọi chứng chỉ. Nếu bạn tin rằng m\xecnh c\xf3 thể mang lại trải nghiệm đ\xe1ng nhớ bằng sự ch\xe2n th\xe0nh v\xe0 th\xe1i độ t\xedch cực, ch\xfang t\xf4i muốn được gặp bạn.","section1Paragraph2":"H\xe3y gửi email tới hostelpositano@gmail.com v\xe0 kể cho ch\xfang t\xf4i điều khiến bạn kh\xe1c biệt. Chia sẻ những th\xe0nh tựu nổi bật – trong c\xf4ng việc hay cuộc sống – v\xe0 c\xe1ch bạn sẽ hết m\xecnh v\xec kh\xe1ch. Ch\xfang t\xf4i t\xecm kiếm những người năng động, nhiệt huyết v\xe0 y\xeau th\xedch m\xf4i trường hướng tới con người.","section2Paragraph1":"Ch\xfang t\xf4i cung cấp quyền lợi vượt chuẩn ng\xe0nh: chỗ ở ngay tại hostel, bữa s\xe1ng miễn ph\xed, đồ uống tại quầy bar với gi\xe1 nội bộ v\xe0 cả những chuyến đi miễn ph\xed.","section2Paragraph2":"L\xe0 doanh nghiệp gia đ\xecnh với đội ngũ n\xf2ng cốt tận t\xe2m, ch\xfang t\xf4i lu\xf4n h\xe1o hức đ\xf3n ch\xe0o nh\xe2n tố mới v\xe0 g\xf3c nh\xecn mới.","section3Paragraph1":"Ch\xfang t\xf4i tự ph\xe1t triển phần mềm để tinh giản vận h\xe0nh, gi\xfap bạn tập trung v\xe0o điều quan trọng nhất: phục vụ kh\xe1ch thật chu đ\xe1o. Chatbot sẽ xử l\xfd c\xe1c c\xe2u hỏi lặp lại, c\xf2n ứng dụng trực quan gi\xfap bạn học nhanh v\xe0 l\xe0m chủ c\xf4ng việc. Ch\xfang t\xf4i sẽ đ\xe0o tạo đầy đủ rồi trao quyền để bạn tỏa s\xe1ng.","requirementsHeading":"Nếu muốn gia nhập đội ngũ, bạn cần:","requirementsList":["L\xe0m việc hiệu quả dưới \xe1p lực","Tự gi\xe1c, gọn g\xe0ng v\xe0 giao tiếp tốt","C\xf3 tinh thần tr\xe1ch nhiệm v\xe0 ham học hỏi","Sẵn s\xe0ng xắn tay \xe1o v\xe0 l\xe0m việc chăm chỉ","Với vị tr\xed to\xe0n thời gian, bạn cần đủ điều kiện ph\xe1p l\xfd l\xe0m việc tại ch\xe2u \xc2u trong suốt hợp đồng"],"notGoodFitHeading":"Bạn sẽ kh\xf4ng ph\xf9 hợp nếu:","notGoodFitList":["Bạn chỉ t\xecm vị tr\xed t\xecnh nguyện dưới ba th\xe1ng"],"forCustomerFacingHeading":"Với vị tr\xed tiếp x\xfac kh\xe1ch:","forCustomerFacingParagraph1":"Bạn phải thực sự y\xeau th\xedch việc mang tới dịch vụ xuất sắc. Nếu kh\xf4ng sẵn s\xe0ng chăm s\xf3c kh\xe1ch tận t\xe2m, đ\xe2y sẽ kh\xf4ng phải nơi d\xe0nh cho bạn.","rolesParagraph":"Ch\xfang t\xf4i lu\xf4n cần nh\xe2n sự l\xe0m sạch to\xe0n thời gian, c\xf9ng c\xe1c ca bữa s\xe1ng, ca tối, lễ t\xe2n v\xe0 những vai tr\xf2 trải nghiệm như hướng dẫn trekking hoặc l\xe1i t\xe0u.","closingParagraph":"Nếu cảm thấy đồng điệu, h\xe3y email tới hostelpositano@gmail.com, cho biết kinh nghiệm li\xean quan, vị tr\xed mong muốn v\xe0 l\xfd do bạn ph\xf9 hợp. Ch\xfang t\xf4i mong được tr\xf2 chuyện với bạn!","section4Paragraph1":"Hostel Brikette ra đời năm 1995, khi Positano c\xf2n nổi tiếng v\xec thời trang hơn l\xe0 du lịch to\xe0n cầu. Ch\xfang t\xf4i muốn giới thiệu vẻ đẹp Amalfi Coast – đặc biệt l\xe0 với du kh\xe1ch trẻ. Khởi đầu giản dị, ch\xfang t\xf4i đ\xe1p ứng những nhu cầu cơ bản của người ưa kh\xe1m ph\xe1 thời đ\xf3.","section5Paragraph1":"Từ đ\xf3 đến nay, Positano đ\xe3 trở th\xe0nh điểm đến xa xỉ h\xe0ng đầu. Ch\xfang t\xf4i cũng hiện đại h\xf3a: bổ sung c\xf4ng nghệ v\xe0 tiện nghi, n\xe2ng tầm bữa s\xe1ng v\xe0 tạo ra kh\xf4ng gian giao lưu khiến kh\xe1ch nhớ m\xe3i. Nhiều người n\xf3i rằng Hostel Brikette l\xe0 điểm nhấn trong chuyến đi của họ.","altAmenities":"Khu sinh hoạt chung của Hostel Brikette","altProfessional":"Phong th\xe1i chuy\xean nghiệp của Hostel Brikette","altVintage":"Quang cảnh Positano xưa","altPanorama":"To\xe0n cảnh Positano hiện đại","apply":"Ứng tuyển ngay"}}')}}]);