"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[865592],{865592:i=>{i.exports=JSON.parse('{"seo":{"title":"الأساطير والحكايات الشعبية لساحل أمالفي","description":"قصص شهيرة حول الساحل وأين ترى آثارها اليوم."},"linkLabel":"الأساطير والحكايات الشعبية لساحل أمالفي","intro":["استكشف أساطير وحكايات ساحل أمالفي التي تشكل المهرجانات والتماثيل والأمثال المحلية."],"sections":[{"id":"saint","title":"القديس أندراوس","body":["تضم كاتدرائية أمالفي رفات القديس أندراوس؛ ينسب السكان المحليون إليه تهدئة العواصف وحماية الصيادين.","في كل 27 يونيو و30 نوفمبر تحمل المواكب تمثال القديس عبر البلدة مع الألعاب النارية والفرق النحاسية."]},{"id":"sirens","title":"حوريات لي غالي","body":["تقول الأسطورة إن جزر لي غالي كانت تغوي البحارة بالغناء؛ اليوم تشارك جولات القوارب الحكاية أثناء مرورك بتلك الجزر الخاصة.","استمع إلى الإشارات لبارثينوب والحوريات عند زيارة نابولي — تربط الأساطير بين المنطقتين."]},{"id":"traditions","title":"تقاليد حية","body":["يخلط مهرجان لوميناريا في برايانو وبركات الصيادين في بوسيتانو بين الإيمان والفلكلور بطقوس مضاءة بالشموع.","تصور البلاط الخزفي اليدوي غالبًا القديسين أو الليمون أو الأساطير الساحلية — ابحث عنها على المداخل ودرجات الكنائس."]}],"tips":["راجع جداول فعاليات البلديات لأيام الأعياد حتى تنضم إلى الاحتفالات باحترام.","احمل وشاحًا خفيفًا؛ كثير من الفعاليات ينتهي بقداس داخل الكنيسة حيث يُقدَّر اللباس المحتشم."],"faqs":[{"q":"هل يمكن لأي شخص الانضمام إلى المواكب؟","a":["نعم — قف على طول المسار، وصفق باحترام، ودع السكان الذين يحملون التماثيل يمرون أولًا."]},{"q":"أين أسمع الموسيقى التقليدية؟","a":["ابحث عن حفلات الفولكلور في أتراني ورافيلو خلال مهرجانات الصيف؛ تنشر مكاتب السياحة الجداول الأسبوعية."]}]}')}}]);