"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[87002],{87002:i=>{i.exports=JSON.parse('{"seo":{"title":"أنشطة مجانية ومنخفضة التكلفة في بوسيتانو","description":"مطلات، مسارات، وشواطئ مجانية للاستمتاع بالبلدة دون إنفاق كبير."},"linkLabel":"أنشطة مجانية ومنخفضة التكلفة في بوسيتانو","intro":["تكافئ بوزيتانو مستيقظي الصباح والفضوليين؛ يمكنك ملء أيامك بالإطلالات والسباحة والثقافة دون إنفاق أكثر من بضعة يورو."],"sections":[{"id":"viewpoints","title":"نقاط مشاهدة تستحق الصعود","body":["ابدأ بالشرفة خارج Chiesa Nuova لألوان الشروق على الخليج؛ أحضر زجاجة 500 مل لأن لا توجد صنابير قريبة.","سر على فيا كريستوفورو كولومبو وشرفات فيالي باسيتيّا عند الساعة الذهبية لزوايا بطاقة بريدية من دون حجز طاولة."]},{"id":"beaches","title":"وقت شاطئ مجاني","body":["سبياجا غراندي تخصص شريطًا عامًا على الجانب الأيمن — أحمل منشفة خفيفة واجلس قرب خط الماء لتفادي صفوف الكراسي.","اتبع المسار الساحلي إلى شاطئ فورنيلو لخليج أهدأ؛ تستغرق المسافة حوالي 12 دقيقة مع سلالم متواضعة والقسم العام يبقى مجانيًا."]},{"id":"walks","title":"مسارات مشي ذاتية","body":["استكشف الأزقة الضيقة في حي ليبارلاتي، متسللًا إلى باحات صغيرة وسلالم مزيّنة بالسيراميك بين فيا دي موليني وفيا باسيتيّا.","استقل الشاتل المجاني إلى محطة مونتيبيرتوسو وانزل الدرج الحجري القديم إلى بوزيتانو خلال 25–35 دقيقة، متوقفًا عند قوس الصخرة الطبيعي."]},{"id":"culture","title":"ثقافة دون تذكرة","body":["ادخل كنيسة سانتا ماريا أسونتا لرؤية الأيقونة البيزنطية والقبة المايوليكا؛ يكفي تغطية الأكتاف.","راجع لوحة الإعلانات قرب Piazza dei Mulini للحفلات أو عروض الأفلام المسائية على الشاطئ خلال أسابيع الصيف."]}],"tips":["احمل زجاجة قابلة لإعادة الاستخدام وأعد تعبئتها من النافورة العامة قرب Chiesa Nuova قبل التوجه لنقاط المشاهدة.","احزم واقي شمس صديقًا للشعاب ووشاحًا؛ كلاهما يعمل كحصيرة شاطئ وغطاء خفيف في الكنائس."],"faqs":[{"q":"هل توجد duşات مجانية على الشواطئ؟","a":["نعم — سبياجا غراندي توفر دوشات ماء بارد قرب القسم العام. أحضر شبشبًا لأن الأرض تصبح حارة بحلول الظهر."]},{"q":"هل أحتاج تذاكر لدرب الآلهة؟","a":["المسار مجاني طوال العام. فقط احجز 2.40€ لتذكرة حافلة SITA إلى نوتشيليه والعودة إذا لم ترغب بالمشي ذهابًا وإيابًا."]}]}')}}]);