"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[871890],{871890:t=>{t.exports=JSON.parse('{"meta":{"title":"नेपल्स सिटी सेंटर से पोसितानो फ़ेरी द्वारा | Hostel Brikette","description":"केंद्रीय नेपल्स से पोसितानो तक फ़ेरी से जाने के चरण-दर-चरण निर्देश: बंदरगाह तक पहुँच, टिकट, बोर्डिंग और आगमन सुझाव।"},"hero":{"eyebrow":"Hostel Brikette यात्रा गाइड","title":"नेपल्स सिटी सेंटर से पोसितानो — फ़ेरी","description":"इन निर्देशों का उपयोग करके केंद्रीय नेपल्स से पूरी तरह समुद्री मार्ग द्वारा पोसितानो की यात्रा करें। यह गाइड नेपोली चेन्ट्राले से बंदरगाह तक छोटा ट्रांसफ़र, फ़ेरी टिकट खरीद, बोर्डिंग और पोसितानो के पियर पर पहुँचने पर क्या अपेक्षा करें, सब कवर करता है।"},"tip":{"label":"टिप","copy":"अन्य मार्ग या खराब मौसम की स्थिति में बैकअप खोज रहे हैं? <link>यहाँ कैसे पहुँचे अवलोकन</link> खोलें।"},"sections":{"journey":{"title":"यात्रा का अवलोकन","body":"नेपल्स और पोसितानो के बीच फ़ेरी वसंत और गर्मियों में सबसे अधिक चलती हैं और खराब मौसम में रद्द हो सकती हैं। कुल यात्रा समय आमतौर पर 1.5–2.5 घंटे होता है, जो जहाज के प्रकार और बंदरगाह पर प्रतीक्षा समय पर निर्भर करता है।"},"transfer":{"title":"नेपोली चेन्ट्राले से बंदरगाह पहुँचे","intro":"सबसे तेज़ सार्वजनिक परिवहन पियाज़ा गारिबाल्डी से Municipio (मोलो बेवेरेल्लो के लिए) या Marittima (पोर्टा दी मास्सा के लिए) तक ट्राम 1 है। समूहों के लिए टैक्सी भी सरल और किफ़ायती विकल्प है।","items":["नेपोली गारिबाल्डी/चेन्ट्राले से पियाज़ा गारिबाल्डी ट्राम स्टॉप के संकेतों का पालन करें।","“Municipio/Porto” दिशा में ट्राम 1 लें।","तेज़ हाइड्रोफ़ॉइल के लिए “Municipio” (मोलो बेवेरेल्लो) या धीमी फ़ेरी के लिए “Marittima” (पोर्टा दी मास्सा) पर उतरें।"],"map":"एक नमूना मार्ग देखें: <map>गूगल मैप्स (ट्राम से बंदरगाह)</map>."},"tickets":{"title":"फ़ेरी टिकट और ऑपरेटर","items":["अग्रिम में ऑनलाइन या मोलो बेवेरेल्लो टिकट कार्यालयों से खरीदें; गर्मियों में लाइनें लग सकती हैं।","ई-टिकट का उपयोग कर रहे हैं तो फोटो आईडी साथ रखें; बोर्डिंग से पहले ऑपरेटर जाँच कर सकते हैं।","खुले समुद्र में ऊँची लहरों के कारण सेवाएँ रद्द हो सकती हैं; सुबह की सैरियाँ आम तौर पर अधिक भरोसेमंद होती हैं।"],"copy":"फ़ेरी समय और किराया: <capri>Capri.net – Naples ↔ Positano</capri>। आप <positano>Positano.com (समेकित समय सारणी)</positano> पर भी विभिन्न ऑपरेटरों की तुलना कर सकते हैं।"},"boarding":{"title":"बंदरगाह पर बोर्डिंग","body":"यदि आवश्यक हो तो किसी भी बुकिंग वाउचर का आदान-प्रदान करने और बोर्डिंग की कतार में लगने के लिए पियर पर 20–30 मिनट पहले पहुँचें। सामान रखने के लिए कर्मचारियों के निर्देशों का पालन करें और जब ज्वार से गैंगवे हिलता है तो सावधानी से चढ़ें।"},"arrival":{"title":"पोसितानो में आगमन","items":["पोसितानो स्पियाज्ज़ा ग्रांदे पियर पर उतरें।","पियर पर कुली शुल्क लेकर सामान उठा सकते हैं; बैग देने से पहले कीमत तय कर लें।","फ़ेरी समुद्र स्तर पर पहुँचती हैं; ऊपर जाने के लिए सीढ़ियों की अपेक्षा करें। कम सीढ़ियों के लिए बंदरगाह से स्थानीय Interno Bus लेने पर विचार करें।"],"copy":"पियर से Hostel Brikette तक सामान के साथ अनुकूल मार्ग के लिए हमारा चरण-दर-चरण गाइड देखें: <link>फेरी डॉक → Hostel Brikette</link>."},"fallback":{"title":"यदि फ़ेरी रद्द हो जाएँ","body":"मौसम या समुद्री परिस्थितियों के कारण रद्द होने पर, सोरेंटो के माध्यम से रेल और बस मार्ग चुनें। इसमें अधिक समय लगता है लेकिन अधिकांश परिस्थितियों में चलती रहती है।","cta":"यह बैकअप उपयोग करें: <link>Naples Center → Positano by train then bus</link>."}},"externalLinks":{"map":"https://www.google.com/maps/dir/40.8408941,14.2611372/Napoli+Centrale,+P.za+Giuseppe+Garibaldi,+80142+Napoli+NA/@40.8375677,14.2464964,14z/data=!4m10!4m9!1m0!1m5!1m1!1s0x133b081659811bf3:0x26f7eaf2d2c03d87!2m2!1d14.2723487!2d40.8529222!3e3!5i4","capri":"https://www.capri.net/en/t/napoli/positano","positano":"https://www.positano.com/en/ferry-schedule"}}')}}]);