"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[887194],{887194:e=>{e.exports=JSON.parse('{"seo":{"title":"رحلة يومية إلى كابري من بوسيتانو","description":"كيفية التخطيط لرحلة يوم واحد إلى كابري: العبّارات، التوقيت، وأبرز الأماكن التي يجب زيارتها."},"linkLabel":"رحلة يومية إلى كابري من بوسيتانو","intro":["استكشف كابري في يوم واحد انطلاقًا من بوسيتانو: انطلق مبكرًا، سافر بخفة، وخطّط لمسار يوازن بين إطلالات أناكابري وعودة مريحة."],"toc":[{"href":"#morning","label":"الصباح — الوصول وأناكابري"},{"href":"#midday","label":"منتصف اليوم — جولة مارينا بيكولا"},{"href":"#afternoon","label":"بعد الظهر — بلدة كابري والعودة"},{"href":"#gallery","label":"معرض الصور"},{"href":"#faqs","label":"الأسئلة الشائعة"}],"sections":[{"id":"morning","title":"الصباح — الوصول وأناكابري","body":["استقل أول عبّارة لتصل قبل زحمة رحلات السفن السياحية، ثم اركب الحافلة إلى أناكابري وجرّب التلفريك إلى مونتي سولارو إذا كنت تحب المرتفعات.","تناول فطورًا بسيطًا في ساحة بياتزا فيتوريو واشترِ تذاكر العودة فورًا لتثبيت وقت المغادرة."]},{"id":"midday","title":"منتصف اليوم — حلقة مارينا بيكولا","body":["سر أو استقل الحافلة نزولاً نحو مارينا بيكولا للسباحة أو التوقف لالتقاط الصور؛ احمل واقيًا شمسياً صديقًا للشعاب وقارورة ماء لا تقل عن لتر واحد لكل شخص.","اشترِ بانيني أو علب سلطات من دكّان بدلًا من وجبة طويلة، كي تتمكن من الصعود مرة أخرى من دون استعجال."]},{"id":"afternoon","title":"بعد الظهر — بلدة كابري والعودة","body":["اركب عربات الفونيكولار نحو بلدة كابري لزيارة فيا كاميريللي، وحدائق أغسطس، والاستمتاع بوقفة جيلاتو أو إسبرسو أخيرة.","كن في مارينا غراندي قبل المغادرة بمدة لا تقل عن 30 دقيقة تحسبًا لأي طابور على الفونيكولار أو نقطة تسليم الأمتعة."]}],"galleryTitle":"معرض الصور","gallery":{"items":{"monteSolaro":{"alt":"إطلالة من مونتي سولارو في كابري","caption":"نظرة شاملة على مونتي سولارو"},"gardensOfAugustus":{"alt":"حدائق أغسطس في كابري","caption":"حدائق أغسطس"}}},"faqsTitle":"الأسئلة الشائعة","howTo":{"steps":[{"name":"تحقق من عبّارات الموسم","text":"تأكد من مواعيد الانطلاق الصباحية وآخر الرحلات العائدة إلى بوسيتانو."},{"name":"وصل إلى الرصيف مبكرًا","text":"اصطف قبل 20–30 دقيقة من موعد الرحلة في موسم الذروة."},{"name":"خطط لمسارك على جزيرة كابري","text":"حدد الأولويات بين أناكابري أو مارينا غراندي + الإطلالات بحسب الوقت المتاح."}]},"tipsTitle":"نصائح ليوم الرحلة","tips":["اشترِ تذاكر الذهاب والعودة معًا كي تتمكن من تعديل مسارك لاحقًا من دون القلق على المقاعد.","احمل حقيبة صغيرة فيها ماء وواقي شمس ونقودًا؛ أجهزة الصراف في كابري قد تنفد خلال أسابيع الذروة.","حمّل خرائط كابري وجدول الحافلات للعمل بلا إنترنت؛ الإشارة تضعف قرب أناكابري وطابور الكهف الأزرق."],"warningsTitle":"تحذيرات مهمة","warnings":["طوابير التلفريك والفونيكولار تزداد بعد الساعة 15:00؛ تفويت دورك قد يجبرك على الانتظار لرحلة عودة احتياطية.","قد تُلغى الرحلات المتأخرة بسبب البحر الهائج من دون سابق إنذار — تحقق من نشرات الصباح وضع اتصال سورينتو كخطة بديلة.","إغلاق الكهف الأزرق شائع عند الرياح أو الأمواج؛ قد يستمر الباعة في بيع التذاكر، لذا تأكد من تشغيله قبل الانضمام للطابور."],"faqs":[{"q":"هل أتمكن من إضافة الكهف الأزرق؟","a":["فقط إذا كان أولوية لديك — توقع قارب نقل وانتظاراً بين 60 و90 دقيقة، ما يضغط بقية البرنامج."]},{"q":"هل أحتاج إلى حجز مسبق؟","a":["احجز عبّارات الصباح في موسم الذروة، وفكر في شراء تذاكر التلفريك أو الجولات البحرية عبر الإنترنت؛ زوار مواسم الكتف يمكنهم عادة الشراء فورًا."]}]}')}}]);