"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[912218],{912218:e=>{e.exports=JSON.parse('{"seo":{"title":"Billig mad i Positano","description":"Hvor du spiser godt uden at spr\xe6nge budgettet — fra pizza slices til lokale deli’er."},"linkLabel":"Billig mad i Positano","heroAlt":"Billig takeaway-mad i Positano","intro":["Spis godt i Positano uden at overskride budgettet ved at kombinere takeaway-klassikere, deli-picnic og lokale bagerier."],"sections":[{"id":"grab-and-go","title":"Hurtige klassikere","body":["Snup pizza al taglio, panini eller arancini fra barerne p\xe5 Via dei Mulini f\xf8r frokost for at sl\xe5 k\xf8en.","Bestil espresso ved disken (\\"al banco\\") for den lokale €1,20-pris i stedet for bordservering."]},{"id":"casual-dining","title":"Uformelle spisesteder","body":["V\xe6lg trattoriaer oppe i byen, hvor coperto er lavt, og husets vin serveres p\xe5 karaffel.","Del forretter, spring desserten over, og bed om postevand (\\"acqua del rubinetto\\") for at holde regningen nede."]},{"id":"picnics","title":"Picnic med udsigt","body":["K\xf8b ost, focaccia og frugt i minimarkedet og picnic p\xe5 hostellets terrasse eller p\xe5 Fornillos offentlige omr\xe5de.","Medbring genanvendeligt bestik og servietter for at undg\xe5 engangsaffald og ekstra k\xf8b."]},{"id":"sweet-breaks","title":"S\xf8de pauser","body":["Pr\xf8v lemon granita fra gadeboderne til ca. €3 i stedet for dyre barcocktails.","Del et stykke delizia al limone eller tiramis\xf9 fra bageren for et billigt dessertfix."]}],"tips":["Sp\xf8rg efter \\"menu del giorno\\" eller frokosttilbud — mange steder kombinerer pasta, vand og kaffe for under €18.","Medbring en sammenklappelig beholder til rester, s\xe5 du kan hygge videre p\xe5 hostellet senere."],"recommendationsTitle":"Hvor kan du spise","recommendations":[{"name":"Collina Bakery","note":"Panini, pizza al taglio, salater og takeaway-kager til hurtige m\xe5ltider."},{"name":"Lokale delikatessediske","note":"Bestil antipasti, focaccia og picnicforsyninger v\xe6k fra vandkanten, hvor priserne er lavere."},{"name":"Minimarkeder og frugtstande","note":"Fyld op med frugt, store vandflasker og snacks til terrasseture."}],"gallery":{"title":"Fotogalleri","items":[{"alt":"Panini fra en lokal deli","caption":"Panini og deli"},{"alt":"Picnic med udsigt over Positano","caption":"Picnic-udsigt"}]},"faqs":[{"q":"Kan jeg fylde vand gratis?","a":["Ja — fyld flasker p\xe5 hostellet eller ved offentlige font\xe6ner som den ved Chiesa Nuova; hav altid mindst 750 ml med."]},{"q":"Findes der vegetariske budgetvalg?","a":["Helt sikkert — bestil caprese-panini, aubergine parmigiana i skiver eller pasta al pomodoro p\xe5 de uformelle trattoriaer."]}],"fallbackIntro":["Positano har masser af budgetvenlige bidder, n\xe5r du ved hvor du skal kigge."],"fallbackRecommendations":[{"name":"Collina Bakery","note":"Panini, pizza al taglio, salater og takeaway-kager."},{"name":"Lokale delikatessediske","note":"Antipasti, focaccia og picnicforsyninger v\xe6k fra strandpriserne."},{"name":"Minimarkeder og frugtstande","note":"Frugt, store vandflasker og snacks til terrasser."}],"fallbackGalleryItems":[{"alt":"Panini fra en lokal deli","caption":"Panini og deli"},{"alt":"Picnic med udsigt over Positano","caption":"Picnic-udsigt"}],"fallbackFaqs":[{"q":"Tager steder imod kort?","a":["De fleste g\xf8r, men hav sm\xe5 kontanter til kiosker eller minimumsbel\xf8b."]},{"q":"Er takeaway-gebyrer almindelige?","a":["Nogle opkr\xe6ver sm\xe5 emballagegebyrer; medbring en genbrugelig beholder for at spare og mindske spild."]}]}')}}]);