"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[92242],{92242:e=>{e.exports=JSON.parse('{"seo":{"title":"Plan your Sorrento day","description":"Base vs day trip, transport connections, and what to see."},"linkLabel":"Plan your Sorrento day","intro":["Sorrento is a practical base: plenty of accommodation, easy rail links, and fast connections to Capri and Pompeii. The trade-off is fewer postcard views than Positano."],"sections":[{"id":"why","title":"Why base in Sorrento","body":["More accommodation choice and straightforward train access. Trade-off: less postcard scenery than Positano."]},{"id":"connections","title":"Connections to Positano","body":["Frequent SITA buses and seasonal ferries. Buy bus tickets before boarding; ferries can sell out on peak days."]},{"id":"see","title":"What to see","body":["Historic center, marinas, and viewpoints; a handy jumping-off point for Capri and Pompeii."]}],"faqs":[{"q":"Is Sorrento cheaper than Positano?","a":["Generally yes—expect accommodation savings of €30–€60 per night and more dining options at mid-range prices."]},{"q":"How late do trains run back to Naples?","a":["The Circumvesuviana runs until around 22:30; after that, use regional buses or plan to overnight if you have a late flight."]}]}')}}]);