"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[961359],{961359:i=>{i.exports=JSON.parse('{"seo":{"title":"Tanie jedzenie w Positano","description":"Gdzie zjeść dobrze i niedrogo: piekarnie, trattorie, street food i lokalne tipy."},"linkLabel":"Tanie jedzenie w Positano","heroAlt":"Niedrogie jedzenie na wynos w Positano","intro":["Jedz dobrze w Positano bez przepłacania, łącząc klasyki na wynos, pikniki z delikates\xf3w i lokalne piekarnie."],"sections":[{"id":"grab-and-go","title":"Szybkie klasyki","body":["Kup pizzę al taglio, panini lub arancini w barach przy Via dei Mulini przed południem, aby uniknąć kolejek.","Zamawiaj espresso przy barze („al banco”) za lokalne 1,20 €, zamiast siadać przy stoliku."]},{"id":"casual-dining","title":"Luźne lokale z obsługą","body":["Szukaj trattorii powyżej plaży, gdzie coperto jest niewielkie, a wino domowe podaje się na karafki.","Dzielenie przystawek, pomijanie deseru i proszenie o wodę z kranu („acqua del rubinetto”) pomaga trzymać rachunek w ryzach."]},{"id":"picnics","title":"Pikniki z widokiem","body":["Zaopatrz się w sery, focaccię i owoce w minimarketach, a potem piknikuj na tarasie hostelu lub w publicznej części Fornillo.","Noś wielorazowe sztućce i serwetki, by ograniczyć jednorazowe odpady i dodatkowe zakupy."]},{"id":"sweet-breaks","title":"Słodkie przerwy i desery","body":["Spr\xf3buj granity cytrynowej z ulicznych w\xf3zk\xf3w za około 3 €, zamiast droższych koktajli z baru.","Podziel się kawałkiem delizia al limone lub tiramisu z piekarni, by tanio zaspokoić ochotę na deser."]}],"tips":["Pytaj o „menu del giorno” lub zestawy lunchowe — wiele miejsc oferuje makaron, wodę i kawę za mniej niż 18 €.","Zabierz składany pojemnik na resztki, aby zjeść je p\xf3źniej w hostelu."],"recommendationsTitle":"Gdzie zjeść","recommendations":[{"name":"Collina Bakery","note":"Panini, pizza al taglio, sałatki i wypieki na wynos na szybkie posiłki."},{"name":"Local delicatessen counters","note":"Zamawiaj deski z delikates\xf3w, focaccię i zestawy piknikowe z dala od nadmorskich cen."},{"name":"Minimarkets & fruit stands","note":"Kupuj owoce, duże butelki wody i przekąski na tarasowe pikniki."}],"gallery":{"title":"Galeria zdjęć","items":[{"alt":"Panini z lokalnego delikatesu","caption":"Panini i delikatesy"},{"alt":"Piknik z widokiem na Positano","caption":"Piknikowe widoki"}]},"faqs":[{"q":"Czy mogę uzupełnić wodę za darmo?","a":["Tak — napełniaj butelki w hostelu lub przy publicznych fontannach, np. przy Chiesa Nuova; noś zawsze co najmniej 750 ml."]},{"q":"Czy są niedrogie opcje wegetariańskie?","a":["Oczywiście — zam\xf3w caprese panini, porcję bakłażana parmigiana albo pastę al pomodoro w luźnych trattoriach."]}],"fallbackIntro":["W Positano znajdziesz sporo niedrogich przekąsek, jeśli wiesz, gdzie szukać."],"fallbackRecommendations":[{"name":"Collina Bakery","note":"Panini, pizza al taglio, sałatki i wypieki na wynos na szybkie posiłki."},{"name":"Local delicatessen counters","note":"Zamawiaj deski z delikates\xf3w, focaccię i zestawy piknikowe z dala od nadmorskich cen."},{"name":"Minimarkets & fruit stands","note":"Kupuj owoce, duże butelki wody i przekąski na tarasowe pikniki."}],"fallbackGalleryItems":[{"alt":"Panini z lokalnego delikatesu","caption":"Panini i delikatesy"},{"alt":"Piknik z widokiem na Positano","caption":"Piknikowe widoki"}],"fallbackFaqs":[{"q":"Czy lokale akceptują karty?","a":["Większość tak, ale drobna got\xf3wka pomaga w kioskach lub przy minimalnych kwotach."]},{"q":"Czy opłaty za opakowania są częste?","a":["Niekt\xf3re pobierają niewielkie opłaty za opakowania; zabierz wielorazowy pojemnik, aby oszczędzić i ograniczyć odpady."]}]}')}}]);