"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[986573],{986573:n=>{n.exports=JSON.parse('{"meta":{"title":"Từ Ravello đến Positano bằng xe bu\xfdt | Hostel Brikette","description":"Hướng dẫn từng bước đi xe bu\xfdt SITA qua Amalfi từ Ravello đến Positano, k\xe8m mẹo đọc giờ chạy, chi tiết điểm dừng v\xe0 đoạn đi bộ cuối tới Hostel Brikette."},"header":{"eyebrow":"Cẩm nang di chuyển của Hostel Brikette","title":"Ravello đến Positano – qua Amalfi bằng xe bu\xfdt","description":"Di chuyển từ Ravello đến Positano với một lần đổi xe đơn giản tại Amalfi. Hướng dẫn n\xe0y cho biết nơi mua v\xe0 x\xe1c nhận v\xe9 SITA, c\xe1ch đọc bảng giờ v\xe0 những điều cần lưu \xfd khi đổi tuyến ở Amalfi — c\xf9ng đoạn đi bộ ngắn từ trạm Chiesa Nuova tới Hostel Brikette."},"tip":{"eyebrow":"Mẹo","body":"Cần xem mọi lựa chọn di chuyển? Mở <GuideLink>{{guideLabel}}</GuideLink> để tham khảo ph\xe0, taxi v\xe0 h\xe0nh tr\xecnh kết hợp."},"journeyOverview":{"title":"Tổng quan h\xe0nh tr\xecnh","description":"Tổng thời gian khoảng 1,5–2,5 giờ t\xf9y lần nối chuyến. Trước ti\xean đi xe bu\xfdt SITA từ Ravello tới Amalfi, sau đ\xf3 chuyển sang xe SITA hướng Positano/Sorrento. M\xf9a cao điểm xe chạy thường xuy\xean nhưng dễ đ\xf4ng; h\xe3y đến sớm một ch\xfat để xếp h\xe0ng."},"sitaTickets":{"title":"Xe bu\xfdt SITA v\xe0 v\xe9","points":["C\xe1c chuyến xe giữa Ravello v\xe0 Amalfi do SITA (SITA Sud Srl) vận h\xe0nh.","Bạn phải đi Amalfi trước rồi đổi sang chuyến thứ hai đi Positano.","C\xf3 hai tuyến Ravello → Amalfi: 5110 v\xe0 5120. Tuyến 5110 thường nhanh hơn khoảng 10 ph\xfat.","Mua v\xe9 tại quầy b\xe1o hoặc cửa h\xe0ng thuốc l\xe1 (tabacchi) v\xe0 chuẩn bị sẵn để x\xe1c nhận khi l\xean xe."]},"timetable":{"title":"Giờ chạy","description":"Xem lịch mới nhất tr\xean <TimetableLink>{{timetableLabel}}</TimetableLink>.","notesTitle":"C\xe1ch đọc bảng giờ","points":["C\xe1c h\xe0ng ở cột đầu liệt k\xea từng điểm dừng tr\xean tuyến.","C\xe1c cột tương ứng với c\xe1c chuyến được gắn chữ c\xe1i.","Đi từ thứ Hai đến thứ Bảy? D\xf9ng cột ‘F’ v\xe0 ‘G’.","Đi v\xe0o Chủ nhật hoặc ng\xe0y lễ? D\xf9ng cột ‘G’ v\xe0 ‘H’."],"linkLabel":"Positano.com (tổng quan lịch SITA)"},"ravelloToAmalfi":{"title":"Ravello → Amalfi","points":["Bắt xe SITA 5110 hoặc 5120 từ Ravello đến Amalfi (5110 thường nhanh hơn).","X\xe1c nhận v\xe9 khi l\xean xe v\xe0 giữ tới khi xuống.","Xuống tại bến xe Amalfi để nối chuyến đi Positano."]},"waitingInAmalfi":{"title":"Chờ ở Amalfi","points":["Tới Amalfi, bạn c\xf3 thể dạo quanh trung t\xe2m thị trấn trong l\xfac chờ.","Quay lại khu bến xe nơi bạn vừa xuống để bắt chuyến đi Positano.","L\xean bất kỳ xe SITA n\xe0o ghi Positano/Sorrento via Amalfi Coast."]},"arrivingPositano":{"title":"Đến Positano (Chiesa Nuova)","points":["Trạm SITA gần nhất l\xe0 Positano (Chiesa Nuova) tr\xean Via G. Marconi.","Trạm c\xe1ch cửa hostel khoảng 140 m\xe9t.","Bạn sẽ đứng chờ b\xean ngo\xe0i qu\xe1n Bar Internazionale."],"figure":{"alt":"Trạm SITA Chiesa Nuova của Positano ph\xeda trước Bar Internazionale","caption":"Đến sớm v\xe0i ph\xfat để dễ c\xf3 chỗ ngồi; m\xf9a h\xe8 xe thường k\xedn nhanh."}},"walking":{"title":"Đi bộ từ trạm xe bu\xfdt về hostel","points":["Từ trạm Chiesa Nuova, đi tiếp khoảng 100 m\xe9t.","Rẽ phải v\xe0 đi theo c\xe1c bậc thang l\xean cửa ch\xednh của hostel.","T\xecm bảng hiệu của ch\xfang t\xf4i cạnh s\xe2n thượng; lối v\xe0o nằm ngay tr\xean kh\xfac cua."]},"links":{"guideOverview":"Tổng quan C\xe1ch đến đ\xe2y"}}')}}]);