"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[987154],{987154:n=>{n.exports=JSON.parse('{"seo":{"title":"Path of the Gods qua tuyến xe bu\xfdt Amalfi","description":"Đi xe bu\xfdt SITA qua Amalfi tới Bomerano, sau đ\xf3 đi bộ tới Nocelle v\xe0 về bằng xe đưa đ\xf3n với kế hoạch cả ng\xe0y."},"linkLabel":"Path of the Gods qua Amalfi (xe bu\xfdt + đi bộ)","intro":["Th\xedch ngồi tr\xean xe hơn? Lộ tr\xecnh n\xe0y đi xe bu\xfdt SITA từ Positano đến Amalfi rồi tiếp tục tới Bomerano trước khi đi bộ đường m\xf2n Path of the Gods trở lại hướng Nocelle. Đ\xe2y l\xe0 lựa chọn tin cậy khi biển động v\xe0 thường l\xe0 c\xe1ch nhanh nhất để đến to\xe0n bộ cung đường.","Xe bu\xfdt rất đ\xf4ng v\xe0o m\xf9a cao điểm, v\xec vậy h\xe3y mua v\xe9 từ ng\xe0y h\xf4m trước nếu c\xf3 thể v\xe0 đến sớm tại mỗi điểm dừng."],"sections":[{"id":"prep","title":"Mua v\xe9 v\xe0 l\xean kế hoạch thời gian","body":["Bạn cần ba v\xe9 xe bu\xfdt cho mỗi người: Positano → Amalfi, Amalfi → Bomerano v\xe0 Nocelle → Positano. Hỏi nh\xe2n vi\xean tại Bar Internazionale để chọn được loại v\xe9 ph\xf9 hợp (lẻ hoặc v\xe9 t\xedch hợp).","Kiểm tra lịch tr\xecnh SITA được d\xe1n b\xean trong qu\xe1n bar hoặc th\xf4ng qua ứng dụng Unico Campania. Hướng đến chuyến khởi h\xe0nh buổi s\xe1ng để tr\xe1nh xếp h\xe0ng v\xe0 nắng gắt giữa trưa tr\xean đường m\xf2n.","Mang theo nước, đồ ăn nhẹ v\xe0 kem chống nắng — kh\xf4ng c\xf3 dịch vụ n\xe0o giữa Bomerano v\xe0 Nocelle."]},{"id":"bus-to-amalfi","title":"Đi từ Bar Internazionale đến Amalfi","body":["Rời hostel, rẽ tr\xe1i v\xe0 tới Bar Internazionale trong khoảng hai ph\xfat. Mua v\xe9, sau đ\xf3 đợi ở ph\xeda đối diện đường để đ\xf3n xe bu\xfdt đi Amalfi.","X\xe1c nhận v\xe9 khi l\xean xe. H\xe0nh tr\xecnh mất 45–60 ph\xfat t\xf9y giao th\xf4ng v\xe0 c\xf3 nhiều kh\xfac cua \xf4m vịnh với tầm nh\xecn biển rộng mở.","Khi đến Amalfi, ở gần c\xe1c bến xe bu\xfdt — bạn sẽ nối chuyến tuyến Agerola tại đ\xf3."]},{"id":"wait-in-amalfi","title":"Tạm dừng ở Amalfi (t\xf9y chọn)","body":["Nếu c\xf2n thời gian trước chuyến tiếp theo, bạn c\xf3 thể đi bộ một đoạn ngắn v\xe0o thị trấn để uống c\xe0 ph\xea hoặc ăn b\xe1nh ngọt. Trở lại bến \xedt nhất 10 ph\xfat trước giờ khởi h\xe0nh.","Xếp h\xe0ng sớm v\xe0o m\xf9a h\xe8 v\xec xe bu\xfdt đầy nhanh. T\xe0i xế c\xf3 thể hạn chế l\xean xe khi tất cả ghế v\xe0 chỗ đứng an to\xe0n đ\xe3 k\xedn."]},{"id":"bus-to-bomerano","title":"Xe bu\xfdt l\xean đồi tới Bomerano","body":["L\xean xe bu\xfdt ghi \\"Agerola/Bomerano\\". D\xf9ng v\xe9 thứ hai v\xe0 giữ tay b\xe1m vững khi qua c\xe1c kh\xfac cua gắt.","Xuống tại điểm dừng Bomerano đối diện Ristorante Jerla. Băng qua đường, đi v\xe0o con phố nhỏ dẫn đến quảng trường rồi theo biển chỉ đường tới điểm bắt đầu Path of the Gods."]},{"id":"hike","title":"Đi bộ đến Nocelle","body":["Theo c\xe1c biển b\xe1o Sentiero degli Dei băng qua ruộng bậc thang v\xe0 sống n\xfai. D\xe0nh 2,5–3 giờ cho h\xe0nh tr\xecnh k\xe8m thời gian chụp ảnh.","Mang r\xe1c theo người v\xe0 t\xf4n trọng c\xe1c khu đất n\xf4ng nghiệp tư nh\xe2n dọc đường."]},{"id":"nocelle","title":"Đi trong Nocelle v\xe0 tới điểm dừng xe bu\xfdt","body":["Khi đường m\xf2n chuyển th\xe0nh b\xea t\xf4ng, bạn đ\xe3 tới Nocelle. Giữ b\xean phải tại ng\xe3 rẽ đầu ti\xean để tr\xe1nh cầu thang d\xe0i xuống Positano.","Đi bộ khoảng 175 m\xe9t tới b\xe3i đỗ xe nhỏ v\xe0 nh\xe0 chờ xe bu\xfdt. Đến sớm; đ\xf4i khi xe bu\xfdt đỗ ngay trước điểm dừng khi b\xe3i xe k\xedn."]},{"id":"return","title":"Trở về Positano","body":["D\xf9ng v\xe9 cuối c\xf9ng để đi xe bu\xfdt Nocelle/Montepertuso về Bar Internazionale hoặc Chiesa Nuova.","Nếu lỡ chuyến, bạn c\xf3 thể đi bộ theo đường nhựa (khoảng 4 km) nhưng h\xe3y chuẩn bị cho đoạn xuống dốc d\xe0i c\xf3 xe cộ qua lại."]},{"id":"journey-planner","title":"Lịch tr\xecnh mẫu","body":["07:00 rời hostel → 07:15 xe bu\xfdt tới Amalfi → 08:10 tới Amalfi → 08:30 xe bu\xfdt tới Bomerano → 09:10 bắt đầu đi bộ → 12:00 tới Nocelle → 12:30 xe bu\xfdt về Positano.","Phương \xe1n muộn hơn: khởi h\xe0nh 08:00 vẫn kịp về đầu giờ chiều nếu c\xe1c chuyến nối tiếp khớp nhau."]}],"essentialsTitle":"Vật dụng cần thiết","essentials":["Ba v\xe9 SITA mỗi người (Positano → Amalfi, Amalfi → Bomerano, Nocelle → Positano) cộng một v\xe9 dự ph\xf2ng.","Nước, đồ ăn nhẹ, kem chống nắng v\xe0 \xe1o kho\xe1c nhẹ cho đường m\xf2n tr\xean sống n\xfai trống trải.","Gi\xe0y d\xe9p \xeam c\xf3 độ b\xe1m v\xe0 điện thoại đầy pin với bản đồ ngoại tuyến."],"typicalCostsTitle":"Chi ph\xed tham khảo","typicalCosts":["V\xe9 xe bu\xfdt SITA: 2–4 € mỗi chặng t\xf9y loại v\xe9 lẻ hoặc v\xe9 t\xedch hợp.","C\xe0 ph\xea hoặc đồ ăn nhẹ khi chờ ở Amalfi: 2–6 €.","Lối v\xe0o đường m\xf2n: miễn ph\xed."],"tipsTitle":"Mẹo cho h\xe0nh tr\xecnh","tips":["Đến sớm 10–15 ph\xfat tại mọi điểm dừng — xe bu\xfdt c\xf3 thể đầy trước giờ cao điểm.","Ngồi ph\xeda b\xean phải khi rời Positano để ngắm to\xe0n cảnh bờ biển, v\xe0 b\xean tr\xe1i khi trở về từ Amalfi.","Mang theo tiền lẻ ph\xf2ng khi bạn cần x\xe1c nhận th\xeam v\xe9 tr\xean xe."],"warningsTitle":"Cần lưu \xfd","warnings":["Đừng đi theo cầu thang 1.700 bậc từ Nocelle trừ khi bạn sẵn s\xe0ng cho một đoạn xuống dốc gắt g\xe2y \xe1p lực cho đầu gối.","Dịch vụ cuối tuần c\xf3 thể trễ; h\xe3y c\xf3 sẵn đồ ăn nhẹ v\xe0 nước ph\xf2ng khi phải chờ l\xe2u hơn dự kiến.","Xe bu\xfdt giờ cao điểm c\xf3 thể hạn chế kh\xe1ch đứng — h\xe3y chuẩn bị bỏ qua một chuyến v\xe0 đ\xf3n chuyến kế tiếp."],"steps":[{"name":"Mua v\xe9 SITA","text":"Mua đủ v\xe9 xe bu\xfdt tại Bar Internazionale trước khi khởi h\xe0nh."},{"name":"Xe bu\xfdt đến Amalfi","text":"Bắt xe bu\xfdt hướng Amalfi tại Bar Internazionale v\xe0 ở lại xe khi đi qua c\xe1c l\xe0ng ven biển."},{"name":"Chuyển sang xe bu\xfdt Agerola","text":"Chờ tại bến Amalfi v\xe0 l\xean tuyến c\xf3 ghi Agerola/Bomerano."},{"name":"Đến điểm bắt đầu đường m\xf2n","text":"Xuống xe tại Bomerano, theo biển qua quảng trường v\xe0 nhập v\xe0o Sentiero degli Dei."},{"name":"Đi bộ tới Nocelle","text":"Theo c\xe1c biển chỉ CAI dọc sống n\xfai cho đến khi bạn tới lối đi b\xea t\xf4ng dẫn v\xe0o thị trấn."},{"name":"Về bằng xe bu\xfdt","text":"Bắt xe bu\xfdt Nocelle/Montepertuso về Bar Internazionale hoặc Chiesa Nuova."}],"faqsTitle":"C\xe2u hỏi thường gặp","faqs":[{"q":"Xe bu\xfdt đ\xf4ng đến mức n\xe0o?","a":["C\xe1c chuyến buổi s\xe1ng m\xf9a h\xe8 đầy rất nhanh. H\xe3y đến sớm, xếp h\xe0ng b\xecnh tĩnh v\xe0 sẵn s\xe0ng đứng. T\xe0i xế c\xf3 thể từ chối cho l\xean khi lối đi đ\xe3 k\xedn."]},{"q":"T\xf4i c\xf3 thể dừng lại tham quan Amalfi kh\xf4ng?","a":["C\xf3 — h\xe3y chừa \xedt nhất một khoảng thời gian giữa hai chuyến (30–60 ph\xfat) để bạn c\xf3 thời gian kh\xe1m ph\xe1 v\xe0 trở lại xếp h\xe0ng trước chuyến Agerola tiếp theo."]},{"q":"Nếu t\xf4i lỡ chuyến xe từ Nocelle về th\xec sao?","a":["Kiểm tra bảng giờ tại điểm dừng. Nếu chuyến tiếp theo hơn một giờ nữa mới tới, bạn c\xf3 thể đi bộ theo đường nhựa xuống Positano trong khoảng 45–60 ph\xfat."]}],"toc":{"onThisPage":"Mục trong trang","essentials":"Vật dụng cần thiết","costs":"Chi ph\xed tham khảo","tips":"Mẹo cho h\xe0nh tr\xecnh","warnings":"Cần lưu \xfd","faqs":"C\xe2u hỏi thường gặp"}}')}}]);