"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[99731],{99731:c=>{c.exports=JSON.parse('{"meta":{"title":"Ch\xednh s\xe1ch cookie | Hostel Brikette","description":"C\xe1ch Hostel Brikette sử dụng cookie v\xe0 c\xe1c c\xf4ng nghệ tương tự tr\xean website của ch\xfang t\xf4i v\xe0 c\xe1c lựa chọn của bạn."},"headings":{"pageTitle":"Ch\xednh s\xe1ch cookie","subtitle":"Cookie, lưu trữ cục bộ v\xe0 dịch vụ b\xean thứ ba"},"lastUpdated":{"label":"Cập nhật lần cuối","date":"2026-01-09"},"toc":{"title":"Trong trang n\xe0y","aria":"Mục lục ch\xednh s\xe1ch cookie"},"sections":{"intro":{"title":"Tổng quan","body":["Ch\xednh s\xe1ch cookie n\xe0y giải th\xedch cookie l\xe0 g\xec, liệu ch\xfang t\xf4i c\xf3 sử dụng ch\xfang tr\xean website n\xe0y kh\xf4ng, v\xe0 c\xe1ch c\xe1c dịch vụ b\xean thứ ba c\xf3 thể sử dụng cookie khi bạn tương t\xe1c với nội dung của họ."]},"whatAreCookies":{"title":"Cookie l\xe0 g\xec","body":["Cookie l\xe0 c\xe1c tệp văn bản nhỏ m\xe0 website c\xf3 thể lưu tr\xean thiết bị của bạn để ghi nhớ th\xf4ng tin giữa c\xe1c lần truy cập. C\xe1c c\xf4ng nghệ tương tự bao gồm lưu trữ cục bộ v\xe0 lưu trữ phi\xean trong tr\xecnh duyệt của bạn."]},"ourUse":{"title":"C\xe1ch ch\xfang t\xf4i sử dụng cookie tr\xean website n\xe0y","body":["Ch\xfang t\xf4i cố gắng giảm thiểu việc sử dụng cookie. Khi c\xf3 thể, ch\xfang t\xf4i sử dụng bộ nhớ của tr\xecnh duyệt (lưu trữ cục bộ) cho c\xe1c t\xf9y chọn đơn giản thay v\xec cookie.","Nếu trong tương lai ch\xfang t\xf4i \xe1p dụng cookie (v\xed dụ cho bảo mật thiết yếu, ph\xe2n t\xedch hoặc dịch vụ nh\xfang), ch\xfang t\xf4i sẽ cập nhật ch\xednh s\xe1ch n\xe0y."]},"localStorage":{"title":"Lưu trữ cục bộ (t\xf9y chọn)","body":["Lưu trữ cục bộ cho ph\xe9p website ghi nhớ một số t\xf9y chọn tr\xean thiết bị của bạn. V\xed dụ, n\xf3 c\xf3 thể được d\xf9ng để giữ lại t\xf9y chọn giao diện giữa c\xe1c trang.","Bạn c\xf3 thể x\xf3a lưu trữ cục bộ trong phần c\xe0i đặt của tr\xecnh duyệt."]},"thirdParties":{"title":"Dịch vụ b\xean thứ ba","body":["Một số li\xean kết hoặc nội dung nh\xfang c\xf3 thể do b\xean thứ ba cung cấp (v\xed dụ: bản đồ hoặc nh\xe0 cung cấp đặt ph\xf2ng). C\xe1c b\xean thứ ba n\xe0y c\xf3 thể đặt cookie hoặc sử dụng c\xe1c c\xf4ng nghệ tương tự theo ch\xednh s\xe1ch của họ.","Khi bạn theo một li\xean kết đến website b\xean thứ ba, c\xe1c ch\xednh s\xe1ch quyền ri\xeang tư v\xe0 cookie của họ sẽ được \xe1p dụng."]},"choices":{"title":"Lựa chọn của bạn","body":["Hầu hết c\xe1c tr\xecnh duyệt cho ph\xe9p bạn chặn hoặc x\xf3a cookie v\xe0 x\xf3a dữ liệu trang web như lưu trữ cục bộ. Chặn một số c\xf4ng nghệ c\xf3 thể ảnh hưởng đến chức năng.","Bạn cũng c\xf3 thể sử dụng chế độ duyệt ri\xeang tư hoặc tiện \xedch mở rộng tr\xecnh duyệt để hạn chế theo d\xf5i."]},"contact":{"title":"Li\xean hệ","body":["Nếu bạn c\xf3 c\xe2u hỏi về ch\xednh s\xe1ch cookie n\xe0y, h\xe3y li\xean hệ với ch\xfang t\xf4i tại {{email}}."]}},"footer":{"backToTop":"L\xean đầu trang"}}')}}]);