  eventsLabel,
  eventsHref,
  breakfastLabel,
  breakfastHref,
  conciergeLabel,
  onConciergeClick,
}: Props): JSX.Element | null {
  const hasContent = Boolean(
    title ||
      subtitle ||
      bookLabel ||
      eventsLabel ||
      breakfastLabel ||
      conciergeLabel,
  );

  if (!hasContent) return null;

  return (
    <div className="mx-auto w-full max-w-6xl">
